entity.query:
MERGE ARM_BALANCE_SUMMARY_CONFIG AS TARGET USING ( SELECT ARM_ADJ_RES_CONFIG_MASTER_SID, ACCOUNT, REPORT_TYPE, PIPELINE_ACCRUAL, DEMAND_ACCRUAL, PIPELINE_INVENTORY, DEMAND_REFORECAST, DEMAND_PAYMENT_TRUE_UP, FEES_ACCRUAL, INFLATION_ADJ, CREDIT_CARD_FEES, OTHER_FIXED_DOLLAR_FEES, INVENTORY_VALULATION, RETURN_RESERVE, VERSION_NO FROM ?
) SOURCE
ON TARGET.ARM_ADJ_RES_CONFIG_MASTER_SID = SOURCE.ARM_ADJ_RES_CONFIG_MASTER_SID AND TARGET.REPORT_TYPE = SOURCE.REPORT_TYPE AND TARGET.VERSION_NO = SOURCE.VERSION_NO WHEN MATCHED THEN UPDATE SET TARGET.ARM_ADJ_RES_CONFIG_MASTER_SID = SOURCE.ARM_ADJ_RES_CONFIG_MASTER_SID , TARGET.ACCOUNT = SOURCE.ACCOUNT , TARGET.REPORT_TYPE = SOURCE.REPORT_TYPE , TARGET.PIPELINE_ACCRUAL = SOURCE.PIPELINE_ACCRUAL , TARGET.DEMAND_ACCRUAL = SOURCE.DEMAND_ACCRUAL , TARGET.PIPELINE_INVENTORY = SOURCE.PIPELINE_INVENTORY , TARGET.DEMAND_REFORECAST = SOURCE.DEMAND_REFORECAST , TARGET.DEMAND_PAYMENT_TRUE_UP = SOURCE.DEMAND_PAYMENT_TRUE_UP , TARGET.FEES_ACCRUAL = SOURCE.FEES_ACCRUAL , TARGET.INFLATION_ADJ = SOURCE.INFLATION_ADJ , TARGET.CREDIT_CARD_FEES = SOURCE.CREDIT_CARD_FEES , TARGET.OTHER_FIXED_DOLLAR_FEES = SOURCE.OTHER_FIXED_DOLLAR_FEES , TARGET.INVENTORY_VALULATION = SOURCE.INVENTORY_VALULATION , TARGET.RETURN_RESERVE = SOURCE.RETURN_RESERVE , TARGET.VERSION_NO = SOURCE.VERSION_NO WHEN NOT MATCHED BY SOURCE AND TARGET.ARM_ADJ_RES_CONFIG_MASTER_SID = ? THEN DELETE WHEN NOT MATCHED BY TARGET THEN INSERT ( ARM_ADJ_RES_CONFIG_MASTER_SID, ACCOUNT, REPORT_TYPE, PIPELINE_ACCRUAL, DEMAND_ACCRUAL, PIPELINE_INVENTORY, DEMAND_REFORECAST, DEMAND_PAYMENT_TRUE_UP, FEES_ACCRUAL, INFLATION_ADJ, CREDIT_CARD_FEES, OTHER_FIXED_DOLLAR_FEES, INVENTORY_VALULATION, RETURN_RESERVE, VERSION_NO ) VALUES ( ARM_ADJ_RES_CONFIG_MASTER_SID, ACCOUNT, REPORT_TYPE, PIPELINE_ACCRUAL, DEMAND_ACCRUAL, PIPELINE_INVENTORY, DEMAND_REFORECAST, DEMAND_PAYMENT_TRUE_UP, FEES_ACCRUAL, INFLATION_ADJ, CREDIT_CARD_FEES, OTHER_FIXED_DOLLAR_FEES, INVENTORY_VALULATION, RETURN_RESERVE, VERSION_NO ) ;
