entity.query:
MERGE ARM_ADJ_SUMMARY_CONFIG_DETAILS AS TARGET USING ( SELECT ARM_ADJ_RES_CONFIG_MASTER_SID, METHODOLOGY, ADJUSTMENT_TYPE, CURRENT_BALANCE, CURRENT_BALANCE_ACC, DEMAND_ACCRUAL, DEMAND_ACCRUAL_ACC, DEMAND_REFORECAST, DEMAND_REFORECAST_ACC, DEMAND_PAYMENT_RECON, DEMAND_PAYMENT_RECON_ACC, VERSION_NO, PARENT_VERSION_NO FROM ?
) SOURCE
ON TARGET.ARM_ADJ_RES_CONFIG_MASTER_SID = SOURCE.ARM_ADJ_RES_CONFIG_MASTER_SID AND TARGET.ADJUSTMENT_TYPE = SOURCE.ADJUSTMENT_TYPE AND TARGET.VERSION_NO = SOURCE.VERSION_NO WHEN MATCHED THEN UPDATE SET TARGET.ARM_ADJ_RES_CONFIG_MASTER_SID = SOURCE.ARM_ADJ_RES_CONFIG_MASTER_SID , TARGET.METHODOLOGY = SOURCE.METHODOLOGY , TARGET.ADJUSTMENT_TYPE = SOURCE.ADJUSTMENT_TYPE , TARGET.CURRENT_BALANCE = SOURCE.CURRENT_BALANCE , TARGET.CURRENT_BALANCE_ACC = SOURCE.CURRENT_BALANCE_ACC , TARGET.DEMAND_ACCRUAL = SOURCE.DEMAND_ACCRUAL , TARGET.DEMAND_ACCRUAL_ACC = SOURCE.DEMAND_ACCRUAL_ACC , TARGET.DEMAND_REFORECAST = SOURCE.DEMAND_REFORECAST , TARGET.DEMAND_REFORECAST_ACC = SOURCE.DEMAND_REFORECAST_ACC , TARGET.DEMAND_PAYMENT_RECON = SOURCE.DEMAND_PAYMENT_RECON , TARGET.DEMAND_PAYMENT_RECON_ACC = SOURCE.DEMAND_PAYMENT_RECON_ACC , TARGET.VERSION_NO = SOURCE.VERSION_NO , TARGET.PARENT_VERSION_NO = SOURCE.PARENT_VERSION_NO WHEN NOT MATCHED BY SOURCE AND TARGET.ARM_ADJ_RES_CONFIG_MASTER_SID = ? THEN DELETE WHEN NOT MATCHED BY TARGET THEN INSERT ( ARM_ADJ_RES_CONFIG_MASTER_SID, METHODOLOGY, ADJUSTMENT_TYPE, CURRENT_BALANCE, CURRENT_BALANCE_ACC, DEMAND_ACCRUAL, DEMAND_ACCRUAL_ACC, DEMAND_REFORECAST, DEMAND_REFORECAST_ACC, DEMAND_PAYMENT_RECON, DEMAND_PAYMENT_RECON_ACC, VERSION_NO, PARENT_VERSION_NO ) VALUES ( ARM_ADJ_RES_CONFIG_MASTER_SID, METHODOLOGY, ADJUSTMENT_TYPE, CURRENT_BALANCE, CURRENT_BALANCE_ACC, DEMAND_ACCRUAL, DEMAND_ACCRUAL_ACC, DEMAND_REFORECAST, DEMAND_REFORECAST_ACC, DEMAND_PAYMENT_RECON, DEMAND_PAYMENT_RECON_ACC, VERSION_NO, PARENT_VERSION_NO ) ;
