entity.query:
DECLARE @FROMPERIOD DATETIME; DECLARE @TOPERIOD DATETIME;
SET @FROMPERIOD = (SELECT TOP 1 PERIOD_DATE FROM DBO.PERIOD WHERE ? = ? AND YEAR = ? ORDER BY PERIOD_DATE ASC); SET @TOPERIOD = (SELECT TOP 1 PERIOD_DATE FROM DBO.PERIOD WHERE ? = ? AND YEAR = ? ORDER BY PERIOD_DATE ASC);
; WITH CTE AS (SELECT DISTINCT YEAR, ? FROM PERIOD WHERE PERIOD_DATE <= ( @TOPERIOD ) AND PERIOD_DATE >= ( @FROMPERIOD )) SELECT COUNT(*) FROM CTE
