entity.query:
DECLARE @ARM_ADJ_RES INT =?, @COUNT INT SELECT @COUNT = COUNT(ARM_ADJ_RES_CONFIG_DETAIL_SID) FROM ? WHERE ARM_ADJ_RES_CONFIG_MASTER_SID = @ARM_ADJ_RES IF( @COUNT = 0 ) BEGIN SELECT NULL END ELSE BEGIN IF NOT EXISTS( SELECT * FROM ? WHERE ARM_ADJ_RES_CONFIG_MASTER_SID =@ARM_ADJ_RES AND( ( ADJUSTMENT_TYPE IS NULL OR ADJUSTMENT_TYPE = '' OR ADJUSTMENT_TYPE = 0 ) OR( ADJUSTMENT_LEVEL IS NULL OR ADJUSTMENT_LEVEL = '' OR ADJUSTMENT_LEVEL = 0 ) OR( ACCOUNT IS NULL OR ACCOUNT = ''
) OR( REPORT_INDICATOR IS NULL
)
OR( PROJECT IS NULL OR PROJECT = ''
) OR( FUTURE_1 IS NULL OR FUTURE_1 = ''
) OR( FUTURE_2 IS NULL OR FUTURE_2 = ''
) OR( BALANCE_TYPE IS NULL OR BALANCE_TYPE = ''
) OR( [DATABASE] IS NULL OR [DATABASE] = ''
) OR( DATA_ACCESS_SET IS NULL OR DATA_ACCESS_SET = ''
) OR( CHART_OF_ACCOUNTS IS NULL OR CHART_OF_ACCOUNTS = ''
) OR( LEDGER IS NULL OR LEDGER = ''
) OR( CATEGORY IS NULL OR CATEGORY = ''
) OR( DATA_ACCESS_SET IS NULL OR DATA_ACCESS_SET = ''
) OR( SOURCE IS NULL OR SOURCE = ''
) OR( JOURNAL_NAME IS NULL OR JOURNAL_NAME = ''
) OR( REVERSE_JOURNAL IS NULL OR REVERSE_JOURNAL = ''
) ) ) SELECT 1 ELSE SELECT 0 END

insert into ? ( ARM_ADJ_RES_CONFIG_MASTER_SID, ADJUSTMENT_TYPE, ADJUSTMENT_LEVEL, ACCOUNT_CATEGORY, ACCOUNT_TYPE, ACCOUNT, ACCOUNT_DESC, ACCOUNT_INDICATOR, COST_CENTER, PROJECT, FUTURE_1, FUTURE_2, BALANCE_TYPE, [DATABASE], DATA_ACCESS_SET, CHART_OF_ACCOUNTS, LEDGER, CATEGORY, SOURCE, CURRENCY, JOURNAL_NAME, JOURNAL_DESCRIPTION, REVERSE_JOURNAL, REVERSAL_PERIOD_DATE, LINE_DESCRIPTION, UDC_1, UDC_2, UDC_3, UDC_4, UDC_5, UDC_6, CREDIT, DEBIT, DIVISION,REPORT_INDICATOR
) select ARM_ADJ_RES_CONFIG_MASTER_SID, ADJUSTMENT_TYPE, ADJUSTMENT_LEVEL, ACCOUNT_CATEGORY, ACCOUNT_TYPE, ACCOUNT, ACCOUNT_DESC, ACCOUNT_INDICATOR, COST_CENTER, PROJECT, FUTURE_1, FUTURE_2, BALANCE_TYPE, [DATABASE], DATA_ACCESS_SET, CHART_OF_ACCOUNTS, LEDGER, CATEGORY, SOURCE, CURRENCY, JOURNAL_NAME, JOURNAL_DESCRIPTION, REVERSE_JOURNAL, REVERSAL_PERIOD_DATE, LINE_DESCRIPTION, UDC_1, UDC_2, UDC_3, UDC_4, UDC_5, UDC_6, CREDIT, DEBIT, DIVISION,REPORT_INDICATOR
FROM ARM_ADJ_RES_CONFIG_DETAIL where ARM_ADJ_RES_CONFIG_MASTER_SID in(?) order by ARM_ADJ_RES_CONFIG_DETAIL_SID asc




update ? set ARM_ADJ_RES_CONFIG_MASTER_SID=?
where ARM_ADJ_RES_CONFIG_MASTER_SID=?;

update ? set ARM_ADJ_RES_CONFIG_MASTER_SID=?
where ARM_ADJ_RES_CONFIG_MASTER_SID=?;



UPDATE ARM_ADJ_RES_CONFIG_MASTER
SET MODIFIED_DATE = GETDATE()
WHERE ARM_ADJ_RES_CONFIG_MASTER_SID IN (?);
