entity.query:
DELETE ARM_INFLATION_INVENTORY WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE RATE FROM dbo.ARM_INFLATION_INVENTORY_ADJ RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE AA FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ;
INSERT INTO ARM_INFLATION_INVENTORY( PROJECTION_MASTER_SID, ITEM_MASTER_SID, COMPANY_MASTER_SID, PERIOD_SID, TOTAL_INVENTORY, BASELINE_PRICE, BASELINE_PRICE_OVERRIDE, ADJUSTED_PRICE, ADJUSTED_PRICE_OVERRIDE, PRICE_CHANGE, PRICE_CHANGE_PERCENT, BASELINE_CALCULATED_AMOUNT, ADJUSTED_CALCULATED_AMOUNT, NET_CALCULATED_AMOUNT, INFLATION_FACTOR, INFLATION_ADJUSTMENT ) SELECT PROJECTION_MASTER_SID, ITEM_MASTER_SID, COMPANY_MASTER_SID, PERIOD_SID, TOTAL_INVENTORY, BASELINE_PRICE, BASELINE_PRICE_OVERRIDE, ADJUSTED_PRICE, ADJUSTED_PRICE_OVERRIDE, PRICE_CHANGE, PRICE_CHANGE_PERCENT, BASELINE_CALCULATED_AMOUNT, ADJUSTED_CALCULATED_AMOUNT, NET_CALCULATED_AMOUNT, INFLATION_FACTOR, INFLATION_ADJUSTMENT FROM ST_ARM_INFLATION_INVENTORY WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID
INSERT INTO ARM_INFLATION_INVENTORY_ADJ( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, CURRENT_BALANCE, CALCULATED_ADJUSTMENT, ADJUSTMENT_RATIO, VARIANCE, OVERRIDE ) SELECT RATE.ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, CURRENT_BALANCE, CALCULATED_ADJUSTMENT, ADJUSTMENT_RATIO, VARIANCE, OVERRIDE FROM ST_ARM_INFLATION_INVENTORY_ADJ RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ;
INSERT INTO ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT FROM ST_ARM_ADJUSTMENTS;
DELETE ST_ARM_INFLATION_INVENTORY WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ;
DELETE RATE FROM ST_ARM_INFLATION_INVENTORY_ADJ RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ;
DELETE AA FROM ST_ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ;

INSERT INTO ST_ARM_INFLATION_INVENTORY( PROJECTION_MASTER_SID, ITEM_MASTER_SID, COMPANY_MASTER_SID, PERIOD_SID, TOTAL_INVENTORY, BASELINE_PRICE, BASELINE_PRICE_OVERRIDE, ADJUSTED_PRICE, ADJUSTED_PRICE_OVERRIDE, PRICE_CHANGE, PRICE_CHANGE_PERCENT, BASELINE_CALCULATED_AMOUNT, ADJUSTED_CALCULATED_AMOUNT, NET_CALCULATED_AMOUNT, INFLATION_FACTOR, INFLATION_ADJUSTMENT ) SELECT PROJECTION_MASTER_SID, ITEM_MASTER_SID, COMPANY_MASTER_SID, PERIOD_SID, TOTAL_INVENTORY, BASELINE_PRICE, BASELINE_PRICE_OVERRIDE, ADJUSTED_PRICE, ADJUSTED_PRICE_OVERRIDE, PRICE_CHANGE, PRICE_CHANGE_PERCENT, BASELINE_CALCULATED_AMOUNT, ADJUSTED_CALCULATED_AMOUNT, NET_CALCULATED_AMOUNT, INFLATION_FACTOR, INFLATION_ADJUSTMENT FROM ARM_INFLATION_INVENTORY WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID
INSERT INTO ST_ARM_INFLATION_INVENTORY_ADJ( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, CURRENT_BALANCE, CALCULATED_ADJUSTMENT, ADJUSTMENT_RATIO, VARIANCE, OVERRIDE
) SELECT RATE.ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, CURRENT_BALANCE, CALCULATED_ADJUSTMENT, ADJUSTMENT_RATIO, VARIANCE, OVERRIDE FROM ARM_INFLATION_INVENTORY_ADJ RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID
INSERT INTO ST_ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT AA.ARM_ADJUSTMENT_DETAILS_SID, AA.PERIOD_SID, AA.ADJUSTMENT_TYPE, AA.CREDIT, AA.DEBIT FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
