entity.query:
DECLARE @PROJECTION_MASTER_SID INT = ?,
@ITEM_MASTER_SID INT = ?, @OVERRIDE_VALUE INT = ? , @CONTRACT_MASTER_SID INT = ?, @COMPANY_MASTER_SID INT = ?, @BRAND_MASTER_SID INT = ?, @RS_MODEL_SID INT = ?
UPDATE RATE SET RATE.TEMP_OVERRIDE = @OVERRIDE_VALUE FROM ? RATE JOIN( SELECT RATE.ARM_ADJUSTMENT_DETAILS_SID, COALESCE(abs(CALCULATED_ADJUSTMENT)/NULLIF(SUM(abs(CALCULATED_ADJUSTMENT)) OVER(PARTITION BY CCP.ITEM_MASTER_SID,CCP.COMPANY_MASTER_SID,CCP.CONTRACT_MASTER_SID, AD.RS_MODEL_SID),0),0) AS RATIO FROM ? RATE JOIN ARM_ADJUSTMENT_DETAILS AD ON AD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID JOIN CCP_DETAILS CCP ON CCP.CCP_DETAILS_SID = AD.CCP_DETAILS_SID JOIN dbo.ITEM_MASTER IM ON IM.ITEM_MASTER_SID = CCP.ITEM_MASTER_SID WHERE AD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID AND CCP.ITEM_MASTER_SID = @ITEM_MASTER_SID AND CCP.CONTRACT_MASTER_SID LIKE @CONTRACT_MASTER_SID AND CCP.COMPANY_MASTER_SID LIKE @COMPANY_MASTER_SID AND IM.BRAND_MASTER_SID LIKE @BRAND_MASTER_SID AND AD.RS_MODEL_SID = @RS_MODEL_SID ) A ON RATE.ARM_ADJUSTMENT_DETAILS_SID = A.ARM_ADJUSTMENT_DETAILS_SID

-- MAIN TABLE DELETE DELETE ARM_DISTRIBUTION_FEES_SALES WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE RATE FROM ARM_DISTRIBUTION_FEES_RATE RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON RATE.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE ARM_PIPELINE_EXCLUSION_DETAILS WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE AA FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ;
-- TEMP TO MAIN INSERT INSERT INTO ARM_DISTRIBUTION_FEES_SALES( PROJECTION_MASTER_SID, COMPANY_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, TOTAL_SALES, EXCLUDED_UNITS, EXCLUDED_SALES, NET_UNITS, NET_SALES, PRICE, PRICE_OVERRIDE, NET_CALCULATED_SALES, SALES_VARIANCE, SALES_VARIANCE_PERCENT ) SELECT PROJECTION_MASTER_SID, COMPANY_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, TOTAL_SALES, EXCLUDED_UNITS, EXCLUDED_SALES, NET_UNITS, NET_SALES, PRICE, PRICE_OVERRIDE, NET_CALCULATED_SALES, SALES_VARIANCE, SALES_VARIANCE_PERCENT FROM ST_ARM_DISTRIBUTION_FEES_SALES
INSERT INTO ARM_DISTRIBUTION_FEES_RATE( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, RATE, CURRENT_BALANCE, CALCULATED_ADJUSTMENT, ADJUSTMENT_RATIO, VARIANCE, [OVERRIDE], DEDUCTION_AMOUNT ) SELECT ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, RATE, CURRENT_BALANCE, CALCULATED_ADJUSTMENT, ADJUSTMENT_RATIO, VARIANCE, [OVERRIDE], DEDUCTION_AMOUNT FROM ST_ARM_DISTRIBUTION_FEES_RATE
INSERT into ARM_PIPELINE_EXCLUSION_DETAILS( PROJECTION_MASTER_SID, COMPANY_MASTER_SID ) SELECT PROJECTION_MASTER_SID, COMPANY_MASTER_SID FROM ST_ARM_PIPELINE_EXCLUSION_DETAILS
INSERT INTO ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT FROM ST_ARM_ADJUSTMENTS;

INSERT INTO ST_ARM_DISTRIBUTION_FEES_SALES( PROJECTION_MASTER_SID, COMPANY_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, TOTAL_SALES, EXCLUDED_UNITS, EXCLUDED_SALES, NET_UNITS, NET_SALES, PRICE, PRICE_OVERRIDE, NET_CALCULATED_SALES, SALES_VARIANCE, SALES_VARIANCE_PERCENT
) SELECT PROJECTION_MASTER_SID, COMPANY_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, TOTAL_SALES, EXCLUDED_UNITS, EXCLUDED_SALES, NET_UNITS, NET_SALES, PRICE, PRICE_OVERRIDE, NET_CALCULATED_SALES, SALES_VARIANCE, SALES_VARIANCE_PERCENT
FROM ARM_DISTRIBUTION_FEES_SALES where PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
INSERT INTO ST_ARM_DISTRIBUTION_FEES_RATE( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, RATE, CURRENT_BALANCE, CALCULATED_ADJUSTMENT, ADJUSTMENT_RATIO, VARIANCE, [OVERRIDE] ) SELECT RATE.ARM_ADJUSTMENT_DETAILS_SID, RATE.PERIOD_SID, RATE.RATE, RATE.CURRENT_BALANCE, RATE.CALCULATED_ADJUSTMENT, RATE.ADJUSTMENT_RATIO, RATE.VARIANCE, RATE.[OVERRIDE] FROM ARM_DISTRIBUTION_FEES_RATE RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON RATE.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID; INSERT into ST_ARM_PIPELINE_EXCLUSION_DETAILS( PROJECTION_MASTER_SID, COMPANY_MASTER_SID ) SELECT PROJECTION_MASTER_SID, COMPANY_MASTER_SID FROM ARM_PIPELINE_EXCLUSION_DETAILS WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID; INSERT INTO ST_ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT AA.ARM_ADJUSTMENT_DETAILS_SID, AA.PERIOD_SID, AA.ADJUSTMENT_TYPE, AA.CREDIT, AA.DEBIT FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
