entity.query:
DELETE ADAS FROM ARM_DEMAND_ADJ_SUMMARY ADAS JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = ADAS.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE AA FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
INSERT INTO ARM_DEMAND_ADJ_SUMMARY( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, DEMAND_ACCRUAL, DEMAND_ACCRUAL_REFORECAST, TOTAL_DEMAND_ACCRUAL, PROJ_TOTAL_DEMAND_ACCRUAL, DEMAND_ACCRUAL_RATIO, VARIANCE, OVERRIDE, PROJECTED_RATE )SELECT ADAS.ARM_ADJUSTMENT_DETAILS_SID, ADAS.PERIOD_SID, ADAS.DEMAND_ACCRUAL, ADAS.DEMAND_ACCRUAL_REFORECAST, ADAS.TOTAL_DEMAND_ACCRUAL, ADAS.PROJ_TOTAL_DEMAND_ACCRUAL, ADAS.DEMAND_ACCRUAL_RATIO, ADAS.VARIANCE, ADAS.OVERRIDE, ADAS.PROJECTED_RATE FROM ST_ARM_DEMAND_ADJ_SUMMARY ADAS JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = ADAS.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
INSERT INTO ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT FROM ST_ARM_ADJUSTMENTS;
DELETE ADAS FROM ST_ARM_DEMAND_ADJ_SUMMARY ADAS JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = ADAS.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE AA FROM ST_ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
