entity.query:
DELETE ADRS FROM ARM_DEMAND_RECON_SUMMARY ADRS JOIN ARM_ADJUSTMENT_DETAILS AAD ON ADRS.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ; DELETE AA FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
INSERT INTO ARM_DEMAND_RECON_SUMMARY (ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID,DEMAND_ACCRUAL, DEMAND_ACCRUAL_REFORECAST, DEMAND_PAYMENT_RECON, TOTAL_DEMAND_ACCRUAL, ACTUAL_PAYMENTS, PAYMENT_RATIO, VARIANCE, OVERRIDE, PROJECTED_RATE) SELECT ADRS.ARM_ADJUSTMENT_DETAILS_SID, ADRS.PERIOD_SID, ADRS.DEMAND_ACCRUAL, ADRS.DEMAND_ACCRUAL_REFORECAST, ADRS.DEMAND_PAYMENT_RECON, ADRS.TOTAL_DEMAND_ACCRUAL, ADRS.ACTUAL_PAYMENTS, ADRS.PAYMENT_RATIO, ADRS.VARIANCE, ADRS.OVERRIDE, ADRS.PROJECTED_RATE FROM ST_ARM_DEMAND_RECON_SUMMARY ADRS JOIN ARM_ADJUSTMENT_DETAILS AAD ON ADRS.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ;
INSERT INTO ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT FROM ST_ARM_ADJUSTMENTS;
DELETE ADRS FROM ST_ARM_DEMAND_RECON_SUMMARY ADRS JOIN ARM_ADJUSTMENT_DETAILS AAD ON ADRS.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ; DELETE AA FROM ST_ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
