entity.query:

           SELECT FIELD_NAME,FIELD_VALUES 
              from ARM_PROJ_SELECTION 
           where PROJECTION_MASTER_SID = '@projectionId';


           SELECT FIELD_NAME,FIELD_VALUES 
              from ARM_PROJ_SELECTION 
           where PROJECTION_MASTER_SID = '@projectionId' AND FIELD_NAME in (@fieldname) ORDER BY FIELD_NAME ASC;

-- MAIN TABLE DELETE DELETE ARM_PIPELINE_SALES WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE RATE FROM ARM_PIPELINE_RATE RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON RATE.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE ARM_PIPELINE_EXCLUSION_DETAILS WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID; DELETE AA FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
-- TEMP TO MAIN INSERT INSERT INTO ARM_PIPELINE_SALES( PROJECTION_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, TOTAL_SALES, EXCLUDED_UNITS, EXCLUDED_SALES, NET_UNITS, NET_SALES, PRICE, PRICE_OVERRIDE, NET_CALCULATED_SALES, SALES_VARIANCE, SALES_VARIANCE_PERCENT ) SELECT PROJECTION_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, TOTAL_SALES, EXCLUDED_UNITS, EXCLUDED_SALES, NET_UNITS, NET_SALES, PRICE, PRICE_OVERRIDE, NET_CALCULATED_SALES, SALES_VARIANCE, SALES_VARIANCE_PERCENT FROM ST_ARM_PIPELINE_SALES
INSERT INTO ARM_PIPELINE_RATE( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, RATE, CURRENT_PIPELINE_ACCRUAL, PROJECTED_PIPELINE_ACCRUAL, VARIANCE, OVERRIDE, SALES_AMOUNT, DISCOUNT_AMOUNT ) SELECT ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, RATE, CURRENT_PIPELINE_ACCRUAL, PROJECTED_PIPELINE_ACCRUAL, VARIANCE, OVERRIDE, SALES_AMOUNT, DISCOUNT_AMOUNT FROM ST_ARM_PIPELINE_RATE
INSERT into ARM_PIPELINE_EXCLUSION_DETAILS( PROJECTION_MASTER_SID, COMPANY_MASTER_SID ) SELECT PROJECTION_MASTER_SID, COMPANY_MASTER_SID FROM ST_ARM_PIPELINE_EXCLUSION_DETAILS
INSERT INTO ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT FROM ST_ARM_ADJUSTMENTS;

INSERT INTO ST_ARM_PIPELINE_SALES( PROJECTION_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, TOTAL_SALES, EXCLUDED_UNITS, EXCLUDED_SALES, NET_UNITS, NET_SALES, PRICE, PRICE_OVERRIDE, NET_CALCULATED_SALES, SALES_VARIANCE, SALES_VARIANCE_PERCENT
) SELECT PROJECTION_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, TOTAL_SALES, EXCLUDED_UNITS, EXCLUDED_SALES, NET_UNITS, NET_SALES, PRICE, PRICE_OVERRIDE, NET_CALCULATED_SALES, SALES_VARIANCE, SALES_VARIANCE_PERCENT
FROM ARM_PIPELINE_SALES where PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
INSERT INTO ST_ARM_PIPELINE_RATE( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, RATE, CURRENT_PIPELINE_ACCRUAL, PROJECTED_PIPELINE_ACCRUAL, VARIANCE, OVERRIDE
) SELECT RATE.ARM_ADJUSTMENT_DETAILS_SID, RATE.PERIOD_SID, RATE.RATE, RATE.CURRENT_PIPELINE_ACCRUAL, RATE.PROJECTED_PIPELINE_ACCRUAL, RATE.VARIANCE, RATE.OVERRIDE
FROM ARM_PIPELINE_RATE RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON RATE.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID; INSERT into ST_ARM_PIPELINE_EXCLUSION_DETAILS( PROJECTION_MASTER_SID, COMPANY_MASTER_SID ) SELECT PROJECTION_MASTER_SID, COMPANY_MASTER_SID FROM ARM_PIPELINE_EXCLUSION_DETAILS WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
INSERT INTO ST_ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT AA.ARM_ADJUSTMENT_DETAILS_SID, AA.PERIOD_SID, AA.ADJUSTMENT_TYPE, AA.CREDIT, AA.DEBIT FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
