entity.query:
DELETE ARM_INVENTORY WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE RATE FROM ARM_INVENTORY_RATE RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE ARM_TR_INVENTORY_DETAILS WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE AA FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
INSERT INTO dbo.ARM_INVENTORY( PROJECTION_MASTER_SID, ITEM_MASTER_SID, COMP_CUST_MASTER_SID, PERIOD_SID, INVENTORY_UNITS, TOTAL_INVENTORY, WEEKS_ON_HAND, UNITS_PER_RETAIL, PRICE, RETURN_RESERVE, NET_PIPELINE_VALUE, PRICE_OVERRIDE ) SELECT PROJECTION_MASTER_SID, ITEM_MASTER_SID, COMP_CUST_MASTER_SID, PERIOD_SID, INVENTORY_UNITS, TOTAL_INVENTORY, WEEKS_ON_HAND, UNITS_PER_RETAIL, PRICE, RETURN_RESERVE, NET_PIPELINE_VALUE, PRICE_OVERRIDE FROM ST_ARM_INVENTORY WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID
INSERT INTO ARM_INVENTORY_RATE( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, RATE, CURRENT_PIPELINE_INVENTORY, PROJECTED_PIPELINE_INVENTORY, PIPELINE_RATIO, VARIANCE, OVERRIDE, SALES_AMOUNT, DISCOUNT_AMOUNT ) SELECT RATE.ARM_ADJUSTMENT_DETAILS_SID, RATE.PERIOD_SID, RATE.RATE, RATE.CURRENT_PIPELINE_INVENTORY, RATE.PROJECTED_PIPELINE_INVENTORY, RATE.PIPELINE_RATIO, RATE.VARIANCE, RATE.OVERRIDE, RATE.SALES_AMOUNT, RATE.DISCOUNT_AMOUNT FROM ST_ARM_INVENTORY_RATE RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID ;
INSERT INTO ARM_TR_INVENTORY_DETAILS( PROJECTION_MASTER_SID, CHECK_RECORD, "INDICATOR", COMPANY_MASTER_SID, COMPANY_GROUP_SID ) SELECT PROJECTION_MASTER_SID, CHECK_RECORD, "INDICATOR", COMPANY_MASTER_SID, COMPANY_GROUP_SID FROM ST_ARM_TR_INVENTORY_DETAILS WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
INSERT INTO ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT FROM ST_ARM_ADJUSTMENTS;
DELETE ST_ARM_INVENTORY WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE AA FROM ST_ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE RATE FROM ST_ARM_INVENTORY_RATE RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
DELETE ST_ARM_TR_INVENTORY_DETAILS WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;

INSERT INTO ST_ARM_INVENTORY( PROJECTION_MASTER_SID, ITEM_MASTER_SID, COMP_CUST_MASTER_SID, PERIOD_SID, INVENTORY_UNITS, TOTAL_INVENTORY, WEEKS_ON_HAND, UNITS_PER_RETAIL, PRICE, RETURN_RESERVE, NET_PIPELINE_VALUE, PRICE_OVERRIDE ) SELECT PROJECTION_MASTER_SID, ITEM_MASTER_SID, COMP_CUST_MASTER_SID, PERIOD_SID, INVENTORY_UNITS, TOTAL_INVENTORY, WEEKS_ON_HAND, UNITS_PER_RETAIL, PRICE, RETURN_RESERVE, NET_PIPELINE_VALUE, PRICE_OVERRIDE FROM ARM_INVENTORY WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID
INSERT INTO ST_ARM_INVENTORY_RATE( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, RATE, CURRENT_PIPELINE_INVENTORY, PROJECTED_PIPELINE_INVENTORY, PIPELINE_RATIO, VARIANCE, OVERRIDE
) SELECT RATE.ARM_ADJUSTMENT_DETAILS_SID, RATE.PERIOD_SID, RATE.RATE, RATE.CURRENT_PIPELINE_INVENTORY, RATE.PROJECTED_PIPELINE_INVENTORY, RATE.PIPELINE_RATIO, RATE.VARIANCE, RATE.OVERRIDE FROM ARM_INVENTORY_RATE RATE JOIN ARM_ADJUSTMENT_DETAILS AAD ON AAD.ARM_ADJUSTMENT_DETAILS_SID = RATE.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID
INSERT INTO ST_ARM_TR_INVENTORY_DETAILS( PROJECTION_MASTER_SID, CHECK_RECORD, "INDICATOR", COMPANY_MASTER_SID, COMPANY_GROUP_SID ) SELECT PROJECTION_MASTER_SID, CHECK_RECORD, "INDICATOR", COMPANY_MASTER_SID, COMPANY_GROUP_SID FROM ARM_TR_INVENTORY_DETAILS WHERE PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID; INSERT INTO ST_ARM_ADJUSTMENTS( ARM_ADJUSTMENT_DETAILS_SID, PERIOD_SID, ADJUSTMENT_TYPE, ACCOUNT, DEBIT, CREDIT ) SELECT AA.ARM_ADJUSTMENT_DETAILS_SID, AA.PERIOD_SID, AA.ADJUSTMENT_TYPE, AA.CREDIT, AA.DEBIT FROM ARM_ADJUSTMENTS AA JOIN ARM_ADJUSTMENT_DETAILS AAD ON AA.ARM_ADJUSTMENT_DETAILS_SID = AAD.ARM_ADJUSTMENT_DETAILS_SID WHERE AAD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID;
