entity.query:
IF EXISTS (SELECT 1 FROM ?tableNameCCP_DEDUCTION_HIERARCHY B JOIN #SELECTED_HIERARCHY_NO CCP ON B.CCP_DETAILS_SID = CCP.CCP_DETAILS_SID AND B.DEDUCTION_HIERARCHY_NO=CCP.HIERARCHY_NO ) BEGIN SELECT DISTINCT RS_CONTRACT_SID FROM ?tableNameCCP_DEDUCTION_HIERARCHY B JOIN #SELECTED_HIERARCHY_NO CCP ON B.CCP_DETAILS_SID = CCP.CCP_DETAILS_SID AND FILTER_CCPD=1 AND B.DEDUCTION_HIERARCHY_NO=CCP.HIERARCHY_NO
END ELSE BEGIN SELECT DISTINCT RS_CONTRACT_SID FROM ?tableNameCCP_DEDUCTION_HIERARCHY B
JOIN #SELECTED_HIERARCHY_NO_TEMP CCP ON B.CCP_DETAILS_SID = CCP.CCP_DETAILS_SID AND FILTER_CCPD=1 END;
