entity.query:
DECLARE @CFF_MASTER_SID INT =?, @IS_NON_MANDATED BIT=0, @IS_MANDATED BIT=0 DECLARE @TEMP TABLE ( TYPE VARCHAR(100) );
INSERT INTO @TEMP SELECT DISTINCT FORECASTING_TYPE FROM PROJECTION_MASTER PM WHERE EXISTS (SELECT 1 FROM DBO.CFF_MASTER M JOIN CFF_DETAILS D ON D.CFF_MASTER_SID = M.CFF_MASTER_SID AND PM.PROJECTION_MASTER_SID = D.PROJECTION_MASTER_SID WHERE M.CFF_MASTER_SID = @CFF_MASTER_SID);
SELECT @IS_NON_MANDATED = 1 FROM @TEMP WHERE TYPE = 'NON MANDATED';
SELECT @IS_MANDATED = 1 FROM @TEMP WHERE TYPE = 'MANDATED';
DECLARE @TEMP_MANDATED_DISCOUNTS TABLE ( RS_MODEL_SID INT, DISCOUNT VARCHAR(100), CHECK_RECORD BIT )
INSERT INTO @TEMP_MANDATED_DISCOUNTS VALUES (0, 'MANDATED', @IS_MANDATED), (0, 'SUPPLEMENTAL', @IS_MANDATED);
DECLARE @TEMP_NM_DISCOUNTS TABLE ( RS_MODEL_SID INT, DISCOUNT VARCHAR(500), CHECK_RECORD BIT );
INSERT INTO @TEMP_NM_DISCOUNTS SELECT DISTINCT NMDP.RS_MODEL_SID, RS_NAME, @IS_NON_MANDATED FROM CFF_DETAILS CFF JOIN PROJECTION_DETAILS PD ON CFF.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID AND CFF.CCP_DETAILS_SID = PD.CCP_DETAILS_SID JOIN NM_DISCOUNT_PROJ_MASTER NMDP ON NMDP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID JOIN RS_CONTRACT RM ON RM.RS_CONTRACT_SID=NMDP.RS_CONTRACT_SID JOIN CCP_DETAILS CCP ON CCP.CCP_DETAILS_SID=CFF.CCP_DETAILS_SID WHERE CFF.CFF_MASTER_SID = @CFF_MASTER_SID UNION ALL SELECT DISTINCT RM.RS_MODEL_SID, RM.RS_NAME, @IS_NON_MANDATED FROM RS_CONTRACT RM WHERE EXISTS(SELECT 1 FROM CFF_DETAILS CFF JOIN PROJECTION_DETAILS PD ON CFF.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID AND CFF.CCP_DETAILS_SID = PD.CCP_DETAILS_SID JOIN NM_PPA_PROJECTION_MASTER NMDP ON NMDP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID WHERE CFF.CFF_MASTER_SID = @CFF_MASTER_SID AND RM.RS_CONTRACT_SID = NMDP.RS_CONTRACT_SID)
SELECT RS_MODEL_SID, DISCOUNT FROM (SELECT * FROM @TEMP_MANDATED_DISCOUNTS UNION ALL SELECT * FROM @TEMP_NM_DISCOUNTS) A WHERE CHECK_RECORD = 1 ORDER BY DISCOUNT

DECLARE @CFF_MASTER_SID INT =?, @IS_NON_MANDATED BIT=0, @IS_MANDATED BIT=0 DECLARE @TEMP TABLE ( TYPE VARCHAR(100) );
INSERT INTO @TEMP SELECT DISTINCT FORECASTING_TYPE FROM PROJECTION_MASTER PM WHERE EXISTS (SELECT 1 FROM DBO.CFF_MASTER M JOIN CFF_DETAILS D ON D.CFF_MASTER_SID = M.CFF_MASTER_SID AND PM.PROJECTION_MASTER_SID = D.PROJECTION_MASTER_SID WHERE M.CFF_MASTER_SID = @CFF_MASTER_SID);
SELECT @IS_NON_MANDATED = 1 FROM @TEMP WHERE TYPE = 'NON MANDATED';
SELECT @IS_MANDATED = 1 FROM @TEMP WHERE TYPE = 'MANDATED';
DECLARE @TEMP_MANDATED_DISCOUNTS TABLE ( DISCOUNT VARCHAR(100), CHECK_RECORD BIT )
INSERT INTO @TEMP_MANDATED_DISCOUNTS VALUES ('MANDATED', @IS_MANDATED), ('SUPPLEMENTAL', @IS_MANDATED);
DECLARE @TEMP_NM_DISCOUNTS TABLE ( DISCOUNT VARCHAR(500), CHECK_RECORD BIT );
INSERT INTO @TEMP_NM_DISCOUNTS SELECT DISTINCT PRICE_GROUP_TYPE AS DISCOUNT, @IS_NON_MANDATED FROM CFF_DETAILS CFF JOIN PROJECTION_DETAILS PD ON CFF.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID AND CFF.CCP_DETAILS_SID = PD.CCP_DETAILS_SID JOIN NM_DISCOUNT_PROJ_MASTER NMDP ON NMDP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID JOIN RS_CONTRACT RM ON RM.RS_CONTRACT_SID=NMDP.RS_CONTRACT_SID JOIN CCP_DETAILS CCP ON CCP.CCP_DETAILS_SID=CFF.CCP_DETAILS_SID WHERE CFF.CFF_MASTER_SID = @CFF_MASTER_SID AND PRICE_GROUP_TYPE IS NOT NULL AND PRICE_GROUP_TYPE NOT IN( '-SELECT ONE-' ) UNION ALL SELECT DISTINCT HT.DESCRIPTION AS DISCOUNT, @IS_NON_MANDATED FROM RS_CONTRACT RM JOIN HELPER_TABLE HT ON HT.HELPER_TABLE_SID = RM.REBATE_PROGRAM_TYPE AND HT.HELPER_TABLE_SID <> 0 AND HT.LIST_NAME = 'REBATE_PROGRAM_TYPE' WHERE EXISTS (SELECT 1 FROM CFF_DETAILS CFF JOIN PROJECTION_DETAILS PD ON CFF.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID AND CFF.CCP_DETAILS_SID = PD.CCP_DETAILS_SID JOIN NM_PPA_PROJECTION_MASTER NMDP ON NMDP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID WHERE CFF.CFF_MASTER_SID = @CFF_MASTER_SID AND RM.RS_CONTRACT_SID = NMDP.RS_CONTRACT_SID)
SELECT DISCOUNT FROM (SELECT * FROM @TEMP_MANDATED_DISCOUNTS UNION ALL SELECT * FROM @TEMP_NM_DISCOUNTS) A WHERE CHECK_RECORD = 1 ORDER BY DISCOUNT

IF Object_id('TEMPDB.DBO.#CFF_APPROVAL_DETAILS', 'U') IS NOT NULL DROP TABLE #CFF_APPROVAL_DETAILS;
select * into #CFF_APPROVAL_DETAILS from ( select *,row_number()over(partition by cff_master_sid order by CFF_APPROVAL_DETAILS_sid desc)as rn from CFF_APPROVAL_DETAILS )a JOIN HELPER_TABLE HT1 ON HT1.HELPER_TABLE_SID = a.APPROVAL_STATUS AND HT1.DESCRIPTION = '?' where rn=1 ; WITH CTE AS (SELECT PM.CFF_NAME, PM.CFF_NAME AS CFF_DESCRIPTION, CASE WHEN COUNT(DISTINCT HT.DESCRIPTION) > 1 THEN 'MULTIPLE' ELSE MAX(HT.DESCRIPTION) END AS MARKET_TYPE, CASE WHEN COUNT(DISTINCT CM.COMPANY_NO) > 1 THEN 'MULTIPLE' ELSE MAX(CM.COMPANY_NO) END AS CONTRACT_HOLDER, CASE WHEN COUNT(DISTINCT C.CONTRACT_NO) > 1 THEN 'MULTIPLE' ELSE MAX(C.CONTRACT_NO) END AS CONTRACT, CASE WHEN COUNT(DISTINCT BM.BRAND_NAME) > 1 THEN 'MULTIPLE' ELSE MAX(BM.BRAND_NAME) END AS BRAND, CASE WHEN COUNT(DISTINCT IM.ITEM_NO) > 1 THEN 'MULTIPLE' ELSE MAX(IM.ITEM_NO) END AS ITEM_NO, CASE WHEN COUNT(DISTINCT IM.ITEM_NAME) > 1 THEN 'MULTIPLE' ELSE MAX(IM.ITEM_NAME) END AS ITEM_NAME, PM.CFF_MASTER_SID, PM.CREATED_DATE, PM.CREATED_BY FROM CFF_MASTER PM INNER JOIN CFF_DETAILS PD ON PD.CFF_MASTER_SID = PM.CFF_MASTER_SID AND PM.CFF_MASTER_SID NOT IN( ? ) AND PM.CFF_NAME LIKE '?' AND PM.CFF_NAME LIKE '?' ? ? JOIN #CFF_APPROVAL_DETAILS CAD ON CAD.CFF_MASTER_SID = PM.CFF_MASTER_SID INNER JOIN CCP_DETAILS CCP ON CCP.CCP_DETAILS_SID = PD.CCP_DETAILS_SID INNER JOIN CONTRACT_MASTER C ON C.CONTRACT_MASTER_SID = CCP.CONTRACT_MASTER_SID AND C.CONTRACT_NAME LIKE '%' AND EXISTS ( SELECT 1 FROM CFF_DETAILS CD WHERE PD.CCP_DETAILS_SID = CD.CCP_DETAILS_SID AND CFF_MASTER_SID IN ( ? ) )
LEFT JOIN HELPER_TABLE HT ON C.CONTRACT_TYPE = HT.HELPER_TABLE_SID AND ( HT.LIST_NAME = 'CONTRACT_TYPE' AND HT.DESCRIPTION LIKE '%' ) LEFT JOIN COMPANY_MASTER CM ON C.CONT_HOLD_COMPANY_MASTER_SID = CM.COMPANY_MASTER_SID AND ( CM.COMPANY_NO LIKE '%' OR CM.COMPANY_NO IS NULL ) JOIN ITEM_MASTER IM ON CCP.ITEM_MASTER_SID = IM.ITEM_MASTER_SID AND IM.ITEM_NAME LIKE '%' AND IM.ITEM_NO LIKE '%' LEFT JOIN BRAND_MASTER BM ON BM.BRAND_MASTER_SID = IM.BRAND_MASTER_SID AND ( BM.BRAND_NAME LIKE '%' OR BM.BRAND_NAME IS NULL ) ? GROUP BY PM.CFF_NAME, PM.CFF_MASTER_SID, PM.CREATED_DATE, PM.CREATED_BY
) SELECT * FROM CTE

              

            DECLARE @TEMP TABLE 
              ( 
                 TYPE VARCHAR(100) 
              ) 
            DECLARE @CFF_MASTER_SID  INT =?, 
                    @IS_NON_MANDATED BIT=0, 
                    @IS_MANDATED     BIT=0, 
                    @NM_RS_COUNT     INT=0, 
                    @NM_PPA_COUNT    INT=0, 
                    @M_COUNT         INT=0,
                    @CCP_IDS VARCHAR(MAX) = '?',
                    @RS_IDS VARCHAR(MAX) = '?'
            INSERT INTO @TEMP 
            SELECT DISTINCT FORECASTING_TYPE 
            FROM   PROJECTION_MASTER PM 
            WHERE  EXISTS (SELECT 1 
                           FROM   DBO.CFF_MASTER M 
                                  JOIN CFF_DETAILS D 
                                    ON D.CFF_MASTER_SID = M.CFF_MASTER_SID 
                                       AND 
                          PM.PROJECTION_MASTER_SID = D.PROJECTION_MASTER_SID 
                           WHERE  M.CFF_MASTER_SID = @CFF_MASTER_SID); 

            SELECT @IS_NON_MANDATED = 1 
            FROM   @TEMP 
            WHERE  TYPE = 'Non Mandated'; 

            SELECT @IS_MANDATED = 1 
            FROM   @TEMP 
            WHERE  TYPE = 'Mandated'; 

            SET @M_COUNT =CASE 
                            WHEN @IS_MANDATED = 1 THEN 2 
                            WHEN @IS_MANDATED = 0 THEN 0 
                          END

SELECT @NM_RS_COUNT = ISNULL(COUNT(DISTINCT RS_MODEL_SID) ,0) FROM NM_DISCOUNT_PROJ_MASTER NMDP WHERE EXISTS (SELECT 1 FROM CFF_DETAILS CFF JOIN PROJECTION_DETAILS PD ON CFF.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID AND CFF.CCP_DETAILS_SID = PD.CCP_DETAILS_SID ? @RSQUERY JOIN RS_MODEL RM ON RM.RS_MODEL_SID = NMDP.RS_MODEL_SID WHERE NMDP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID AND CFF.CFF_MASTER_SID = @CFF_MASTER_SID);
-- PPA PROGRAM TYPE SELECT @NM_PPA_COUNT = ISNULL(COUNT(DISTINCT RM.RS_MODEL_SID),0) FROM RS_MODEL RM WHERE EXISTS (SELECT 1 FROM CFF_DETAILS CFF JOIN PROJECTION_DETAILS PD ON CFF.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID AND CFF.CCP_DETAILS_SID = PD.CCP_DETAILS_SID ? JOIN NM_PPA_PROJECTION_MASTER NMDP ON RM.RS_MODEL_SID = NMDP.RS_MODEL_SID WHERE NMDP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID AND CFF.CFF_MASTER_SID = @CFF_MASTER_SID);
SELECT @M_COUNT + @NM_RS_COUNT + @NM_PPA_COUNT;

SELECT @NM_RS_COUNT = ISNULL(COUNT(DISTINCT PRICE_GROUP_TYPE) ,0) FROM NM_DISCOUNT_PROJ_MASTER NMDP WHERE EXISTS (SELECT 1 FROM CFF_DETAILS CFF JOIN PROJECTION_DETAILS PD ON CFF.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID AND CFF.CCP_DETAILS_SID = PD.CCP_DETAILS_SID ? JOIN RS_MODEL RM ON RM.RS_MODEL_SID = NMDP.RS_MODEL_SID WHERE NMDP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID AND CFF.CFF_MASTER_SID = @CFF_MASTER_SID);
-- PPA PROGRAM CATEGORY SELECT @NM_PPA_COUNT = ISNULL(COUNT(DISTINCT RM.REBATE_PROGRAM_TYPE) ,0) FROM RS_MODEL RM WHERE EXISTS (SELECT 1 FROM CFF_DETAILS CFF JOIN PROJECTION_DETAILS PD ON CFF.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID AND CFF.CCP_DETAILS_SID = PD.CCP_DETAILS_SID ? JOIN NM_PPA_PROJECTION_MASTER NMDP ON RM.RS_MODEL_SID = NMDP.RS_MODEL_SID WHERE NMDP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID AND CFF.CFF_MASTER_SID = @CFF_MASTER_SID);
SELECT @M_COUNT + @NM_RS_COUNT + @NM_PPA_COUNT;

              

            DECLARE @TEMP TABLE 
              ( 
                 TYPE VARCHAR(100) 
              ) 
            DECLARE @CFF_MASTER_SID  INT =?, 
                    @IS_NON_MANDATED BIT=0, 
                    @IS_MANDATED     BIT=0, 
                    @NM_RS_COUNT     INT=0, 
                    @NM_PPA_COUNT    INT=0, 
                    @M_COUNT         INT=0,
                    @CCP_IDS VARCHAR(MAX) = '?',
                    @RS_IDS VARCHAR(MAX) = '?'
            SELECT COUNT(DISTINCT @RS_SELECTION)
     FROM ST_CCP_DEDUCTION_HIERARCHY ST

     JOIN RS_CONTRACT RS ON RS.RS_CONTRACT_SID = ST.RS_CONTRACT_SID
       AND ST.FILTER_CCPD = 1
       @UDCJOIN
       @HELPERTABLEJOIN
       WHERE EXISTS(SELECT 1 FROM UDF_SPLITSTRING(@CCP_IDS,',') B WHERE B.TOKEN=ST.CCP_DETAILS_SID)
       AND EXISTS(SELECT 1 FROM UDF_SPLITSTRING(@RS_IDS,',') B WHERE B.TOKEN=ST.RS_CONTRACT_SID)
