entity.query:
MERGE ST_NM_ASSUMPTIONS AS TARGET USING (SELECT GROSS_PROJECTED.PROJECTION_DETAILS_SID, P.YEAR, SUM(ISNULL(NMS.PROJECTION_SALES, 0)) GROSS_SALES_PRIOR, SUM(ISNULL(NMSP.PROJECTION_SALES, 0)) GROSS_SALES_PROJECTED, SUM(ISNULL(NMSP.PROJECTION_SALES, 0)) - SUM(ISNULL(NMS.PROJECTION_SALES, 0)) GROSS_SALES_CHANGE, ? CAM_ID, 1 PARENT, 0 IS_CHECKED, ? USER_ID, ? SESSION_ID, 0 NM_ASSUMPTIONS_SID FROM (SELECT PD.PROJECTION_MASTER_SID, STNMSPM.PROJECTION_DETAILS_SID, STNMSPM.SESSION_ID, PD.CCP_DETAILS_SID FROM ST_NM_SALES_PROJECTION_MASTER STNMSPM JOIN PROJECTION_DETAILS PD ON PD.PROJECTION_DETAILS_SID = STNMSPM.PROJECTION_DETAILS_SID WHERE STNMSPM.USER_ID = ? AND STNMSPM.SESSION_ID = ? AND PD.PROJECTION_MASTER_SID = ?) GROSS_PROJECTED OUTER apply (SELECT TOP 1 PD.PROJECTION_MASTER_SID, NMSPM.PROJECTION_DETAILS_SID, PD.CCP_DETAILS_SID FROM NM_SALES_PROJECTION_MASTER NMSPM JOIN PROJECTION_DETAILS PD ON PD.PROJECTION_DETAILS_SID = NMSPM.PROJECTION_DETAILS_SID JOIN WORKFLOW_MASTER WM ON WM.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID JOIN HELPER_TABLE HT ON HT.HELPER_TABLE_SID = WM.WORKFLOW_STATUS_ID WHERE (PD.PROJECTION_MASTER_SID < GROSS_PROJECTED.PROJECTION_MASTER_SID OR PD.PROJECTION_MASTER_SID > GROSS_PROJECTED.PROJECTION_MASTER_SID) AND PD.CCP_DETAILS_SID = GROSS_PROJECTED.CCP_DETAILS_SID AND HT.DESCRIPTION = 'Approved' AND HT.LIST_NAME = 'WorkFlowStatus' ORDER BY PD.PROJECTION_MASTER_SID DESC) LATEST_CCP_SALES JOIN ST_NM_SALES_PROJECTION NMSP ON NMSP.PROJECTION_DETAILS_SID = GROSS_PROJECTED.PROJECTION_DETAILS_SID AND NMSP.SESSION_ID=GROSS_PROJECTED.SESSION_ID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID AND P.PERIOD_DATE BETWEEN '?' AND '?' LEFT JOIN NM_SALES_PROJECTION NMS ON NMS.PROJECTION_DETAILS_SID = LATEST_CCP_SALES.PROJECTION_DETAILS_SID AND NMSP.PERIOD_SID = NMS.PERIOD_SID GROUP BY GROSS_PROJECTED.PROJECTION_DETAILS_SID, P.YEAR) AS SOURCE ON ( TARGET.PROJECTION_DETAILS_SID = SOURCE.PROJECTION_DETAILS_SID AND TARGET.PROJECTION_PERIOD = SOURCE.YEAR AND TARGET.USER_ID=SOURCE.USER_ID AND TARGET.SESSION_ID= SOURCE.SESSION_ID AND TARGET.PARENT=1) WHEN MATCHED THEN UPDATE SET TARGET.GROSS_SALES_PRIOR = SOURCE.GROSS_SALES_PRIOR, TARGET.GROSS_SALES_PROJECTED = SOURCE.GROSS_SALES_PROJECTED, TARGET.GROSS_SALES_PERCENT_CHANGE = SOURCE.GROSS_SALES_CHANGE WHEN NOT MATCHED THEN INSERT (NM_ASSUMPTIONS_SID, PROJECTION_DETAILS_SID, PROJECTION_PERIOD, GROSS_SALES_PRIOR, GROSS_SALES_PROJECTED, GROSS_SALES_PERCENT_CHANGE, CAM_ID, PARENT, IS_CHECKED, USER_ID, SESSION_ID ) VALUES ( SOURCE.NM_ASSUMPTIONS_SID, SOURCE.PROJECTION_DETAILS_SID, SOURCE.YEAR, SOURCE.GROSS_SALES_PRIOR, SOURCE.GROSS_SALES_PROJECTED, SOURCE.GROSS_SALES_CHANGE, SOURCE.CAM_ID, SOURCE.PARENT, SOURCE.IS_CHECKED, SOURCE.USER_ID, SOURCE.SESSION_ID );
MERGE ST_NM_ASSUMPTIONS AS TARGET USING (SELECT GROSS_PROJECTED.PROJECTION_DETAILS_SID, P.YEAR, SUM(ISNULL(NMD.PROJECTION_SALES, 0)) TOTAL_DISCOUNT_PRIOR, SUM(ISNULL(NMDP.PROJECTION_SALES, 0)) TOTAL_DISCOUNT_PROJECTED, SUM(ISNULL(NMDP.PROJECTION_SALES, 0)) - SUM(ISNULL(NMD.PROJECTION_SALES, 0)) TOTAL_DISCOUNT_CHANGE, ? CAM_ID, 1 PARENT, 0 IS_CHECKED, ? USER_ID, ? SESSION_ID, 0 NM_ASSUMPTIONS_SID FROM (SELECT PD.PROJECTION_MASTER_SID, STNMSPM.PROJECTION_DETAILS_SID, STNMSPM.SESSION_ID, PD.CCP_DETAILS_SID FROM ST_NM_SALES_PROJECTION_MASTER STNMSPM JOIN PROJECTION_DETAILS PD ON PD.PROJECTION_DETAILS_SID = STNMSPM.PROJECTION_DETAILS_SID WHERE STNMSPM.USER_ID = ? AND STNMSPM.SESSION_ID = ? AND PD.PROJECTION_MASTER_SID = ?) GROSS_PROJECTED OUTER apply (SELECT TOP 1 PD.PROJECTION_MASTER_SID, NMSPM.PROJECTION_DETAILS_SID, PD.CCP_DETAILS_SID FROM NM_SALES_PROJECTION_MASTER NMSPM JOIN PROJECTION_DETAILS PD ON PD.PROJECTION_DETAILS_SID = NMSPM.PROJECTION_DETAILS_SID JOIN WORKFLOW_MASTER WM ON WM.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID JOIN HELPER_TABLE HT ON HT.HELPER_TABLE_SID = WM.WORKFLOW_STATUS_ID WHERE (PD.PROJECTION_MASTER_SID < GROSS_PROJECTED.PROJECTION_MASTER_SID OR PD.PROJECTION_MASTER_SID > GROSS_PROJECTED.PROJECTION_MASTER_SID) AND PD.CCP_DETAILS_SID = GROSS_PROJECTED.CCP_DETAILS_SID AND HT.DESCRIPTION = 'Approved' AND HT.LIST_NAME = 'WorkFlowStatus' ORDER BY PD.PROJECTION_MASTER_SID DESC) LATEST_CCP_SALES LEFT JOIN ST_NM_DISCOUNT_PROJECTION NMDP ON NMDP.PROJECTION_DETAILS_SID = GROSS_PROJECTED.PROJECTION_DETAILS_SID AND NMDP.SESSION_ID=GROSS_PROJECTED.SESSION_ID JOIN PERIOD P ON P.PERIOD_SID = NMDP.PERIOD_SID AND P.PERIOD_DATE BETWEEN '?' AND '?' LEFT JOIN NM_DISCOUNT_PROJECTION NMD ON NMD.PROJECTION_DETAILS_SID = LATEST_CCP_SALES.PROJECTION_DETAILS_SID AND NMDP.PERIOD_SID = NMD.PERIOD_SID GROUP BY GROSS_PROJECTED.PROJECTION_DETAILS_SID, P.YEAR) AS SOURCE ON ( TARGET.PROJECTION_DETAILS_SID = SOURCE.PROJECTION_DETAILS_SID AND TARGET.PROJECTION_PERIOD = SOURCE.YEAR AND TARGET.USER_ID=SOURCE.USER_ID AND TARGET.SESSION_ID= SOURCE.SESSION_ID AND TARGET.PARENT=1) WHEN MATCHED THEN UPDATE SET TARGET.TOTAL_DISCOUNT_PERCENT_PRIOR = SOURCE.TOTAL_DISCOUNT_PRIOR, TARGET.TOTAL_DISCOUNT_PERCENT_PROJECTED = SOURCE.TOTAL_DISCOUNT_PROJECTED, TARGET.TOTAL_DISCOUNT_PERCENT_CHANGE = SOURCE.TOTAL_DISCOUNT_CHANGE;
MERGE ST_NM_ASSUMPTIONS AS TARGET USING ( SELECT distinct PD.PROJECTION_DETAILS_SID, HLV.LEVEL_VALUES AS RELATIONSHIP_LEVEL_VALUES FROM PROJECTION_DETAILS PD, HIERARCHY_LEVEL_VALUES HLV, (SELECT RLD1.RELATIONSHIP_LEVEL_VALUES, RLD1.RELATIONSHIP_LEVEL_SID, CCPMAP.CCP_DETAILS_SID, RLD1.HIERARCHY_NO FROM RELATIONSHIP_LEVEL_DEFINITION RLD JOIN PROJECTION_CUST_HIERARCHY PCH ON PCH.PROJECTION_MASTER_SID = ? AND RLD.RELATIONSHIP_LEVEL_SID = PCH.RELATIONSHIP_LEVEL_SID AND RLD.RELATIONSHIP_BUILDER_SID = ? JOIN RELATIONSHIP_LEVEL_DEFINITION RLD1 ON RLD.RELATIONSHIP_BUILDER_SID = RLD1.RELATIONSHIP_BUILDER_SID AND RLD.HIERARCHY_NO LIKE RLD1.HIERARCHY_NO + '%' JOIN CCP_MAP CCPMAP ON CCPMAP.RELATIONSHIP_LEVEL_SID = RLD.RELATIONSHIP_LEVEL_SID JOIN PROJECTION_DETAILS PD ON PD.CCP_DETAILS_SID=CCPMAP.CCP_DETAILS_SID AND PD.PROJECTION_MASTER_SID=? WHERE RLD1.LEVEL_NAME='Segment') CCP1 WHERE PD.PROJECTION_MASTER_SID=? AND CCP1.CCP_DETAILS_SID=PD.CCP_DETAILS_SID AND HLV.HIERARCHY_LEVEL_VALUES_SID=CCP1.RELATIONSHIP_LEVEL_VALUES ) AS SOURCE ON (TARGET.PROJECTION_DETAILS_SID=SOURCE.PROJECTION_DETAILS_SID AND TARGET.USER_ID=? AND TARGET.SESSION_ID=? AND TARGET.PARENT=1) WHEN MATCHED THEN UPDATE SET TARGET.SEGMENT=SOURCE.RELATIONSHIP_LEVEL_VALUES;
