entity.query:

            
           @DEDTABLE
       UPDATE MAS SET CHECK_RECORD = @CHECK_RECORD From ST_NM_DISCOUNT_PROJ_MASTER MAS 
   JOIN ST_CCP_HIERARCHY CCPH ON CCPH.CCP_DETAILS_SID = MAS.CCP_DETAILS_SID
   @DEDJOIN
   WHERE @HIERARCHY_COLUMN LIKE '@HIERARCHY_NO%'

IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO CREATE TABLE #SELECTED_HIERARCHY_NO (HIERARCHY_NO varchar(50),INSTR INT) INSERT INTO #SELECTED_HIERARCHY_NO (HIERARCHY_NO,INSTR) SELECT HIERARCHY_NO,INSTR FROM (VALUES @HIERARCHY_NO )A(HIERARCHY_NO,INSTR)
@REBATE
SELECT COUNT(DISTINCT SHN.HIERARCHY_NO) from ST_NM_DISCOUNT_PROJ_MASTER MAS JOIN ST_CCP_HIERARCHY CCPH ON MAS.CCP_DETAILS_SID = CCPH.CCP_DETAILS_SID JOIN #SELECTED_HIERARCHY_NO SHN ON CCPH.@COLUMN_NAME LIKE SHN.HIERARCHY_NO+'%' @FOR_CUSTOM JOIN #SELECTED_REBATE SR ON SR.PRICE_GROUP_TYPE = MAS.@RS_COLUMN

@PERIOD_TABLE @TEMP_TABLE_DISCOUNT IF OBJECT_ID('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO
CREATE TABLE #SELECTED_HIERARCHY_NO ( HIERARCHY_NO_1 VARCHAR(1000), CCP_DETAILS_SID INT, HIERARCHY_NO_2 VARCHAR(8000), RS_CONTRACT_SID INT )
INSERT INTO #SELECTED_HIERARCHY_NO ( HIERARCHY_NO_1, CCP_DETAILS_SID, HIERARCHY_NO_2, RS_CONTRACT_SID ) SELECT DISTINCT CUST_HIERARCHY_NO, CCPH.CCP_DETAILS_SID, PROD_HIERARCHY_NO, MAS.RS_CONTRACT_SID FROM ST_CCP_HIERARCHY CCPH INNER JOIN ST_NM_DISCOUNT_PROJ_MASTER MAS ON CCPH.CCP_DETAILS_SID = MAS.CCP_DETAILS_SID @COLUMN_NAME @CUSTOM_VIEW;
WITH CTE AS ( SELECT COUNT(SR.RS_CONTRACT_SID) OVER ( PARTITION BY HIERARCHY_NO_1, HIERARCHY_NO_2 ) AMOUNT_COUNT, SR.*, COUNT(SR.RS_CONTRACT_SID) OVER ( PARTITION BY HIERARCHY_NO_1, SR.CCP_DETAILS_SID, HIERARCHY_NO_2 ) RATE_RPU_COUNT FROM #SELECTED_HIERARCHY_NO SR ) UPDATE DPT SET @SETTER / ( CASE WHEN @REFRESHED_NAME = 'AMOUNT' THEN AMOUNT_COUNT * @PERIOD_COUNT ELSE RATE_RPU_COUNT END ), DPT.REFRESHED_NAME = @REFRESHED_NAME FROM ST_NM_DISCOUNT_PROJECTION DPT INNER JOIN CTE M ON M.CCP_DETAILS_SID = DPT.CCP_DETAILS_SID AND DPT.RS_CONTRACT_SID = M.RS_CONTRACT_SID INNER JOIN #PERIOD I ON I.PERIOD_SID = DPT.PERIOD_SID WHERE I.YEAR = ?Y

IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO
CREATE TABLE #SELECTED_HIERARCHY_NO ( HIERARCHY_NO VARCHAR(50), SEC_HIERARCHYNO VARCHAR(50), INSTTR INT , HIERARCHY_INDICATOR VARCHAR(50), CCP_DETAILS_SID INT, RS_CONTRACT_SID INT, rebates varchar(100) ) INSERT INTO #SELECTED_HIERARCHY_NO (HIERARCHY_NO, SEC_HIERARCHYNO, INSTTR, HIERARCHY_INDICATOR, CCP_DETAILS_SID,RS_CONTRACT_SID, REBATES) SELECT DISTINCT A.HIERARCHY_NO, SEC_HIERARCHYNO, INSTTR, HIERARCHY_INDICATOR, CH.CCP_DETAILS_SID, SPM.RS_CONTRACT_SID, SR.DED_HIE_NO FROM (VALUES [?Hierarchy-Combination]
)A(HIERARCHY_NO,SEC_HIERARCHYNO,HIERARCHY_INDICATOR,INSTTR) JOIN ST_CCP_HIERARCHY CH ON ((CH.CUST_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%' OR CH.PROD_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%') AND (HIERARCHY_NO IS NULL OR SEC_HIERARCHYNO IS NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%' OR CH.PROD_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%') AND (HIERARCHY_NO IS NULL OR SEC_HIERARCHYNO IS NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%' AND CH.PROD_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%') AND (HIERARCHY_NO IS NOT NULL AND SEC_HIERARCHYNO IS NOT NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%' AND CH.PROD_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%') AND (HIERARCHY_NO IS NOT NULL AND SEC_HIERARCHYNO IS NOT NULL)) JOIN ST_NM_DISCOUNT_PROJ_MASTER SPM ON SPM.CCP_DETAILS_SID = CH.CCP_DETAILS_SID JOIN #SELECTED_REBATE SR ON SPM.DEDUCTION_HIERARCHY_NO like SR.DED_HIE_NO+'%' SET @FREQUENCY_COUNT= (SELECT CASE WHEN @FREQUENCY = 'M' THEN 1 WHEN @FREQUENCY = 'Q' THEN 3 WHEN @FREQUENCY = 'S' THEN 6 ELSE 12 END);
WITH CTE AS (SELECT HIERARCHY_NO, CCP_DETAILS_SID, RS_CONTRACT_SID, Count(CCP_DETAILS_SID) OVER( PARTITION BY HIERARCHY_NO, COALESCE(SEC_HIERARCHYNO,HIERARCHY_NO),rebates ) CCP_COUNT FROM #SELECTED_HIERARCHY_NO H WHERE EXISTS(SELECT 1 FROM ST_NM_DISCOUNT_PROJ_MASTER MAS WHERE MAS.CCP_DETAILS_SID = H.CCP_DETAILS_SID AND MAS.RS_CONTRACT_SID = H.RS_CONTRACT_SID AND MAS.CHECK_RECORD = 1)), SELECTED_HIERARCHY_NO AS (SELECT *, DISCOUNT_AMOUNT = COALESCE(@DISCOUNT_AMOUNT / ( NULLIF(CCP_COUNT, 0) * @FREQUENCY_COUNT ), 0) FROM CTE) UPDATE DP SET DP.PROJECTION_SALES = M.DISCOUNT_AMOUNT FROM SELECTED_HIERARCHY_NO M JOIN ST_NM_DISCOUNT_PROJECTION DP ON DP.CCP_DETAILS_SID = M.CCP_DETAILS_SID AND DP.RS_CONTRACT_SID = M.RS_CONTRACT_SID JOIN "PERIOD" P ON DP.PERIOD_SID = P.PERIOD_SID WHERE P.PERIOD_DATE BETWEEN Datefromparts(@START_YEAR, @START_MONTH, 1) AND Datefromparts (@END_YEAR, @END_MONTH, 1)
