entity.query:
MERGE NM_DISCOUNT_PROJ_MASTER AS TARGET USING ( SELECT PD.PROJECTION_DETAILS_SID, RS_MODEL_SID, RS_CONTRACT_SID, METHODOLOGY, PRICE_GROUP_TYPE, NET_FLAG, USER_GROUP, CHECK_RECORD, CALCULATION_PERIODS
FROM ST_NM_DISCOUNT_PROJ_MASTER MAS JOIN PROJECTION_DETAILS PD ON MAS.CCP_DETAILS_SID = PD.CCP_DETAILS_SID WHERE PD.PROJECTION_MASTER_SID = @PROJECTION_MASTER_SID
) AS SOURCE ON (TARGET.PROJECTION_DETAILS_SID=SOURCE.PROJECTION_DETAILS_SID AND TARGET.RS_MODEL_SID=SOURCE.RS_MODEL_SID AND TARGET.RS_CONTRACT_SID=SOURCE.RS_CONTRACT_SID ) WHEN MATCHED THEN UPDATE SET TARGET.PROJECTION_DETAILS_SID=SOURCE.PROJECTION_DETAILS_SID, TARGET.RS_MODEL_SID=SOURCE.RS_MODEL_SID, TARGET.RS_CONTRACT_SID=SOURCE.RS_CONTRACT_SID, TARGET.METHODOLOGY=SOURCE.METHODOLOGY, TARGET.PRICE_GROUP_TYPE=SOURCE.PRICE_GROUP_TYPE, TARGET.NET_FLAG=SOURCE.NET_FLAG, TARGET.USER_GROUP=SOURCE.USER_GROUP, TARGET.CHECK_RECORD=SOURCE.CHECK_RECORD,
TARGET.CALCULATION_PERIODS=SOURCE.CALCULATION_PERIODS
WHEN NOT MATCHED BY TARGET THEN INSERT(PROJECTION_DETAILS_SID, RS_MODEL_SID, RS_CONTRACT_SID, METHODOLOGY, PRICE_GROUP_TYPE, NET_FLAG, USER_GROUP, CHECK_RECORD,
CALCULATION_PERIODS
) VALUES(SOURCE.PROJECTION_DETAILS_SID, SOURCE.RS_MODEL_SID, SOURCE.RS_CONTRACT_SID, SOURCE.METHODOLOGY, SOURCE.PRICE_GROUP_TYPE, SOURCE.NET_FLAG, SOURCE.USER_GROUP, SOURCE.CHECK_RECORD,
SOURCE.CALCULATION_PERIODS
);

DECLARE @PROJECTION_STATUS VARCHAR(100) = '[$WORKFLOWSTATUS]' IF EXISTS ( SELECT 1 FROM HELPER_TABLE WHERE ( LIST_NAME = 'WorkFlowStatus'
AND @PROJECTION_STATUS = DESCRIPTION) or @PROJECTION_STATUS='View' ) BEGIN; WITH CTE AS ( SELECT DISTINCT FILE_MANAGEMENT_SID = FT.FILE_MANAGEMENT_SID, FORECAST_NAME = FT.FORECAST_NAME, FORECAST_VERSION = FT.[VERSION], HT.DESCRIPTION FILE_TYPE, CMP.COMPANY_NAME COMPANY, BU.COMPANY_NAME BUSINESS_UNIT, ROW_NUMBER() OVER( PARTITION BY FT.FILE_TYPE ORDER BY FT.FROM_PERIOD DESC ) RN, FT.CREATED_DATE ACTIVE_FROM, FT.FROM_PERIOD, FT.TO_PERIOD FROM FILE_MANAGEMENT FT INNER JOIN HELPER_TABLE HT ON HT.HELPER_TABLE_SID = FT.FILE_TYPE INNER JOIN PROJECTION_MASTER PM ON PM.BUSINESS_UNIT = FT.BUSINESS_UNIT AND PM.COMPANY_MASTER_SID = FT.COMPANY JOIN COMPANY_MASTER CMP ON CMP.COMPANY_MASTER_SID = FT.COMPANY JOIN COMPANY_MASTER BU ON BU.COMPANY_MASTER_SID = FT.BUSINESS_UNIT WHERE PM.PROJECTION_MASTER_SID = [@PROJECTION_MASTERID] AND ( CONVERT( DATE, FT.FROM_PERIOD ) <= CONVERT( DATE, Getdate() ) AND FT.FROM_PERIOD IS NOT NULL ) AND ( CONVERT( DATE, FT.TO_PERIOD ) >= CONVERT( DATE, Getdate() ) OR FT.TO_PERIOD IS NULL ) AND HT.LIST_NAME = 'FILE_TYPE' ), CTE2 AS ( SELECT PF.FILE_MANAGEMENT_SID, FT.FORECAST_NAME, CMP.COMPANY_NAME COMPANY, BU.COMPANY_NAME BUSINESS_UNIT, HT.DESCRIPTION FILE_TYPE, FT.VERSION AS FORECAST_VERSION, FT.CREATED_DATE ACTIVE_FROM, FT.FROM_PERIOD, FT.TO_PERIOD FROM (SELECT DISTINCT PROJECTION_MASTER_SID,FILE_MANAGEMENT_SID,FILE_TYPE FROM PROJECTION_FILE_DETAILS) PF JOIN FILE_MANAGEMENT FT ON FT.FILE_MANAGEMENT_SID = PF.FILE_MANAGEMENT_SID INNER JOIN HELPER_TABLE HT ON HT.HELPER_TABLE_SID = FT.FILE_TYPE INNER JOIN PROJECTION_MASTER PM ON PM.BUSINESS_UNIT = FT.BUSINESS_UNIT AND PM.COMPANY_MASTER_SID = FT.COMPANY AND PF.PROJECTION_MASTER_SID = PM.PROJECTION_MASTER_SID JOIN COMPANY_MASTER CMP ON CMP.COMPANY_MASTER_SID = FT.COMPANY JOIN COMPANY_MASTER BU ON BU.COMPANY_MASTER_SID = FT.BUSINESS_UNIT WHERE PM.PROJECTION_MASTER_SID = [@PROJECTION_MASTERID] ) SELECT FILE_MANAGEMENT_SID, FORECAST_NAME, COMPANY, BUSINESS_UNIT, FILE_TYPE, FORECAST_VERSION, ACTIVE_FROM, FROM_PERIOD, TO_PERIOD FROM ( SELECT FILE_MANAGEMENT_SID, FORECAST_NAME, COMPANY, BUSINESS_UNIT, FILE_TYPE, FORECAST_VERSION, ACTIVE_FROM, FROM_PERIOD, TO_PERIOD FROM CTE C WHERE RN = 1 AND NOT EXISTS ( SELECT * FROM CTE2 C2 WHERE C2.BUSINESS_UNIT = C.BUSINESS_UNIT AND C2.COMPANY = C.COMPANY AND C2.FILE_TYPE = C.FILE_TYPE ) UNION ALL SELECT FILE_MANAGEMENT_SID, FORECAST_NAME, COMPANY, BUSINESS_UNIT, FILE_TYPE, FORECAST_VERSION, ACTIVE_FROM, FROM_PERIOD, TO_PERIOD FROM CTE2 C WHERE EXISTS ( SELECT * FROM CTE C2 WHERE C2.BUSINESS_UNIT = C.BUSINESS_UNIT AND C2.COMPANY = C.COMPANY AND C2.FILE_TYPE = C.FILE_TYPE AND C2.RN = 1 ) ) A [$RECORD_FILTER] [$ORDER_BY] OFFSET [$START] ROWS FETCH NEXT [$END] ROWS ONLY;
END ELSE BEGIN; WITH CTE AS ( SELECT DISTINCT FILE_MANAGEMENT_SID = FT.FILE_MANAGEMENT_SID, FORECAST_NAME = FT.FORECAST_NAME, FORECAST_VERSION = FT.[VERSION], HT.DESCRIPTION FILE_TYPE, CMP.COMPANY_NAME COMPANY, BU.COMPANY_NAME BUSINESS_UNIT, ROW_NUMBER() OVER( PARTITION BY FT.FILE_TYPE ORDER BY FT.FROM_PERIOD DESC ) RN, FT.CREATED_DATE ACTIVE_FROM, FT.FROM_PERIOD, FT.TO_PERIOD FROM FILE_MANAGEMENT FT INNER JOIN HELPER_TABLE HT ON HT.HELPER_TABLE_SID = FT.FILE_TYPE INNER JOIN PROJECTION_MASTER PM ON PM.BUSINESS_UNIT = FT.BUSINESS_UNIT AND PM.COMPANY_MASTER_SID = FT.COMPANY JOIN COMPANY_MASTER CMP ON CMP.COMPANY_MASTER_SID = FT.COMPANY JOIN COMPANY_MASTER BU ON BU.COMPANY_MASTER_SID = FT.BUSINESS_UNIT WHERE PM.PROJECTION_MASTER_SID = [@PROJECTION_MASTERID] AND ( CONVERT( DATE, FT.FROM_PERIOD ) <= CONVERT( DATE, Getdate() ) AND FT.FROM_PERIOD IS NOT NULL ) AND ( CONVERT( DATE, FT.TO_PERIOD ) >= CONVERT( DATE, Getdate() ) OR FT.TO_PERIOD IS NULL ) AND HT.LIST_NAME = 'FILE_TYPE' ) SELECT FILE_MANAGEMENT_SID, FORECAST_NAME, COMPANY, BUSINESS_UNIT, FILE_TYPE, FORECAST_VERSION, ACTIVE_FROM, FROM_PERIOD, TO_PERIOD FROM CTE [$RECORD_FILTER] [$WHERECOND] RN = 1 [$ORDER_BY] OFFSET [$START] ROWS FETCH NEXT [$END] ROWS ONLY ;
END
