entity.query:
DECLARE @QUARTER INT=?, @YEAR INT=?, @SESSION_ID INT=?, @USER_ID INT=?,
@UPDATE_VALUE FLOAT=?
UPDATE a SET a.PROJECTION_SALES = ( ( b.MONTHLY_WAC * 100 ) / b.QUARTERLY_WAC ) * ( @UPDATE_VALUE / 100 ) FROM ST_NM_SALES_PROJECTION a JOIN (SELECT DISTINCT SLP.PROJECTION_DETAILS_SID, Sum(SLP.PROJECTION_SALES) OVER ( PARTITION BY SLP.PROJECTION_DETAILS_SID, P.YEAR, P.QUARTER) AS PROJECTION_SALES_QTR_WISE, Sum(FM.DOLLARS) OVER ( PARTITION BY P.YEAR, P.QUARTER) AS QUARTERLY_WAC, Sum(FM.DOLLARS) OVER ( PARTITION BY SLP.PROJECTION_DETAILS_SID, P.YEAR, P.QUARTER, P.MONTH) AS MONTHLY_WAC, p.year, p.quarter, p.MONTH, p.PERIOD_SID FROM ST_NM_SALES_PROJECTION SLP JOIN PROJECTION_DETAILS PD ON SLP.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID JOIN CCP_DETAILS CCP ON CCP.CCP_DETAILS_SID = PD.CCP_DETAILS_SID JOIN PERIOD P ON SLP.PERIOD_SID = P.PERIOD_SID CROSS APPLY (SELECT DOLLARS, P.PERIOD_SID FROM FORECASTING_MASTER FM JOIN(SELECT FORECAST_NAME, [VERSION] FROM FILE_MANAGEMENT WHERE FROM_PERIOD <= Getdate() AND TO_PERIOD IS NULL)FT ON FM.FORECAST_NAME = FT.FORECAST_NAME JOIN PERIOD P ON P.YEAR = FM.FORECAST_YEAR AND P.MONTH = FORECAST_MONTH AND P.PERIOD_SID = SLP.PERIOD_SID JOIN ITEM_MASTER IM ON FM.NDC = IM.ITEM_ID AND IM.ITEM_MASTER_SID = CCP.ITEM_MASTER_SID WHERE FM.FORECAST_VER IN ( FT.VERSION, Floor(FT.VERSION) ))FM WHERE SLP.PROJECTION_DETAILS_SID IN ( ? ) AND USER_ID = @USER_ID AND SESSION_ID = @SESSION_ID AND P.QUARTER = @QUARTER AND P.YEAR = @YEAR)b ON a.PROJECTION_DETAILS_SID = b.PROJECTION_DETAILS_SID AND a.PERIOD_SID = b.PERIOD_SID AND a.USER_ID = @USER_ID AND a.SESSION_ID = @SESSION_ID ;
