entity.query:
IF Object_id('tempdb..#ITEM') IS NOT NULL DROP TABLE #ITEM
CREATE TABLE #ITEM ( ITEM_MASTER_SID INT, HIERARCHY_NO VARCHAR(50) );
INSERT INTO #ITEM (ITEM_MASTER_SID, HIERARCHY_NO) SELECT RELATIONSHIP_LEVEL_VALUES, HIERARCHY_NO FROM RELATIONSHIP_LEVEL_DEFINITION WHERE RELATIONSHIP_BUILDER_SID = 63 AND ( LEVEL_NAME = 'Item' OR LEVEL_NAME = 'Product' OR LEVEL_NAME = 'NDC' ) AND ( HIERARCHY_NO LIKE '63-1.%' )

SELECT CASE WHEN EXISTS (
SELECT 1 FROM ST_CCP_HIERARCHY PD JOIN CCP_DETAILS CCP ON CCP.CCP_DETAILS_SID = PD.CCP_DETAILS_SID LEFT JOIN ACTUALS_DETAILS AM ON AM.CCP_DETAILS_SID = CCP.CCP_DETAILS_SID GROUP BY CCP.COMPANY_MASTER_SID HAVING SUM(AM.SALES) = 0 OR SUM(AM.SALES) IS NULL
) THEN 1 ELSE 0 END

 
             
              	AND EXISTS( SELECT 1 FROM PROJECTION_MASTER PROJECTION_MASTER WHERE PROJECTION_MASTER.PROJECTION_MASTER_SID= ?
AND  (CONTRACT_MASTER.CONTRACT_ELIGIBLE_DATE>= FORECAST_ELIGIBLE_DATE OR CONTRACT_MASTER.CONTRACT_ELIGIBLE_DATE IS NULL)
AND (CFP_CONTRACT_DETAILS.CFP_ELIGIBLE_DATE>= FORECAST_ELIGIBLE_DATE OR CFP_CONTRACT_DETAILS.CFP_ELIGIBLE_DATE IS NULL));
