entity.query:
DECLARE @RELATIONSHIPBUILDERSHIPSID INT =?, @PROJECTIONMASTERSID INT =?, @HIERARCHY_NO VARCHAR(100)= '?%', @LEVEL_NO INT=?, @USER_GROUP VARCHAR(50)='?', @START_DATE VARCHAR(50)='?', @END_DATE VARCHAR(50)='?'
DECLARE @CCP TABLE ( PROJECTION_DETAILS_SID INT )
INSERT INTO @CCP (PROJECTION_DETAILS_SID) SELECT distinct PD.PROJECTION_DETAILS_SID AS PROJECTION_DETAILS_SID FROM RELATIONSHIP_LEVEL_DEFINITION RLD JOIN PROJECTION_CUST_HIERARCHY PCH ON PCH.PROJECTION_MASTER_SID = @PROJECTIONMASTERSID AND RLD.RELATIONSHIP_LEVEL_SID = PCH.RELATIONSHIP_LEVEL_SID AND RLD.RELATIONSHIP_BUILDER_SID = @RELATIONSHIPBUILDERSHIPSID JOIN RELATIONSHIP_LEVEL_DEFINITION RLD1 ON RLD1.HIERARCHY_NO LIKE @HIERARCHY_NO AND RLD.RELATIONSHIP_BUILDER_SID = RLD1.RELATIONSHIP_BUILDER_SID AND RLD.HIERARCHY_NO LIKE RLD1.HIERARCHY_NO + '%' AND RLD1.LEVEL_NO = @LEVEL_NO JOIN CCP_MAP CCPMAP ON CCPMAP.RELATIONSHIP_LEVEL_SID = RLD.RELATIONSHIP_LEVEL_SID JOIN PROJECTION_DETAILS PD ON PD.CCP_DETAILS_SID = CCPMAP.CCP_DETAILS_SID JOIN ST_NM_PPA_PROJECTION_MASTER A ON pd.PROJECTION_DETAILS_SID = A.PROJECTION_DETAILS_SID AND Isnull(A.USER_GROUP, '0') LIKE @USER_GROUP;
SELECT YEAR1 ,YEAR ,SUM(DOLLAR) ACTUAL_DISCOUNT_DOLLAR ,SUM(DOLLAR)/NULLIF(SUM(SALE),0) RATE ,SUM(UNIT) PROJECTION_DISCOUNT_UNITS1 ,SUM(DOLLAR)/NULLIF(SUM(UNIT),0) DISCOUNT ,0 ACTUALS FROM ( SELECT YEAR1,YEAR,SUM(DOLLAR) DOLLAR,MIN(NULLIF(UNIT,0)) UNIT,MIN(NULLIF(SALES,0)) SALE,SUM(UNIT) UNITS,SUM(SALES) SALES,PROJECTION_DETAILS_SID FROM ( SELECT SUM(ACTUAL_DISCOUNT_DOLLAR) DOLLAR ,SUM(SP.ACTUAL_UNITS) UNIT ,SUM(SP.ACTUAL_SALES) SALES ,S.RS_MODEL_SID ,P.YEAR YEAR1 ,P.YEAR ,S.PROJECTION_DETAILS_SID FROM ST_NM_ACTUAL_PPA S LEFT JOIN ST_NM_ACTUAL_SALES SP ON SP.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID AND SP.PERIOD_SID = S.PERIOD_SID JOIN PERIOD P ON P.PERIOD_SID = S.PERIOD_SID JOIN @CCP C ON C.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID GROUP BY S.PROJECTION_DETAILS_SID ,S.RS_MODEL_SID ,P.YEAR ) A GROUP BY PROJECTION_DETAILS_SID,YEAR ,YEAR1) B GROUP BY YEAR ,YEAR1
UNION
SELECT YEAR1 ,YEAR ,SUM(DOLLAR) PROJECTION_DISCOUNT_DOLLAR1 ,SUM(DOLLAR)/NULLIF(SUM(SALES),0) ,SUM(UNIT) PROJECTION_DISCOUNT_UNITS1 ,SUM(DOLLAR)/NULLIF(SUM(UNIT),0) ,1 ACTUALS FROM ( SELECT PROJECTION_DETAILS_SID,MIN(NULLIF(UNIT,0)) UNIT,MIN(NULLIF(SALES,0)) SALES,SUM(UNIT) UNITS ,SUM(DOLLAR) DOLLAR,SUM(SALES) SALE,YEAR1,YEAR FROM ( SELECT SUM(PROJECTION_DISCOUNT_DOLLAR) DOLLAR ,SUM(SP.PROJECTION_UNITS) UNIT ,SUM(SP.PROJECTION_SALES) SALES ,S.RS_MODEL_SID ,P.YEAR YEAR1 ,P.YEAR ,S.PROJECTION_DETAILS_SID FROM ST_NM_PPA_PROJECTION S LEFT JOIN ST_NM_SALES_PROJECTION SP ON SP.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID AND SP.PERIOD_SID = S.PERIOD_SID JOIN PERIOD P ON P.PERIOD_SID = S.PERIOD_SID JOIN @CCP C ON C.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID GROUP BY S.PROJECTION_DETAILS_SID ,S.RS_MODEL_SID ,P.YEAR ) A GROUP BY PROJECTION_DETAILS_SID,YEAR1,YEAR) B GROUP BY YEAR ,YEAR1 ORDER BY YEAR ,YEAR1;

DECLARE @RELATIONSHIPBUILDERSHIPSID INT =?, @PROJECTIONMASTERSID INT =?, @HIERARCHY_NO VARCHAR(100)= '?%', @LEVEL_NO INT=?,
@START_DATE VARCHAR(50)='?', @END_DATE VARCHAR(50)='?'
DECLARE @CCP TABLE ( PROJECTION_DETAILS_SID INT )
INSERT INTO @CCP (PROJECTION_DETAILS_SID) SELECT distinct PD.PROJECTION_DETAILS_SID AS PROJECTION_DETAILS_SID FROM RELATIONSHIP_LEVEL_DEFINITION RLD JOIN PROJECTION_PROD_HIERARCHY PCH ON PCH.PROJECTION_MASTER_SID = @PROJECTIONMASTERSID AND RLD.RELATIONSHIP_LEVEL_SID = PCH.RELATIONSHIP_LEVEL_SID AND RLD.RELATIONSHIP_BUILDER_SID = @RELATIONSHIPBUILDERSHIPSID JOIN RELATIONSHIP_LEVEL_DEFINITION RLD1 ON RLD1.HIERARCHY_NO LIKE @HIERARCHY_NO AND RLD.RELATIONSHIP_BUILDER_SID = RLD1.RELATIONSHIP_BUILDER_SID AND RLD.HIERARCHY_NO LIKE RLD1.HIERARCHY_NO + '%' AND RLD1.LEVEL_NO = @LEVEL_NO JOIN CCP_MAP CCPMAP ON CCPMAP.RELATIONSHIP_LEVEL_SID = RLD.RELATIONSHIP_LEVEL_SID JOIN PROJECTION_DETAILS PD ON PD.CCP_DETAILS_SID = CCPMAP.CCP_DETAILS_SID JOIN ST_NM_PPA_PROJECTION_MASTER A ON pd.PROJECTION_DETAILS_SID = A.PROJECTION_DETAILS_SID;
SELECT MONTH ,YEAR ,SUM(DOLLAR) ACTUAL_DISCOUNT_DOLLAR ,SUM(DOLLAR)/NULLIF(SUM(SALE),0) RATE ,SUM(UNIT) PROJECTION_DISCOUNT_UNITS1 ,SUM(DOLLAR)/NULLIF(SUM(UNIT),0) DISCOUNT ,0 ACTUALS FROM ( SELECT MONTH,YEAR,SUM(DOLLAR) DOLLAR,MIN(NULLIF(UNIT,0)) UNIT,MIN(NULLIF(SALES,0)) SALE,SUM(UNIT) UNITS,SUM(SALES) SALES,PROJECTION_DETAILS_SID FROM ( SELECT SUM(ACTUAL_DISCOUNT_DOLLAR) DOLLAR ,SUM(SP.ACTUAL_UNITS) UNIT ,SUM(SP.ACTUAL_SALES) SALES ,S.RS_MODEL_SID ,P.MONTH ,P.YEAR ,S.PROJECTION_DETAILS_SID FROM ST_NM_ACTUAL_PPA S LEFT JOIN ST_NM_ACTUAL_SALES SP ON SP.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID AND SP.PERIOD_SID = S.PERIOD_SID JOIN PERIOD P ON P.PERIOD_SID = S.PERIOD_SID JOIN @CCP C ON C.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID GROUP BY S.PROJECTION_DETAILS_SID ,S.RS_MODEL_SID ,P.MONTH ,P.YEAR ) A GROUP BY PROJECTION_DETAILS_SID,YEAR ,MONTH) B GROUP BY YEAR ,MONTH
UNION
SELECT MONTH ,YEAR ,SUM(DOLLAR) PROJECTION_DISCOUNT_DOLLAR1 ,SUM(DOLLAR)/NULLIF(SUM(SALES),0) ,SUM(UNIT) PROJECTION_DISCOUNT_UNITS1 ,SUM(DOLLAR)/NULLIF(SUM(UNIT),0) ,1 ACTUALS FROM ( SELECT PROJECTION_DETAILS_SID,MIN(NULLIF(UNIT,0)) UNIT,MIN(NULLIF(SALES,0)) SALES,SUM(UNIT) UNITS ,SUM(DOLLAR) DOLLAR,SUM(SALES) SALE,MONTH,YEAR FROM ( SELECT SUM(PROJECTION_DISCOUNT_DOLLAR) DOLLAR ,SUM(SP.PROJECTION_UNITS) UNIT ,SUM(SP.PROJECTION_SALES) SALES ,S.RS_MODEL_SID ,P.MONTH ,P.YEAR ,S.PROJECTION_DETAILS_SID FROM ST_NM_PPA_PROJECTION S LEFT JOIN ST_NM_SALES_PROJECTION SP ON SP.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID AND SP.PERIOD_SID = S.PERIOD_SID JOIN PERIOD P ON P.PERIOD_SID = S.PERIOD_SID JOIN @CCP C ON C.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID GROUP BY S.PROJECTION_DETAILS_SID ,S.RS_MODEL_SID ,P.MONTH ,P.YEAR ) A GROUP BY PROJECTION_DETAILS_SID,MONTH,YEAR) B GROUP BY YEAR ,MONTH ORDER BY YEAR ,MONTH;

DECLARE @RELATIONSHIPBUILDERSHIPSID INT =?, @PROJECTIONMASTERSID INT =?, @HIERARCHY_NO VARCHAR(100)= '?%', @LEVEL_NO INT=?, @START_DATE VARCHAR(50)='?', @END_DATE VARCHAR(50)='?'
DECLARE @CCP TABLE ( PROJECTION_DETAILS_SID INT
)
INSERT INTO @CCP (PROJECTION_DETAILS_SID ) SELECT distinct PD.PROJECTION_DETAILS_SID AS PROJECTION_DETAILS_SID
FROM RELATIONSHIP_LEVEL_DEFINITION RLD JOIN PROJECTION_PROD_HIERARCHY PCH ON PCH.PROJECTION_MASTER_SID = @PROJECTIONMASTERSID AND RLD.RELATIONSHIP_LEVEL_SID = PCH.RELATIONSHIP_LEVEL_SID AND RLD.RELATIONSHIP_BUILDER_SID = @RELATIONSHIPBUILDERSHIPSID JOIN RELATIONSHIP_LEVEL_DEFINITION RLD1 ON RLD1.HIERARCHY_NO LIKE @HIERARCHY_NO AND RLD.RELATIONSHIP_BUILDER_SID = RLD1.RELATIONSHIP_BUILDER_SID AND RLD.HIERARCHY_NO LIKE RLD1.HIERARCHY_NO + '%' AND RLD1.LEVEL_NO = @LEVEL_NO JOIN CCP_MAP CCPMAP ON CCPMAP.RELATIONSHIP_LEVEL_SID = RLD.RELATIONSHIP_LEVEL_SID JOIN PROJECTION_DETAILS PD ON PD.CCP_DETAILS_SID = CCPMAP.CCP_DETAILS_SID JOIN NM_PPA_PROJECTION_MASTER A ON pd.PROJECTION_DETAILS_SID = A.PROJECTION_DETAILS_SID AND EXISTS ( SELECT 1 FROM PROJECTION_DETAILS PR WHERE PR.PROJECTION_MASTER_SID = @PROJECTIONMASTERSID AND PR.PROJECTION_DETAILS_SID = PD.PROJECTION_DETAILS_SID );
SELECT MONTH ,YEAR ,SUM(DOLLAR) ACTUAL_DISCOUNT_DOLLAR ,SUM(DOLLAR)/NULLIF(SUM(SALE),0) RATE ,SUM(UNIT) PROJECTION_DISCOUNT_UNITS1 ,SUM(DOLLAR)/NULLIF(SUM(UNIT),0) DISCOUNT ,0 ACTUALS FROM ( SELECT MONTH,YEAR,SUM(DOLLAR) DOLLAR,MIN(NULLIF(UNIT,0)) UNIT,MIN(NULLIF(SALES,0)) SALE,SUM(UNIT) UNITS,SUM(SALES) SALES,PROJECTION_DETAILS_SID FROM ( SELECT SUM(ACTUAL_DISCOUNT_DOLLAR) DOLLAR ,SUM(SP.ACTUAL_UNITS) UNIT ,SUM(SP.ACTUAL_SALES) SALES ,S.RS_MODEL_SID ,P.MONTH ,P.YEAR ,S.PROJECTION_DETAILS_SID FROM NM_ACTUAL_PPA S LEFT JOIN NM_ACTUAL_SALES SP ON SP.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID AND SP.PERIOD_SID = S.PERIOD_SID JOIN PERIOD P ON P.PERIOD_SID = S.PERIOD_SID JOIN @CCP C ON C.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID GROUP BY S.PROJECTION_DETAILS_SID ,S.RS_MODEL_SID ,P.MONTH ,P.YEAR ) A GROUP BY PROJECTION_DETAILS_SID,YEAR ,MONTH) B GROUP BY YEAR ,MONTH
UNION
SELECT MONTH ,YEAR ,SUM(DOLLAR) PROJECTION_DISCOUNT_DOLLAR1 ,SUM(DOLLAR)/NULLIF(SUM(SALES),0) ,SUM(UNIT) PROJECTION_DISCOUNT_UNITS1 ,SUM(DOLLAR)/NULLIF(SUM(UNIT),0) ,1 ACTUALS FROM ( SELECT PROJECTION_DETAILS_SID,MIN(NULLIF(UNIT,0)) UNIT,MIN(NULLIF(SALES,0)) SALES,SUM(UNIT) UNITS ,SUM(DOLLAR) DOLLAR,SUM(SALES) SALE,MONTH,YEAR FROM ( SELECT SUM(PROJECTION_DISCOUNT_DOLLAR) DOLLAR ,SUM(SP.PROJECTION_UNITS) UNIT ,SUM(SP.PROJECTION_SALES) SALES ,S.RS_MODEL_SID ,P.MONTH ,P.YEAR ,S.PROJECTION_DETAILS_SID FROM NM_PPA_PROJECTION S LEFT JOIN NM_SALES_PROJECTION SP ON SP.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID AND SP.PERIOD_SID = S.PERIOD_SID JOIN PERIOD P ON P.PERIOD_SID = S.PERIOD_SID JOIN @CCP C ON C.PROJECTION_DETAILS_SID = S.PROJECTION_DETAILS_SID GROUP BY S.PROJECTION_DETAILS_SID ,S.RS_MODEL_SID ,P.MONTH ,P.YEAR ) A GROUP BY PROJECTION_DETAILS_SID,MONTH,YEAR) B GROUP BY YEAR ,MONTH ORDER BY YEAR ,MONTH;
