SE 1 END NEP_FORMULA_NO_FLAG ,CASE WHEN COUNT(DISTINCT NSPP.NET_SALES_FORMULA_NO) IN ( 1 ,0 ) THEN 0 ELSE 1 END NET_SUBSEQUENT_PERIOD_PRICE_FORMULA_NO_FLAG ,CASE WHEN COUNT(DISTINCT NRT.NET_SALES_FORMULA_NO) IN ( 1 ,0 ) THEN 0 ELSE 1 END NET_PRICE_TYPE_FORMULA_NO_FLAG ,CASE WHEN COUNT(DISTINCT NRP.NET_SALES_FORMULA_NO) IN ( 1 ,0 ) THEN 0 ELSE 1 END NET_RESET_PRICE_FORMULA_NO_FLAG FROM RELATIONSHIP_LEVEL_DEFINITION RLD JOIN PROJECTION_CUST_HIERARCHY PCH ON PCH.PROJECTION_MASTER_SID = @PROJECTIONMASTERSID AND RLD.RELATIONSHIP_LEVEL_SID = PCH.RELATIONSHIP_LEVEL_SID AND RLD.RELATIONSHIP_BUILDER_SID = @RELATIONSHIPBUILDERSHIPSID JOIN RELATIONSHIP_LEVEL_DEFINITION RLD1 ON RLD1.HIERARCHY_NO LIKE @HIERARCHY_NO AND RLD.RELATIONSHIP_BUILDER_SID = RLD1.RELATIONSHIP_BUILDER_SID AND RLD.HIERARCHY_NO LIKE RLD1.HIERARCHY_NO + '%' AND RLD1.LEVEL_NO = @LEVEL_NO AND RLD1.HIERARCHY_NO IN (?) JOIN CCP_MAP CCPMAP ON CCPMAP.RELATIONSHIP_LEVEL_SID = RLD.RELATIONSHIP_LEVEL_SID JOIN PROJECTION_DETAILS PD ON PD.CCP_DETAILS_SID = CCPMAP.CCP_DETAILS_SID JOIN ST_NM_PPA_PROJECTION_MASTER A ON pd.PROJECTION_DETAILS_SID = A.PROJECTION_DETAILS_SID JOIN ST_NM_PPA_PROJECTION PA ON pd.PROJECTION_DETAILS_SID = PA.PROJECTION_DETAILS_SID JOIN PERIOD p ON p.period_sid = PA.PERIOD_SID AND A.SESSION_ID = PA.SESSION_ID AND A.USER_ID = PA.USER_ID AND A.session_id = @SESSION_ID AND A.user_id = @USER_ID AND Isnull(A.USER_GROUP, '0') LIKE @USER_GROUP LEFT JOIN NET_SALES_FORMULA_MASTER NBP ON NBP.NET_SALES_FORMULA_MASTER_SID = PA.NET_BASE_PRICE_FORMULA LEFT JOIN NET_SALES_FORMULA_MASTER NEP ON NEP.NET_SALES_FORMULA_MASTER_SID = PA.NEP_FORMULA LEFT JOIN NET_SALES_FORMULA_MASTER NSPP ON NSPP.NET_SALES_FORMULA_MASTER_SID = PA.NET_SUBSEQUENT_PERIOD_PRICE_FORMULA LEFT JOIN NET_SALES_FORMULA_MASTER NRT ON NRT.NET_SALES_FORMULA_MASTER_SID = PA.NET_PRICE_TYPE_FORMULA LEFT JOIN NET_SALES_FORMULA_MASTER NRP ON NRP.NET_SALES_FORMULA_MASTER_SID = PA.NET_RESET_PRICE_FORMULA LEFT JOIN ITEM_PRICING_QUALIFIER IPQ ON IPQ.ITEM_PRICING_QUALIFIER_SID = PA.ITEM_PRICING_QUALIFIER_SID GROUP BY RLD1.RELATIONSHIP_LEVEL_VALUES ,p.QUARTER ,RLD1.LEVEL_NO ,p."YEAR" ,RLD1.HIERARCHY_NO ,RLD1.LEVEL_NAME
) SELECT Distinct level_no ,LEVEL_NAME ,rlv ,rc.QUARTER ,rc.YEAR ,USER_GROUP ,PRICE_PROTECTION_STATUS ,PRICE_PROTECTION_START_DATE ,PRICE_PROTECTION_END_DATE ,HIERARCHY_NO ,CHECK_RECORD ,COUNT1 ,PRICE_PROTECTION_PRICE_TYPE ,NEP ,NEP_FORMULA ,BASE_PRICE_TYPE ,BASE_PRICE_MANUAL ,BASE_PRICE_DATE ,BASE_PRICE_PRICE_TYPE ,NET_BASE_PRICE ,NET_BASE_PRICE_FORMULA ,SUBSEQUENT_PERIOD_PRICE_TYPE ,NET_SUBSEQUENT_PERIOD_PRICE ,NET_SUBSEQUENT_PERIOD_PRICE_FORMULA ,PRICE_TOLERANCE_INTERVAL ,PRICE_TOLERANCE_FREQUENCY ,PRICE_TOLERANCE_TYPE ,CASE WHEN ht.DESCRIPTION IN ( '%' ,'percent' ) THEN RC.PRICE_TOLERANCE WHEN ht.DESCRIPTION = 'dollar' THEN SV.PRICE_TOLERANCE END PRICE_TOLERANCE
,MAX_INCREMENTAL_CHANGE ,RESET_ELIGIBLE ,RESET_TYPE ,RESET_DATE ,RESET_INTERVAL ,RESET_FREQUENCY ,RESET_PRICE_TYPE ,NET_RESET_PRICE_TYPE ,NET_RESET_PRICE_FORMULA ,NET_PRICE_TYPE ,NET_PRICE_TYPE_FORMULA ,ATTACHED_DATE ,ITEM_PRICING_QUALIFIER_SID_flag ,NEP_FORMULA_FLAG ,BASE_PRICE_TYPE_FLAG ,BASE_PRICE_DATE_FLAG ,BASE_PRICE_PRICE_TYPE_FLAG ,NET_BASE_PRICE_FLAG ,NET_BASE_PRICE_FORMULA_FLAG ,SUBSEQUENT_PERIOD_PRICE_TYPE_FLAG ,NET_SUBSEQUENT_PERIOD_PRICE_FLAG ,NET_SUBSEQUENT_PERIOD_PRICE_FORMULA_FLAG ,PRICE_TOLERANCE_INTERVAL_FLAG ,PRICE_TOLERANCE_FREQUENCY_FLAG ,PRICE_TOLERANCE_TYPE_FLAG ,PRICE_TOLERANCE_FLAG = CASE WHEN PTT.TOTAL = 2 THEN 1 ELSE PRICE_TOLERANCE_FLAG END ,RESET_ELIGIBLE_FLAG ,RESET_TYPE_FLAG ,RESET_DATE_FLAG ,RESET_INTERVAL_FLAG ,RESET_FREQUENCY_FLAG ,RESET_PRICE_TYPE_FLAG ,NET_RESET_PRICE_TYPE_FLAG ,NET_RESET_PRICE_FORMULA_FLAG ,NET_PRICE_TYPE_FLAG ,NET_PRICE_TYPE_FORMULA_FLAG ,NET_BASE_PRICE_FORMULA_NO ,NEP_FORMULA_NO ,NET_SUBSEQUENT_PERIOD_PRICE_FORMULA_NO ,NET_PRICE_TYPE_FORMULA_NO ,NET_RESET_PRICE_FORMULA_NO ,NET_BASE_PRICE_FORMULA_NO_FLAG ,NEP_FORMULA_NO_FLAG ,NET_SUBSEQUENT_PERIOD_PRICE_FORMULA_NO_FLAG ,NET_PRICE_TYPE_FORMULA_NO_FLAG ,NET_RESET_PRICE_FORMULA_NO_FLAG
FROM REST_CAL RC JOIN SUM_VALUE SV ON SV.YEAR = RC.YEAR AND SV.QUARTER = RC.QUARTER LEFT JOIN helper_table ht ON ht.helper_table_sid = rc.PRICE_TOLERANCE_TYPE JOIN price_tol_type PTT ON PTT.QUARTER = RC.QUARTER JOIN PERIOD P ON P.QUARTER = RC.QUARTER AND P.YEAR = RC.YEAR WHERE P.PERIOD_SID BETWEEN COALESCE(NULLIF(?, 0), @START_DATE) AND COALESCE(NULLIF(?, 0), @END_DATE)
ORDER BY LEVEL_NO ,rlv ,YEAR ,quarter

DECLARE @USER_GROUP1 VARCHAR(100)= '?', @RELATIONSHIPBUILDERSHIPSID INT =?, @PROJECTIONMASTERSID INT =?, @HIERARCHY_NO VARCHAR(100)= '?%',
@SESSION_ID INT=?, @USER_ID INT=?, @USER_GROUP VARCHAR(50)='?' UPDATE ST_NM_PPA_PROJECTION_MASTER SET user_group = @USER_GROUP1 FROM RELATIONSHIP_LEVEL_DEFINITION RLD JOIN PROJECTION_CUST_HIERARCHY PCH ON PCH.PROJECTION_MASTER_SID = @PROJECTIONMASTERSID AND RLD.RELATIONSHIP_LEVEL_SID = PCH.RELATIONSHIP_LEVEL_SID AND RLD.RELATIONSHIP_BUILDER_SID = @RELATIONSHIPBUILDERSHIPSID JOIN RELATIONSHIP_LEVEL_DEFINITION RLD1 ON RLD1.HIERARCHY_NO LIKE @HIERARCHY_NO AND RLD.RELATIONSHIP_BUILDER_SID = RLD1.RELATIONSHIP_BUILDER_SID AND RLD.HIERARCHY_NO LIKE RLD1.HIERARCHY_NO + '%'
JOIN CCP_MAP CCPMAP ON CCPMAP.RELATIONSHIP_LEVEL_SID = RLD.RELATIONSHIP_LEVEL_SID JOIN PROJECTION_DETAILS PD ON PD.CCP_DETAILS_SID=CCPMAP.CCP_DETAILS_SID JOIN ST_NM_PPA_PROJECTION_MASTER A ON pd.PROJECTION_DETAILS_SID = A.PROJECTION_DETAILS_SID AND A.session_id = @SESSION_ID AND A.user_id = @USER_ID AND A.USER_GROUP LIKE @USER_GROUP;
