entity.query:
MERGE RETURNS_PROJ_MASTER AS TARGET USING ( SELECT RETURNS_DETAILS_SID, METHODOLOGY, METHODOLOGY_START_DATE, METHODOLOGY_END_DATE, CALCULATION_PERIODS, LAG, CLOSED_DATE, LOE_DATE, CHECK_RECORD FROM dbo.ST_RETURNS_PROJ_MASTER
) AS SOURCE ON (TARGET.RETURNS_DETAILS_SID=SOURCE.RETURNS_DETAILS_SID) WHEN MATCHED THEN UPDATE SET TARGET.METHODOLOGY=SOURCE.METHODOLOGY, TARGET.METHODOLOGY_START_DATE=SOURCE.METHODOLOGY_START_DATE ,TARGET.METHODOLOGY_END_DATE=SOURCE.METHODOLOGY_END_DATE, TARGET.CALCULATION_PERIODS=SOURCE.CALCULATION_PERIODS ,TARGET.LAG=SOURCE.LAG, TARGET.CLOSED_DATE=SOURCE.CLOSED_DATE, TARGET.LOE_DATE=SOURCE.LOE_DATE, TARGET.CHECK_RECORD=SOURCE.CHECK_RECORD WHEN NOT MATCHED BY TARGET THEN INSERT VALUES(SOURCE.RETURNS_DETAILS_SID, SOURCE.METHODOLOGY, SOURCE.METHODOLOGY_START_DATE, SOURCE.METHODOLOGY_END_DATE, SOURCE.CALCULATION_PERIODS, SOURCE.LAG, SOURCE.CLOSED_DATE, SOURCE.LOE_DATE, SOURCE.CHECK_RECORD); MERGE RETURNS_ACTUALS AS TARGET USING ( SELECT RETURNS_DETAILS_SID, PERIOD_SID, ACTUAL_RETURN_PERCENT, ACTUAL_RPU, ACTUAL_RETURN_AMOUNT, ORIG_SALE_UNITS, ORIG_SALE_DOLLARS, CUM_RETURN_UNITS, ASP, EXPECTED_RETURN_RATE FROM dbo.ST_RETURNS_ACTUALS
) AS SOURCE ON (TARGET.RETURNS_DETAILS_SID=SOURCE.RETURNS_DETAILS_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID) WHEN MATCHED THEN UPDATE SET TARGET.ACTUAL_RETURN_PERCENT=SOURCE.ACTUAL_RETURN_PERCENT, TARGET.ACTUAL_RPU=SOURCE.ACTUAL_RPU, TARGET.ACTUAL_RETURN_AMOUNT=SOURCE.ACTUAL_RETURN_AMOUNT, TARGET.ORIG_SALE_UNITS = SOURCE.ORIG_SALE_UNITS, TARGET.ORIG_SALE_DOLLARS = SOURCE.ORIG_SALE_DOLLARS, TARGET.CUM_RETURN_UNITS = SOURCE.CUM_RETURN_UNITS, TARGET.ASP = SOURCE.ASP, TARGET.EXPECTED_RETURN_RATE = SOURCE.EXPECTED_RETURN_RATE WHEN NOT MATCHED BY TARGET THEN INSERT VALUES(SOURCE.RETURNS_DETAILS_SID, SOURCE.PERIOD_SID, SOURCE.ACTUAL_RETURN_PERCENT, SOURCE.ACTUAL_RPU, SOURCE.ACTUAL_RETURN_AMOUNT, SOURCE.ORIG_SALE_UNITS, SOURCE.ORIG_SALE_DOLLARS, SOURCE.CUM_RETURN_UNITS, SOURCE.expected_return_rate, SOURCE.ASP); MERGE RETURNS_PROJ_DETAILS AS TARGET USING ( SELECT RETURNS_DETAILS_SID, PERIOD_SID, GROWTH_RATE, PROJECTED_RETURN_PERCENT, PROJECTED_RPU, PROJECTED_RETURN_AMOUNT, PROJECTED_RETURN_UNITS, REFRESHED_NAME, ACTIVE_EXFACTORY_SALES_AMOUNT, ACTIVE_EXFACTORY_SALES_UNITS FROM dbo.ST_RETURNS_PROJ_DETAILS
) AS SOURCE ON (TARGET.RETURNS_DETAILS_SID=SOURCE.RETURNS_DETAILS_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID) WHEN MATCHED THEN UPDATE SET TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.GROWTH_RATE=SOURCE.GROWTH_RATE, TARGET.PROJECTED_RETURN_PERCENT=SOURCE.PROJECTED_RETURN_PERCENT, TARGET.PROJECTED_RPU=SOURCE.PROJECTED_RPU, TARGET.PROJECTED_RETURN_AMOUNT=SOURCE.PROJECTED_RETURN_AMOUNT, TARGET.PROJECTED_RETURN_UNITS=SOURCE.PROJECTED_RETURN_UNITS, TARGET.REFRESHED_NAME = SOURCE.REFRESHED_NAME, TARGET.ACTIVE_EXFACTORY_SALES_AMOUNT = SOURCE.ACTIVE_EXFACTORY_SALES_AMOUNT, TARGET.ACTIVE_EXFACTORY_SALES_UNITS = SOURCE.ACTIVE_EXFACTORY_SALES_UNITS WHEN NOT MATCHED BY TARGET THEN INSERT VALUES(SOURCE.RETURNS_DETAILS_SID, SOURCE.PERIOD_SID, SOURCE.GROWTH_RATE, SOURCE.PROJECTED_RETURN_PERCENT, SOURCE.PROJECTED_RPU, SOURCE.PROJECTED_RETURN_AMOUNT, SOURCE.PROJECTED_RETURN_UNITS, SOURCE.ACTIVE_EXFACTORY_SALES_AMOUNT, SOURCE.ACTIVE_EXFACTORY_SALES_UNITS, SOURCE.REFRESHED_NAME);

 
            
Delete from RETURNS_ACTUALS where RETURNS_DETAILS_SID IN(select RETURNS_DETAILS_SID from RETURNS_DETAILS where PROJECTION_MASTER_SID= @PROJECTION_MASTER_SID); 
Delete from RETURNS_PROJ_MASTER where RETURNS_DETAILS_SID IN(select RETURNS_DETAILS_SID from RETURNS_DETAILS where PROJECTION_MASTER_SID= @PROJECTION_MASTER_SID); 
Delete from RETURNS_PROJ_DETAILS where RETURNS_DETAILS_SID IN(select RETURNS_DETAILS_SID from RETURNS_DETAILS where PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID ); 
Delete from RETURNS_DETAILS where PROJECTION_MASTER_SID= @PROJECTION_MASTER_SID; 
Delete from PROJECTION_PROD_HIERARCHY where PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID ; 
Delete from RETURNS_MAP where PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID ; 
Delete from RETURNS_PROJECTION_SELECTION where PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID; 
Delete from PROJECTION_MASTER where PROJECTION_MASTER_SID= @PROJECTION_MASTER_SID;


            
               DELETE FROM ST_RETURNS_ACTUALS WHERE USER_ID=@USER_ID AND SESSION_ID=@SESSION_ID;
               DELETE FROM ST_RETURNS_PROJ_DETAILS WHERE USER_ID=@USER_ID AND SESSION_ID=@SESSION_ID;
               DELETE FROM ST_RETURNS_PROJ_MASTER WHERE USER_ID=@USER_ID AND SESSION_ID=@SESSION_ID;


       
      DELETE FROM ST_NM_ACTUAL_DISCOUNT WHERE USER_ID=@USER_ID AND SESSION_ID=@SESSION_ID;
   DELETE FROM ST_NM_DISCOUNT_PROJECTION WHERE USER_ID=@USER_ID AND SESSION_ID=@SESSION_ID;
   DELETE FROM ST_NM_DISCOUNT_PROJ_MASTER WHERE USER_ID=@USER_ID AND SESSION_ID=@SESSION_ID;

DECLARE @STRING CHAR(1)='@FREQUENCY'
SELECT Count(*) FROM (SELECT DISTINCT PERIODS=CASE WHEN @STRING = 'A' THEN PERIODS_ANNUAL WHEN @STRING = 'S' THEN PERIODS_SEMI WHEN @STRING = 'M' THEN PERIODS_MONTH WHEN @STRING = 'Q' THEN PERIODS_QUARTER END FROM (SELECT 'Q' + CONVERT(VARCHAR(2), P.QUARTER) + ' ' + CONVERT(VARCHAR(4), P."YEAR") AS PERIODS_QUARTER, 'S' + CONVERT(VARCHAR(2), P.SEMI_ANNUAL) + ' ' + CONVERT(VARCHAR(4), P."YEAR") AS PERIODS_SEMI, CONVERT(VARCHAR(4), P.YEAR) AS PERIODS_ANNUAL, 'M' + CONVERT(VARCHAR(2), P.MONTH) + ' ' + CONVERT(VARCHAR(4), P."YEAR") AS PERIODS_MONTH FROM "PERIOD" P JOIN @ACTUAL_TABLE AC ON AC.PERIOD_SID = P.PERIOD_SID
)A @UNION SELECT DISTINCT PERIODS=CASE WHEN @STRING = 'A' THEN PERIODS_ANNUAL WHEN @STRING = 'S' THEN PERIODS_SEMI WHEN @STRING = 'M' THEN PERIODS_MONTH WHEN @STRING = 'Q' THEN PERIODS_QUARTER END FROM (SELECT 'Q' + CONVERT(VARCHAR(2), P.QUARTER) + ' ' + CONVERT(VARCHAR(4), P."YEAR") AS PERIODS_QUARTER, 'S' + CONVERT(VARCHAR(2), P.SEMI_ANNUAL) + ' ' + CONVERT(VARCHAR(4), P."YEAR") AS PERIODS_SEMI, CONVERT(VARCHAR(4), P.YEAR) AS PERIODS_ANNUAL, 'M' + CONVERT(VARCHAR(2), P.MONTH) + ' ' + CONVERT(VARCHAR(4), P."YEAR") AS PERIODS_MONTH FROM "PERIOD" P JOIN @PROJECTION_TABLE AC ON AC.PERIOD_SID = P.PERIOD_SID
)A)A ;
