entity.query:
MERGE ACCRUAL_SALES_ACTUALS AS TARGET USING ( SELECT PROJECTION_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, EXCLUDED_UNITS, NET_UNITS, PRICE, SALES, EXCLUDED_DOLLARS, DEMAND, ADJUSTED_DEMAND FROM dbo.ST_ACCRUAL_SALES_ACTUALS
) AS SOURCE ON (TARGET.PROJECTION_MASTER_SID=SOURCE.PROJECTION_MASTER_SID AND TARGET.ITEM_MASTER_SID = SOURCE.ITEM_MASTER_SID AND TARGET.PERIOD_SID = SOURCE.PERIOD_SID) WHEN MATCHED THEN UPDATE SET TARGET.ITEM_MASTER_SID=SOURCE.ITEM_MASTER_SID, TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.TOTAL_UNITS=SOURCE.TOTAL_UNITS, TARGET.EXCLUDED_UNITS=SOURCE.EXCLUDED_UNITS, TARGET.NET_UNITS=SOURCE.NET_UNITS, TARGET.PRICE=SOURCE.PRICE, TARGET.SALES=SOURCE.SALES, TARGET.EXCLUDED_DOLLARS = SOURCE.EXCLUDED_DOLLARS, TARGET.DEMAND=SOURCE.DEMAND, TARGET.ADJUSTED_DEMAND=SOURCE.ADJUSTED_DEMAND
WHEN NOT MATCHED BY TARGET THEN INSERT VALUES(SOURCE.PROJECTION_MASTER_SID, SOURCE.ITEM_MASTER_SID, SOURCE.PERIOD_SID, SOURCE.TOTAL_UNITS, SOURCE.EXCLUDED_UNITS, SOURCE.NET_UNITS, SOURCE.PRICE, SOURCE.SALES, SOURCE.EXCLUDED_DOLLARS, SOURCE.DEMAND, SOURCE.ADJUSTED_DEMAND);;

MERGE EXCLUSION_DETAILS AS TARGET USING ( SELECT PROJECTION_MASTER_SID,COMPANY_MASTER_SID FROM dbo.ST_EXCLUSION_DETAILS ) AS SOURCE ON (TARGET.PROJECTION_MASTER_SID=SOURCE.PROJECTION_MASTER_SID AND TARGET.COMPANY_MASTER_SID = SOURCE.COMPANY_MASTER_SID)
WHEN MATCHED
THEN UPDATE SET TARGET.COMPANY_MASTER_SID=SOURCE.COMPANY_MASTER_SID WHEN NOT MATCHED BY TARGET THEN INSERT VALUES(SOURCE.PROJECTION_MASTER_SID, SOURCE.COMPANY_MASTER_SID);;

IF Object_id('tempdb..#COMP_CONT') IS NOT NULL DROP TABLE #COMP_CONT
CREATE TABLE #COMP_CONT ( COMPANY_MASTER_SID INT, CONTRACT_MASTER_SID INT );
WITH contract AS (@CONTRACT), company AS (@CUSTOMER)
INSERT INTO #COMP_CONT (COMPANY_MASTER_SID, CONTRACT_MASTER_SID ) SELECT COM.RELATIONSHIP_LEVEL_VALUES AS COMPANY_MASTER_SID, C.RELATIONSHIP_LEVEL_VALUES AS CONTRACT_MASTER_SID
FROM COMPANY COM JOIN CONTRACT C ON @FILTER + '%' -- LHS Should have bigger level no than RHS IF Object_id('tempdb..#ITEM') IS NOT NULL DROP TABLE #ITEM
CREATE TABLE #ITEM ( ITEM_MASTER_SID INT
);
INSERT INTO #ITEM (ITEM_MASTER_SID) @PRODUCT
INSERT INTO ST_ACCRUAL_PROJ_DETAILS (CCP_DETAILS_SID, RS_MODEL_SID, PROJECTION_MASTER_SID, RS_CONTRACT_SID) SELECT DISTINCT CD.CCP_DETAILS_SID, R1.RS_MODEL_SID, @PROJECTION_MASTER_SID PROJECTION_MASTER_SID, R1.RS_CONTRACT_SID FROM #COMP_CONT CC JOIN CCP_DETAILS CD ON CC.COMPANY_MASTER_SID = CD.COMPANY_MASTER_SID AND CC.CONTRACT_MASTER_SID = CD.CONTRACT_MASTER_SID JOIN #ITEM I ON I.ITEM_MASTER_SID = CD.ITEM_MASTER_SID JOIN RS_CONTRACT R1 ON R1.CONTRACT_MASTER_SID = CD.CONTRACT_MASTER_SID JOIN RS_CONTRACT_DETAILS R2 ON R2.RS_CONTRACT_SID = R1.RS_CONTRACT_SID AND R2.ITEM_MASTER_SID = CD.ITEM_MASTER_SID WHERE R1.INBOUND_STATUS <> 'D' AND R2.INBOUND_STATUS <> 'D' AND EXISTS(SELECT 1 FROM CFP_CONTRACT CFP1 JOIN CFP_CONTRACT_DETAILS CFP2 ON CFP1.CFP_CONTRACT_SID = CFP2.CFP_CONTRACT_SID AND R1.CFP_CONTRACT_SID = CFP1.CFP_CONTRACT_SID WHERE COMPANY_MASTER_SID = CD.COMPANY_MASTER_SID AND CFP1.CONTRACT_MASTER_SID = CD.CONTRACT_MASTER_SID) @SELECTION ;

INSERT INTO ST_ACCRUAL_SALES_ACTUALS(PROJECTION_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, EXCLUDED_UNITS, NET_UNITS, PRICE, SALES, EXCLUDED_DOLLARS, DEMAND, ADJUSTED_DEMAND ) SELECT A.PROJECTION_MASTER_SID, A.ITEM_MASTER_SID, A.PERIOD_SID, A.TOTAL_UNITS, A.EXCLUDED_UNITS, A.NET_UNITS, A.PRICE, A.SALES, A.EXCLUDED_DOLLARS, A.DEMAND, A.ADJUSTED_DEMAND
FROM dbo.ACCRUAL_SALES_ACTUALS A
WHERE A.PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID;
INSERT INTO ST_ACCRUAL_SALES_DETAILS(PROJECTION_MASTER_SID, ITEM_MASTER_SID, PERIOD_SID, TOTAL_UNITS, EXCLUDED_UNITS, NET_UNITS, PRICE, SALES, EXCLUDED_DOLLARS, DEMAND, ADJUSTED_DEMAND ) SELECT A.PROJECTION_MASTER_SID, A.ITEM_MASTER_SID, A.PERIOD_SID, A.TOTAL_UNITS, A.EXCLUDED_UNITS, A.NET_UNITS, A.PRICE, A.SALES, A.EXCLUDED_DOLLARS, A.DEMAND, A.ADJUSTED_DEMAND
FROM dbo.ACCRUAL_SALES_DETAILS A
WHERE A.PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID;

INSERT INTO ST_ACCRUAL_DETAILS_ACTUALS (PROJECTION_MASTER_SID, ITEM_MASTER_SID,PERIOD_SID,COMPANY_MASTER_SID,GROSS_TRADE_SALES,INVENTORY_WITHDRAWALS,ELIGIBLE_GROSS_TRADE_SALES, PERIOD_BASIS_PRICE_CHANGE, GTS_ACCRUAL_BASIS ) SELECT A.PROJECTION_MASTER_SID, A.ITEM_MASTER_SID, A.PERIOD_SID, A.COMPANY_MASTER_SID, A.GROSS_TRADE_SALES, A.INVENTORY_WITHDRAWALS, A.ELIGIBLE_GROSS_TRADE_SALES, A.PERIOD_BASIS_PRICE_CHANGE, A.GTS_ACCRUAL_BASIS
FROM dbo.ACCRUAL_DETAILS_ACTUALS A WHERE A.PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID;
INSERT INTO ST_ACCRUAL_DETAILS_INFO (PROJECTION_MASTER_SID, ITEM_MASTER_SID,PERIOD_SID,COMPANY_MASTER_SID,GROSS_TRADE_SALES,INVENTORY_WITHDRAWALS,ELIGIBLE_GROSS_TRADE_SALES, PERIOD_BASIS_PRICE_CHANGE, GTS_ACCRUAL_BASIS ) SELECT A.PROJECTION_MASTER_SID, A.ITEM_MASTER_SID, A.PERIOD_SID, A.COMPANY_MASTER_SID, A.GROSS_TRADE_SALES, A.INVENTORY_WITHDRAWALS, A.ELIGIBLE_GROSS_TRADE_SALES, A.PERIOD_BASIS_PRICE_CHANGE, A.GTS_ACCRUAL_BASIS
FROM dbo.ACCRUAL_DETAILS_INFO A WHERE A.PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID;

INSERT INTO ST_EXCLUSION_DETAILS(PROJECTION_MASTER_SID, COMPANY_MASTER_SID ) SELECT A.PROJECTION_MASTER_SID, A.COMPANY_MASTER_SID
FROM dbo.EXCLUSION_DETAILS A
WHERE A.PROJECTION_MASTER_SID=@PROJECTION_MASTER_SID;
