entity.query:
DECLARE @COMPANY_CONTRACT TABLE ( COMPANY_MASTER_SID INT, CONTRACT_MASTER_SID INT ) INSERT INTO @COMPANY_CONTRACT(COMPANY_MASTER_SID,CONTRACT_MASTER_SID) SELECT DISTINCT COMPANY_SID,COMPONENT_MASTER_SID FROM AH_TEMP_DETAILS WHERE USER_ID = @USER_ID AND SESSION_ID = @SESSION_ID AND SCREEN_NAME LIKE 'Customer_Selection';
DECLARE @ITEM TABLE ( ITEM_MASTER_SID INT ) INSERT INTO @ITEM(ITEM_MASTER_SID) SELECT DISTINCT ITEM_MASTER_SID FROM AH_TEMP_DETAILS WHERE USER_ID = @USER_ID AND SESSION_ID = @SESSION_ID AND SCREEN_NAME LIKE 'Item_Selection';
SELECT COUNT(CCP.CCP_DETAILS_SID) FROM CCP_DETAILS CCP JOIN @COMPANY_CONTRACT CC ON CC.COMPANY_MASTER_SID = CCP.COMPANY_MASTER_SID AND CC.CONTRACT_MASTER_SID = CCP.CONTRACT_MASTER_SID JOIN @ITEM I ON I.ITEM_MASTER_SID = CCP.ITEM_MASTER_SID;
