entity.query:
IF Object_id('TEMPDB..#ST_NM_SALES_PROJECTION') IS NOT NULL DROP TABLE #ST_NM_SALES_PROJECTION
SELECT SHN.HIERARCHY_NO ,P."YEAR" , P.year @FRE@ , SUM(NMSP.PROJECTION_SALES) AS CONTRACT_PROJECTION_SALES , SUM(NMSP.PROJECTION_UNITS) AS CONTRACT_PROJECTION_UNITS , AVG(NMSP.ACCOUNT_GROWTH) AS ACCOUNT_GROWTH , AVG(NMSP.PRODUCT_GROWTH) AS PRODUCT_GROWTH , COALESCE(SUM(NMSP.PROJECTION_SALES) / NULLIF(SUM(ST.EXFACTORY_FORECAST_SALES),0),0) *100 AS EXFACT_PROJECTION_SALES , COUNT(DISTINCT SHN.CCP_DETAILS_SID) AS ROW_COUNT INTO #ST_NM_SALES_PROJECTION FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_SALES_PROJECTION NMSP ON NMSP.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID JOIN CCP_DETAILS CD ON CD.CCP_DETAILS_SID = NMSP.CCP_DETAILS_SID LEFT JOIN ST_PRODUCT_FILE ST ON CD.ITEM_MASTER_SID = ST.ITEM_MASTER_SID AND ST.PERIOD_SID = NMSP.PERIOD_SID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID GROUP BY SHN.HIERARCHY_NO,P."YEAR", P.year;
IF Object_id('TEMPDB..#ST_NM_ACTUAL_SALES') IS NOT NULL DROP TABLE #ST_NM_ACTUAL_SALES SELECT SHN.HIERARCHY_NO,P."YEAR", P.year @FRE@, Sum(NMSP.ACTUAL_SALES) AS CONTRACT_ACTUAL_SALES, Sum(NMSP.ACTUAL_UNITS) AS CONTRACT_ACTUAL_UNITS, COALESCE(SUM(NMSP.ACTUAL_SALES) / NuLLIF(SUM(ST.EXFACTORY_ACTUAL_SALES),0),0) *100 AS EXFACT_ACTUAL_SALES INTO #ST_NM_ACTUAL_SALES FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_ACTUAL_SALES NMSP ON NMSP.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID JOIN CCP_DETAILS CD ON CD.CCP_DETAILS_SID = NMSP.CCP_DETAILS_SID LEFT JOIN ST_PRODUCT_FILE ST ON CD.ITEM_MASTER_SID = ST.ITEM_MASTER_SID AND ST.PERIOD_SID = NMSP.PERIOD_SID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID GROUP BY SHN.HIERARCHY_NO,P."YEAR", P.year;
SELECT MAINQ.YEARS, MAINQ.PERIODS, MAINQ.CONTRACT_PROJECTION_SALES, MAINQ.CONTRACT_PROJECTION_UNITS, MAINQ.CONTRACT_ACTUAL_SALES, MAINQ.CONTRACT_ACTUAL_UNITS, MAINQ.ap_indicator, MAINQ.EXFACT_PROJECTION_SALES, MAINQ.EXFACT_ACTUAL_SALES FROM ( SELECT Q.CONTRACT_PROJECTION_SALES, Q.CONTRACT_PROJECTION_UNITS, Q.CONTRACT_ACTUAL_SALES , Q.CONTRACT_ACTUAL_UNITS , Q.EXFACT_PROJECTION_SALES, Q.EXFACT_ACTUAL_SALES,
Q.YEARS, Q.PERIODS, Q.ap_indicator, Dense_rank() OVER ( ORDER BY Q.HIERARCHY_NO ASC ) AS TEMP_INDEX FROM ( SELECT NULL AS CONTRACT_PROJECTION_SALES, NULL AS CONTRACT_PROJECTION_UNITS, MAS.CONTRACT_ACTUAL_SALES, MAS.CONTRACT_ACTUAL_UNITS, NULL AS EXFACT_PROJECTION_SALES, MAS.EXFACT_ACTUAL_SALES, "YEAR" AS YEARS, @FRE@ AS PERIODS, MAS.HIERARCHY_NO, 0 AS ap_indicator FROM #ST_NM_ACTUAL_SALES MAS UNION ALL
SELECT MSP.CONTRACT_PROJECTION_SALES, MSP.CONTRACT_PROJECTION_UNITS, NULL AS CONTRACT_ACTUAL_SALES, NULL AS CONTRACT_ACTUAL_UNITS, MSP.EXFACT_PROJECTION_SALES, NULL AS EXFACT_ACTUAL_SALES, "YEAR" AS YEARS, @FRE@ AS PERIODS, MSP.HIERARCHY_NO, 1 as ap_indicator FROM #ST_NM_SALES_PROJECTION MSP ) Q) MAINQ ORDER BY MAINQ.YEARS, MAINQ.PERIODS;
