entity.query:
IF Object_id('TEMPDB..#ST_NM_SALES_PROJECTION') IS NOT NULL DROP TABLE #ST_NM_SALES_PROJECTION
SELECT SHN.HIERARCHY_NO, P."YEAR", @FREQUENCY@ , SUM(NMSP.PROJECTION_SALES) AS PROJECTION_SALES , @SUMPROJECTEDUNITS, AVG(NMSP.ACCOUNT_GROWTH) AS ACCOUNT_GROWTH , AVG(NMSP.PRODUCT_GROWTH) AS PRODUCT_GROWTH , COUNT(DISTINCT SHN.CCP_DETAILS_SID) AS ROW_COUNT INTO #ST_NM_SALES_PROJECTION FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_SALES_PROJECTION NMSP ON NMSP.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID @UOMCODE @SALESINCLUSION GROUP BY SHN.HIERARCHY_NO, P."YEAR" @FREQUENCY_GROUP@;
IF Object_id('TEMPDB..#ST_NM_ACTUAL_SALES') IS NOT NULL DROP TABLE #ST_NM_ACTUAL_SALES SELECT SHN.HIERARCHY_NO, P."YEAR", @FREQUENCY@, Sum(NMSP.ACTUAL_SALES) AS ACTUAL_SALES, @SUMACTUALUNITS INTO #ST_NM_ACTUAL_SALES FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_ACTUAL_SALES NMSP ON NMSP.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID @UOMCODE @SALESINCLUSION GROUP BY SHN.HIERARCHY_NO, P."YEAR" @FREQUENCY_GROUP@;
IF Object_id('TEMPDB..#ST_NM_SALES_PROJECTION_MASTER') IS NOT NULL DROP TABLE #ST_NM_SALES_PROJECTION_MASTER SELECT CASE WHEN Count(DISTINCT MSPM.CALCULATION_PERIODS) > 1 THEN '-' ELSE Max(MSPM.CALCULATION_PERIODS) END CALCULATION_PERIODS, CASE WHEN Count(DISTINCT MSPM.METHODOLOGY) > 1 THEN '-' ELSE Max(MSPM.METHODOLOGY) END AS METHODOLOGY, SHN.HIERARCHY_NO, Count(*) AS RCOUNT, Min(CASE (CHECK_RECORD) WHEN 1 THEN 1 ELSE 0 END) AS checkrec, Sum(CASE (MSPM.CHECK_RECORD) WHEN 1 THEN 0 ELSE 1 END) AS UNCHECK_COUNT, Count(MSPM.CCP_DETAILS_SID) AS CCPCOUNT, Min(MSPM.USER_GROUP) AS USER_GROUP, SHN.SALES_INCLUSION INTO #ST_NM_SALES_PROJECTION_MASTER FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_SALES_PROJECTION_MASTER MSPM ON MSPM.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID @SALESINCLUSION GROUP BY SHN.HIERARCHY_NO,SHN.SALES_INCLUSION;
DECLARE @STAT_SALES INT ,@EN_SALES INT ,@ACTUAL_END_PERIOD int, @ACTUAL_START_PERIOD int
SET @STAT_SALES = ( SELECT min(PERIOD_SID) FROM ST_NM_ACTUAL_SALES ) SET @ACTUAL_END_PERIOD = ( SELECT MAX(PERIOD_SID) FROM ST_NM_SALES_PROJECTION )
SET @EN_SALES = ( SELECT MAX(PERIOD_SID) FROM ST_NM_ACTUAL_SALES )
SET @ACTUAL_START_PERIOD = ( SELECT MIN(PERIOD_SID) FROM ST_NM_SALES_PROJECTION)
SELECT MAINQ.account_growth, MAINQ.product_growth, MAINQ.projection_sales, MAINQ.projection_units, MAINQ.actualsales, MAINQ.actualunits, MAINQ.YEARS, MAINQ.PERIODS, MAINQ.calculation_periods, MAINQ.methodology, MAINQ.HIERARCHY_NO, MAINQ.rcount, MAINQ.actualproj, MAINQ.checkrec, MAINQ.uncheck_count, MAINQ.ccpcount, MAINQ.hierarchy_indicator, MAINQ.user_group, '' AS SEC_HIERARCHY,SALES_INCLUSION , MAINQ.INSTR FROM (SELECT Q.account_growth, Q.product_growth, Q.projection_sales, Q.projection_units, Q.actualsales, Q.actualunits, Q.YEARS, Q.PERIODS, Q.calculation_periods, Q.methodology, Q.HIERARCHY_NO, Q.rcount, Q.actualproj, Q.checkrec, Q.uncheck_count, Q.ccpcount, Q.hierarchy_indicator, Q.user_group, Dense_rank() OVER( ORDER BY Q.INSTR ASC) AS TEMP_INDEX,Q.SALES_INCLUSION,Q.INSTR FROM (SELECT distinct 0.0 AS ACCOUNT_GROWTH, 0.0 AS PRODUCT_GROWTH, NULL AS PROJECTION_SALES, NULL AS PROJECTION_UNITS, MAS.ACTUAL_SALES AS actualSales, MAS.ACTUAL_UNITS AS actualUnits, SH."YEAR" AS YEARS, SH.@FRE@ AS PERIODS, sh.CALCULATION_PERIODS, sh.METHODOLOGY, sh.HIERARCHY_NO, RCOUNT, 1 AS actualProj, checkrec, UNCHECK_COUNT, CCPCOUNT, '@HIERARCHY_INDICATOR' AS HIERARCHY_INDICATOR, sh.USER_GROUP,MSPM.SALES_INCLUSION,INSTR FROM (SELECT HIERARCHY_NO,year,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,P.@FREVAL@,INSTR FROM #SELECTED_HIERARCHY_NO SHS CROSS JOIN PERIOD P WHERE P.PERIOD_SID BETWEEN @STAT_SALES AND @EN_SALES GROUP BY year,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,P.@FREVAL@,INSTR,HIERARCHY_NO) SH left join #ST_NM_SALES_PROJECTION_MASTER MSPM on mspm.HIERARCHY_NO=sh.HIERARCHY_NO left JOIN #ST_NM_ACTUAL_SALES MAS ON MSPM.HIERARCHY_NO = MAS.HIERARCHY_NO and sh.YEAR=mas.year and SH.@FRE@=mas.@FRE@ UNION ALL SELECT distinct MSP.ACCOUNT_GROWTH, MSP.PRODUCT_GROWTH, MSP.PROJECTION_SALES, MSP.PROJECTION_UNITS, NULL AS ACTUAL_SALES, NULL AS ACTUAL_UNITS, SH."YEAR" AS YEARS, SH.@FRE@ AS PERIODS, sh.CALCULATION_PERIODS, sh.METHODOLOGY, sh.HIERARCHY_NO, RCOUNT, 0 AS actualProj, checkrec, UNCHECK_COUNT, CCPCOUNT, '@HIERARCHY_INDICATOR' AS HIERARCHY_INDICATOR, sh.USER_GROUP,MSPM.SALES_INCLUSION, INSTR FROM (SELECT HIERARCHY_NO,year,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,P.@FREVAL@,INSTR FROM #SELECTED_HIERARCHY_NO SHS CROSS JOIN PERIOD P WHERE P.PERIOD_SID BETWEEN @ACTUAL_START_PERIOD AND @ACTUAL_END_PERIOD GROUP BY year,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,P.@FREVAL@,INSTR,HIERARCHY_NO) sh left join #ST_NM_SALES_PROJECTION_MASTER MSPM on mspm.HIERARCHY_NO=sh.HIERARCHY_NO left JOIN #ST_NM_SALES_PROJECTION MSP ON MSPM.HIERARCHY_NO = MSP.HIERARCHY_NO and sh.YEAR=MSP.year and SH.@FRE@=MSP.@FRE@) Q) MAINQ WHERE MAINQ.TEMP_INDEX > @START AND MAINQ.TEMP_INDEX <= (@START+@END) ORDER BY MAINQ.INSTR, MAINQ.YEARS, MAINQ.PERIODS;

IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO
CREATE TABLE #SELECTED_HIERARCHY_NO ( HIERARCHY_NO VARCHAR(50), SEC_HIERARCHYNO VARCHAR(50), INSTTR INT , HIERARCHY_INDICATOR VARCHAR(50), CCP_DETAILS_SID INT ) INSERT INTO #SELECTED_HIERARCHY_NO (HIERARCHY_NO, SEC_HIERARCHYNO, INSTTR, HIERARCHY_INDICATOR, CCP_DETAILS_SID) SELECT DISTINCT A.HIERARCHY_NO, SEC_HIERARCHYNO, INSTTR, HIERARCHY_INDICATOR, CH.CCP_DETAILS_SID FROM (VALUES [?Hierarchy-Combination]
)A(HIERARCHY_NO,SEC_HIERARCHYNO,HIERARCHY_INDICATOR,INSTTR) JOIN ST_CCP_HIERARCHY CH ON ((CH.CUST_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%' OR CH.PROD_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%') AND (HIERARCHY_NO IS NULL OR SEC_HIERARCHYNO IS NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%' OR CH.PROD_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%') AND (HIERARCHY_NO IS NULL OR SEC_HIERARCHYNO IS NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%' AND CH.PROD_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%') AND (HIERARCHY_NO IS NOT NULL AND SEC_HIERARCHYNO IS NOT NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%' AND CH.PROD_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%') AND (HIERARCHY_NO IS NOT NULL AND SEC_HIERARCHYNO IS NOT NULL)) JOIN ST_NM_SALES_PROJECTION_MASTER SPM ON SPM.CCP_DETAILS_SID = CH.CCP_DETAILS_SID

IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO
CREATE TABLE #SELECTED_HIERARCHY_NO ( HIERARCHY_NO VARCHAR(50), SEC_HIERARCHYNO VARCHAR(50), INSTTR INT , HIERARCHY_INDICATOR VARCHAR(50), CCP_DETAILS_SID INT,SALES_INCLUSION int, CALCULATION_PERIODS varchar(8000),USER_GROUP varchar(10),METHODOLOGY varchar(100) ) ;WITH HIERARCHY AS (SELECT DISTINCT A.HIERARCHY_NO, SEC_HIERARCHYNO, INSTTR, HIERARCHY_INDICATOR, CH.CCP_DETAILS_SID,SALES_INCLUSION,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,RELATIONSHIP_LEVEL_VALUES FROM (VALUES [?Hierarchy-Combination]
)A(HIERARCHY_NO,SEC_HIERARCHYNO,HIERARCHY_INDICATOR,INSTTR) JOIN ST_CCP_HIERARCHY CH ON ((CH.CUST_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%' OR CH.PROD_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%') AND (HIERARCHY_NO IS NULL OR SEC_HIERARCHYNO IS NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%' OR CH.PROD_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%') AND (HIERARCHY_NO IS NULL OR SEC_HIERARCHYNO IS NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%' AND CH.PROD_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%') AND (HIERARCHY_NO IS NOT NULL AND SEC_HIERARCHYNO IS NOT NULL)) OR ((CH.CUST_HIERARCHY_NO LIKE A.HIERARCHY_NO + '%' AND CH.PROD_HIERARCHY_NO LIKE A.SEC_HIERARCHYNO + '%') AND (HIERARCHY_NO IS NOT NULL AND SEC_HIERARCHYNO IS NOT NULL)) JOIN RELATIONSHIP_LEVEL_DEFINITION RLD1 ON RLD1.HIERARCHY_NO = A.HIERARCHY_NO JOIN ST_NM_SALES_PROJECTION_MASTER SPM ON SPM.CCP_DETAILS_SID = CH.CCP_DETAILS_SID) INSERT INTO #SELECTED_HIERARCHY_NO (HIERARCHY_NO, SEC_HIERARCHYNO, INSTTR, HIERARCHY_INDICATOR, CCP_DETAILS_SID,SALES_INCLUSION,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY) SELECT DISTINCT STUFF(( SELECT DISTINCT ', ' + HIERARCHY_NO FROM HIERARCHY B WHERE B.RELATIONSHIP_LEVEL_VALUES = A.RELATIONSHIP_LEVEL_VALUES FOR XML PATH('') ), 1, 2, '') HIERARCHY_NO, SEC_HIERARCHYNO, INSTTR, HIERARCHY_INDICATOR, CCP_DETAILS_SID,SALES_INCLUSION,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY FROM HIERARCHY A

IF Object_id('TEMPDB..#ST_NM_SALES_PROJECTION') IS NOT NULL DROP TABLE #ST_NM_SALES_PROJECTION
SELECT SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.INSTTR, SHN.HIERARCHY_INDICATOR, P."YEAR", @FREQUENCY@ , SUM(NMSP.PROJECTION_SALES) AS PROJECTION_SALES , SUM(NMSP.PROJECTION_UNITS) AS PROJECTION_UNITS , AVG(NMSP.ACCOUNT_GROWTH) AS ACCOUNT_GROWTH , AVG(NMSP.PRODUCT_GROWTH) AS PRODUCT_GROWTH , COUNT(DISTINCT SHN.CCP_DETAILS_SID) AS ROW_COUNT INTO #ST_NM_SALES_PROJECTION FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_SALES_PROJECTION NMSP ON NMSP.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID GROUP BY SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.INSTTR, SHN.HIERARCHY_INDICATOR, P."YEAR" @FREQUENCY_GROUP@;
IF Object_id('TEMPDB..#ST_NM_ACTUAL_SALES') IS NOT NULL DROP TABLE #ST_NM_ACTUAL_SALES SELECT SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.INSTTR, SHN.HIERARCHY_INDICATOR, P."YEAR", @FREQUENCY@, Sum(NMSP.ACTUAL_SALES) AS ACTUAL_SALES, Sum(NMSP.ACTUAL_UNITS) AS ACTUAL_UNITS INTO #ST_NM_ACTUAL_SALES FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_ACTUAL_SALES NMSP ON NMSP.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID GROUP BY SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.INSTTR, SHN.HIERARCHY_INDICATOR, P."YEAR" @FREQUENCY_GROUP@;
IF Object_id('TEMPDB..#ST_NM_SALES_PROJECTION_MASTER') IS NOT NULL DROP TABLE #ST_NM_SALES_PROJECTION_MASTER SELECT CASE WHEN Count(DISTINCT MSPM.CALCULATION_PERIODS) > 1 THEN '-' ELSE Max(MSPM.CALCULATION_PERIODS) END CALCULATION_PERIODS, CASE WHEN Count(DISTINCT MSPM.METHODOLOGY) > 1 THEN '-' ELSE Max(MSPM.METHODOLOGY) END AS METHODOLOGY, SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.INSTTR, SHN.HIERARCHY_INDICATOR, Count(*) AS RCOUNT, Min(CASE (CHECK_RECORD) WHEN 1 THEN 1 ELSE 0 END) AS checkrec, Sum(CASE (MSPM.CHECK_RECORD) WHEN 1 THEN 0 ELSE 1 END) AS UNCHECK_COUNT, Count(MSPM.CCP_DETAILS_SID) AS CCPCOUNT, Min(MSPM.USER_GROUP) AS USER_GROUP INTO #ST_NM_SALES_PROJECTION_MASTER FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_SALES_PROJECTION_MASTER MSPM ON MSPM.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID GROUP BY SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO,SHN.INSTTR,SHN.HIERARCHY_INDICATOR;
SELECT MAINQ.account_growth, MAINQ.product_growth, MAINQ.projection_sales, MAINQ.projection_units, MAINQ.actualsales, MAINQ.actualunits, MAINQ.YEARS, MAINQ.PERIODS, MAINQ.calculation_periods, MAINQ.methodology, MAINQ.HIERARCHY_NO, MAINQ.rcount, MAINQ.actualproj, MAINQ.checkrec, MAINQ.uncheck_count, MAINQ.ccpcount, MAINQ.HIERARCHY_INDICATOR, MAINQ.user_group, MAINQ.SEC_HIERARCHYNO FROM (SELECT Q.account_growth, Q.product_growth, Q.projection_sales, Q.projection_units, Q.actualsales, Q.actualunits, Q.YEARS, Q.PERIODS, Q.calculation_periods, Q.methodology, Q.HIERARCHY_NO, Q.SEC_HIERARCHYNO, Q.INSTTR, Q.rcount, Q.actualproj, Q.checkrec, Q.uncheck_count, Q.ccpcount, Q.HIERARCHY_INDICATOR, Q.user_group, Dense_rank() OVER( ORDER BY Q.HIERARCHY_NO ASC) AS TEMP_INDEX FROM (SELECT 0.0 AS ACCOUNT_GROWTH, 0.0 AS PRODUCT_GROWTH, NULL AS PROJECTION_SALES, NULL AS PROJECTION_UNITS, MAS.ACTUAL_SALES AS actualSales, MAS.ACTUAL_UNITS AS actualUnits, "YEAR" AS YEARS, @FRE@ AS PERIODS, CALCULATION_PERIODS, METHODOLOGY, MSPM.HIERARCHY_NO, MSPM.SEC_HIERARCHYNO, MSPM.INSTTR, RCOUNT, 1 AS actualProj, checkrec, UNCHECK_COUNT, CCPCOUNT, MSPM.HIERARCHY_INDICATOR, USER_GROUP FROM #ST_NM_SALES_PROJECTION_MASTER MSPM JOIN #ST_NM_ACTUAL_SALES MAS ON MSPM.HIERARCHY_NO = MAS.HIERARCHY_NO and (MSPM.SEC_HIERARCHYNO = MAS.SEC_HIERARCHYNO or MSPM.SEC_HIERARCHYNO is null ) UNION ALL SELECT MSP.ACCOUNT_GROWTH, MSP.PRODUCT_GROWTH, MSP.PROJECTION_SALES, MSP.PROJECTION_UNITS, NULL AS ACTUAL_SALES, NULL AS ACTUAL_UNITS, "YEAR" AS YEARS, @FRE@ AS PERIODS, CALCULATION_PERIODS, METHODOLOGY, MSPM.HIERARCHY_NO, MSPM.SEC_HIERARCHYNO, MSPM.INSTTR, RCOUNT, 0 AS actualProj, checkrec, UNCHECK_COUNT, CCPCOUNT, MSPM.HIERARCHY_INDICATOR, USER_GROUP FROM #ST_NM_SALES_PROJECTION_MASTER MSPM JOIN #ST_NM_SALES_PROJECTION MSP ON MSPM.HIERARCHY_NO = MSP.HIERARCHY_NO and (MSPM.SEC_HIERARCHYNO = MSP.SEC_HIERARCHYNO or MSPM.SEC_HIERARCHYNO is null ) ) Q) MAINQ WHERE MAINQ.TEMP_INDEX > @START AND MAINQ.TEMP_INDEX <= (@START+@END) ORDER BY MAINQ.INSTTR, MAINQ.HIERARCHY_NO, MAINQ.SEC_HIERARCHYNO, MAINQ.YEARS, MAINQ.PERIODS;

IF Object_id('TEMPDB..#ST_NM_SALES_PROJECTION') IS NOT NULL DROP TABLE #ST_NM_SALES_PROJECTION
SELECT SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.HIERARCHY_INDICATOR, P."YEAR", @FREQUENCY@ , SUM(NMSP.PROJECTION_SALES) AS PROJECTION_SALES , @SUMPROJECTEDUNITS , AVG(NMSP.ACCOUNT_GROWTH) AS ACCOUNT_GROWTH , AVG(NMSP.PRODUCT_GROWTH) AS PRODUCT_GROWTH , COUNT(DISTINCT SHN.CCP_DETAILS_SID) AS ROW_COUNT INTO #ST_NM_SALES_PROJECTION FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_SALES_PROJECTION NMSP ON NMSP.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID @UOMCODE @SALESINCLUSION GROUP BY SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.HIERARCHY_INDICATOR, P."YEAR" @FREQUENCY_GROUP@;
IF Object_id('TEMPDB..#ST_NM_ACTUAL_SALES') IS NOT NULL DROP TABLE #ST_NM_ACTUAL_SALES SELECT SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.HIERARCHY_INDICATOR, P."YEAR", @FREQUENCY@, Sum(NMSP.ACTUAL_SALES) AS ACTUAL_SALES, @SUMACTUALUNITS INTO #ST_NM_ACTUAL_SALES FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_ACTUAL_SALES NMSP ON NMSP.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID JOIN PERIOD P ON P.PERIOD_SID = NMSP.PERIOD_SID @UOMCODE @SALESINCLUSION GROUP BY SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.HIERARCHY_INDICATOR, P."YEAR" @FREQUENCY_GROUP@;
IF Object_id('TEMPDB..#ST_NM_SALES_PROJECTION_MASTER') IS NOT NULL DROP TABLE #ST_NM_SALES_PROJECTION_MASTER SELECT CASE WHEN Count(DISTINCT MSPM.CALCULATION_PERIODS) > 1 THEN '-' ELSE Max(MSPM.CALCULATION_PERIODS) END CALCULATION_PERIODS, CASE WHEN Count(DISTINCT MSPM.METHODOLOGY) > 1 THEN '-' ELSE Max(MSPM.METHODOLOGY) END AS METHODOLOGY, SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO, SHN.HIERARCHY_INDICATOR, Count(*) AS RCOUNT, Min(CASE (CHECK_RECORD) WHEN 1 THEN 1 ELSE 0 END) AS checkrec, Sum(CASE (MSPM.CHECK_RECORD) WHEN 1 THEN 0 ELSE 1 END) AS UNCHECK_COUNT, Count(MSPM.CCP_DETAILS_SID) AS CCPCOUNT, Min(MSPM.USER_GROUP) AS USER_GROUP,mspm.SALES_INCLUSION INTO #ST_NM_SALES_PROJECTION_MASTER FROM #SELECTED_HIERARCHY_NO SHN JOIN ST_NM_SALES_PROJECTION_MASTER MSPM ON MSPM.CCP_DETAILS_SID = SHN.CCP_DETAILS_SID @SALESINCLUSION GROUP BY SHN.HIERARCHY_NO, SHN.SEC_HIERARCHYNO,SHN.HIERARCHY_INDICATOR,mspm.SALES_INCLUSION;
DECLARE @STAT_PERIOD INT ,@EN_PERIOD INT ,@ACTUAL_END_PERIOD int, @ACTUAL_START_PERIOD int
SET @STAT_PERIOD = ( SELECT MIN(PERIOD_SID) FROM ST_NM_ACTUAL_SALES ) SET @ACTUAL_END_PERIOD = ( SELECT MAX(PERIOD_SID) FROM ST_NM_SALES_PROJECTION )
SET @EN_PERIOD = ( SELECT MAX(PERIOD_SID) FROM ST_NM_ACTUAL_SALES ) SET @ACTUAL_START_PERIOD = ( SELECT MIN(PERIOD_SID) FROM ST_NM_SALES_PROJECTION)
SELECT MAINQ.account_growth, MAINQ.product_growth, MAINQ.projection_sales, MAINQ.projection_units, MAINQ.actualsales, MAINQ.actualunits, MAINQ.YEARS, MAINQ.PERIODS, MAINQ.calculation_periods, MAINQ.methodology, MAINQ.HIERARCHY_NO, MAINQ.rcount, MAINQ.actualproj, MAINQ.checkrec, MAINQ.uncheck_count, MAINQ.ccpcount, MAINQ.HIERARCHY_INDICATOR, MAINQ.user_group, MAINQ.SEC_HIERARCHYNO, SALES_INCLUSION FROM (SELECT Q.account_growth, Q.product_growth, Q.projection_sales, Q.projection_units, Q.actualsales, Q.actualunits, Q.YEARS, Q.PERIODS, Q.calculation_periods, Q.methodology, Q.HIERARCHY_NO, Q.SEC_HIERARCHYNO, Q.rcount, Q.actualproj, Q.checkrec, Q.uncheck_count, Q.ccpcount, Q.HIERARCHY_INDICATOR, Q.user_group, Dense_rank() OVER( ORDER BY Q.HIERARCHY_NO ASC) AS TEMP_INDEX,Q.SALES_INCLUSION FROM (SELECT DISTINCT 0.0 AS ACCOUNT_GROWTH, 0.0 AS PRODUCT_GROWTH, NULL AS PROJECTION_SALES, NULL AS PROJECTION_UNITS, MAS.ACTUAL_SALES AS actualSales, MAS.ACTUAL_UNITS AS actualUnits, SH."YEAR" AS YEARS, SH.@FRE@ AS PERIODS, SH.CALCULATION_PERIODS, SH.METHODOLOGY, SH.HIERARCHY_NO, MSPM.SEC_HIERARCHYNO, RCOUNT, 1 AS actualProj, checkrec, UNCHECK_COUNT, CCPCOUNT, SH.HIERARCHY_INDICATOR, SH.USER_GROUP,MSPM.SALES_INCLUSION FROM (SELECT HIERARCHY_NO,year,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,P.@FREVAL@,INSTtR,HIERARCHY_INDICATOR FROM #SELECTED_HIERARCHY_NO SHS CROSS JOIN PERIOD P WHERE P.PERIOD_SID BETWEEN @STAT_PERIOD AND @EN_PERIOD GROUP BY year,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,P.@FREVAL@,INSTtR,HIERARCHY_NO,HIERARCHY_INDICATOR) SH left join #ST_NM_SALES_PROJECTION_MASTER MSPM ON MSPM.HIERARCHY_NO = sh.HIERARCHY_NO left JOIN #ST_NM_ACTUAL_SALES MAS ON MSPM.HIERARCHY_NO = MAS.HIERARCHY_NO and sh.YEAR=mas.year and SH.@FRE@=mas.@FRE@ and (MSPM.SEC_HIERARCHYNO = MAS.SEC_HIERARCHYNO or MSPM.SEC_HIERARCHYNO is null ) UNION ALL SELECT DISTINCT MSP.ACCOUNT_GROWTH, MSP.PRODUCT_GROWTH, MSP.PROJECTION_SALES, MSP.PROJECTION_UNITS, NULL AS ACTUAL_SALES, NULL AS ACTUAL_UNITS, SH."YEAR" AS YEARS, SH.@FRE@ AS PERIODS, SH.CALCULATION_PERIODS, SH.METHODOLOGY, SH.HIERARCHY_NO, MSPM.SEC_HIERARCHYNO, RCOUNT, 0 AS actualProj, checkrec, UNCHECK_COUNT, CCPCOUNT, SH.HIERARCHY_INDICATOR, SH.USER_GROUP,MSPM.SALES_INCLUSION FROM (SELECT HIERARCHY_NO,year,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,P.@FREVAL@,INSTtR,HIERARCHY_INDICATOR FROM #SELECTED_HIERARCHY_NO SHS CROSS JOIN PERIOD P WHERE P.PERIOD_SID BETWEEN @ACTUAL_START_PERIOD AND @ACTUAL_END_PERIOD GROUP BY year,CALCULATION_PERIODS,USER_GROUP,METHODOLOGY,P.@FREVAL@,INSTtR,HIERARCHY_NO,HIERARCHY_INDICATOR) SH left join #ST_NM_SALES_PROJECTION_MASTER MSPM ON MSPM.HIERARCHY_NO = sh.HIERARCHY_NO left JOIN #ST_NM_SALES_PROJECTION MSP ON MSPM.HIERARCHY_NO = MSP.HIERARCHY_NO and sh.YEAR=MSP.year and SH.@FRE@=MSP.@FRE@ and (MSPM.SEC_HIERARCHYNO = MSP.SEC_HIERARCHYNO or MSPM.SEC_HIERARCHYNO is null ) ) Q) MAINQ WHERE MAINQ.TEMP_INDEX > @START AND MAINQ.TEMP_INDEX <= (@START+@END) ORDER BY MAINQ.HIERARCHY_NO, MAINQ.SEC_HIERARCHYNO, MAINQ.YEARS, MAINQ.PERIODS;


UPDATE NMSPM
    SET CHECK_RECORD = [?CHECK_RECORD]
FROM ST_NM_SALES_PROJECTION_MASTER NMSPM;


UPDATE 
    @TABLE_NAME
SET 
    @COLUMN_NAME = @VALUE
FROM 
    @PROJECTION_TABLE SP
JOIN 
    @MASTER_TABLE SPM 
    @JOIN_CONDITION
JOIN 
    PERIOD P 
    ON P.PERIOD_SID = SP.PERIOD_SID
WHERE 
    @USERID_ADD
    SPM.CHECK_RECORD = 1
    AND P.PERIOD_SID IN (SELECT PERIOD_SID FROM "PERIOD" where "YEAR" >= @START_YEAR and "YEAR" <= @END_YEAR
    AND P.PERIOD_SID NOT IN(SELECT PERIOD_SID FROM "PERIOD" where "YEAR" = @START_YEAR @START_FREQUENCY)
    AND P.PERIOD_SID NOT IN(SELECT PERIOD_SID FROM "PERIOD" where "YEAR" = @END_YEAR  @END_FREQUENCY));


UPDATE MSPM
    SET CHECK_RECORD = [?CHECK_RECORD]
FROM ST_M_SALES_PROJECTION_MASTER MSPM;


      SELECT  convert(VARCHAR(8000),HIERARCHY_NO) HIERARCHY_NO  FROM (SELECT DISTINCT HIERARCHY_NO = STUFF((
			SELECT DISTINCT ', ' + HIERARCHY_NO
			FROM #SELECTED_HIERARCHY_NO B
			WHERE B.RELATIONSHIP_LEVEL_VALUES = A.RELATIONSHIP_LEVEL_VALUES
			FOR XML PATH('')
			), 1, 2, ''),INSTR
FROM #SELECTED_HIERARCHY_NO A
@RELJOIN )S
ORDER BY INSTR
                OFFSET [?START] ROWS FETCH NEXT [?END] ROWS ONLY;
