entity.query:


SELECT CompanyNo FROM dbo.IMTD_CFP_DETAILS WHERE USERS_SID='295' AND SESSION_ID='1120201407070575' AND CHECK_RECORD=1
GROUP BY TradingPartnerSystemId, CompanyNo 
HAVING COUNT(TradingPartnerSystemId)>1;



SELECT CompanyNo  FROM dbo.IMTD_CFP_DETAILS WHERE USERS_SID='295' AND SESSION_ID='1120201407070575' 
GROUP BY TradingPartnerSystemId, CompanyNo 
HAVING COUNT(TradingPartnerSystemId)>1;

UPDATE CFP_CONTRACT_DETAILS SET INBOUND_STATUS='D' WHERE CFP_CONTRACT_DETAILS_SID in (SELECT CFP_DETAILS_SID FROM dbo.IMTD_CFP_DETAILS WHERE USERS_SID='?' AND SESSION_ID='?' AND (CHECK_RECORD=0 OR "Operation" = 'D'));
DELETE FROM dbo.IMTD_CFP_DETAILS WHERE USERS_SID='?' AND SESSION_ID='?' AND (CHECK_RECORD=0 OR "Operation" = 'D');
MERGE CFP_CONTRACT_DETAILS AS Target USING (SELECT CFP_DETAILS_START_DATE, CFP_DETAILS_END_DATE, CFP_DETAILS_ATTACHED_STATUS, CFP_DETAILS_ATTACHED_DATE, CFP_DETAILS_SID, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY ,COMPANY_MASTER_SID,COMPANY_START_DATE,COMPANY_END_DATE
FROM dbo.IMTD_CFP_DETAILS WHERE USERS_SID='?' AND SESSION_ID='?' AND ( "Operation" = 'U' OR "Operation" = 'A' ) ) AS Source ON (Target.CFP_CONTRACT_SID = ? AND TARGET.COMPANY_MASTER_SID = SOURCE.COMPANY_MASTER_SID ) WHEN MATCHED THEN UPDATE SET Target.CFP_CONTRACT_ATTACHED_STATUS = Source.CFP_DETAILS_ATTACHED_STATUS, Target.COMPANY_START_DATE = Source.CFP_DETAILS_START_DATE, Target.COMPANY_END_DATE = Source.CFP_DETAILS_END_DATE, Target.CREATED_DATE = Source.CREATED_DATE, Target.CREATED_BY = Source.CREATED_BY, Target.MODIFIED_DATE = Source.MODIFIED_DATE, Target.MODIFIED_BY = Source.MODIFIED_BY, Target.INBOUND_STATUS = 'U' WHEN NOT MATCHED THEN INSERT ( COMPANY_MASTER_SID, CFP_CONTRACT_SID,COMPANY_START_DATE, COMPANY_END_DATE, INBOUND_STATUS, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY,CFP_CONTRACT_ATTACHED_STATUS) VALUES( SOURCE.COMPANY_MASTER_SID,'?' , SOURCE.CFP_DETAILS_START_DATE, SOURCE.CFP_DETAILS_END_DATE, 'A' , SOURCE.CREATED_DATE, SOURCE.CREATED_BY, SOURCE.MODIFIED_DATE, SOURCE.MODIFIED_BY ,SOURCE.CFP_DETAILS_ATTACHED_STATUS);



SELECT COMPANY_MASTER_SID, COMPANY_START_DATE, COMPANY_END_DATE FROM dbo.CFP_CONTRACT_DETAILS WHERE CFP_CONTRACT_SID <> ? 
AND COMPANY_MASTER_SID IN (SELECT COMPANY_MASTER_SID FROM dbo.IMTD_CFP_DETAILS 
WHERE USERS_SID='?' AND SESSION_ID='?' AND "Operation" <> 'D');
