UPDATE IFP_CONTRACT_DETAILS SET INBOUND_STATUS='D' WHERE IFP_CONTRACT_DETAILS_SID in (SELECT IFP_DETAILS_SID FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=? AND SESSION_ID='?' AND (CHECK_RECORD=0 OR "Operation" = 'D'));
DELETE FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID='?' AND SESSION_ID='?' AND (CHECK_RECORD=0 OR "Operation" = 'D');
MERGE IFP_CONTRACT_DETAILS AS Target USING ( SELECT * FROM (SELECT row_number() over(partition by ITEM_MASTER_SID order by ITEM_MASTER_SID)as id, ATTACHED_STATUS, START_DATE, END_DATE, ATTACHED_DATE, IFP_DETAILS_SID, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY,ITEM_MASTER_SID FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=? AND SESSION_ID='?' AND ( "Operation" = 'U' OR "Operation" = 'A' ))a where a.id=1) AS Source ON (Target.IFP_CONTRACT_SID = ? AND TARGET.ITEM_MASTER_SID = SOURCE.ITEM_MASTER_SID ) WHEN MATCHED THEN UPDATE SET Target.ITEM_STATUS = Source.ATTACHED_STATUS, Target.ITEM_START_DATE = Source.START_DATE, Target.ITEM_END_DATE = Source.End_Date, Target.CREATED_DATE = Source.CREATED_DATE, Target.IFP_CONTRACT_ATTACHED_STATUS = Source.ATTACHED_STATUS, Target.IFP_CONTRACT_ATTACHED_DATE = Source.ATTACHED_DATE, Target.CREATED_BY = Source.CREATED_BY, Target.MODIFIED_DATE = Source.MODIFIED_DATE, Target.MODIFIED_BY = Source.MODIFIED_BY, Target.INBOUND_STATUS = 'U' WHEN NOT MATCHED THEN INSERT (IFP_CONTRACT_SID, ITEM_MASTER_SID, ITEM_STATUS, ITEM_START_DATE, ITEM_END_DATE,INBOUND_STATUS, RECORD_LOCK_STATUS, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY, IFP_CONTRACT_ATTACHED_STATUS,IFP_CONTRACT_ATTACHED_DATE) Values( ? , SOURCE.ITEM_MASTER_SID, SOURCE.ATTACHED_STATUS, SOURCE.START_DATE, SOURCE.END_DATE, 'A' ,'false' , SOURCE.CREATED_DATE, SOURCE.CREATED_BY, SOURCE.MODIFIED_DATE, SOURCE.MODIFIED_BY, SOURCE.ATTACHED_STATUS,GETDATE());

