DECLARE @ACTUALS_CCP UDT_CCP_DETAILS , @START_DATE DATETIME = ('@?START_DATE DATETIME') , @END_DATE DATETIME = ('@?END_DATE DATETIME') , @SCREEN_NAME VARCHAR(50) = '@?SCREEN_NAME'
INSERT INTO @ACTUALS_CCP ( CONTRACT_MASTER_SID , COMPANY_MASTER_SID , ITEM_MASTER_SID ) SELECT TEMP_CCP_TABLE.CONTRACT_MASTER_SID , TEMP_CCP_TABLE.COMPANY_MASTER_SID , TEMP_CCP_TABLE.ITEM_MASTER_SID FROM (SELECT DISTINCT CCP.CONTRACT_MASTER_SID , CCP.COMPANY_MASTER_SID , CCP.ITEM_MASTER_SID , CCP.CCP_DETAILS_SID FROM DBO.CCP_DETAILS CCP INNER JOIN ( SELECT CG.COMPANY_MASTER_SID , CG.CONTRACT_MASTER_SID , IG.ITEM_MASTER_SID FROM ( SELECT COMPANY_MASTER_SID, CONTRACT_MASTER_SID FROM dbo.ST_AL_HISTORY_CUSTOMER_SELECTION WHERE SELECTED_RECORDS = 1 ) CG CROSS JOIN ( SELECT ITEM_MASTER_SID FROM dbo.ST_AL_HISTORY_ITEM_SELECTION WHERE SELECTED_RECORDS = 1 ) IG ) SCCP ON SCCP.COMPANY_MASTER_SID = CCP.COMPANY_MASTER_SID AND SCCP.CONTRACT_MASTER_SID = CCP.CONTRACT_MASTER_SID AND SCCP.ITEM_MASTER_SID = CCP.ITEM_MASTER_SID JOIN COMPANY_MASTER CM ON SCCP.COMPANY_MASTER_SID = CM.COMPANY_MASTER_SID @COMPANY_FILTER JOIN ITEM_MASTER IM ON SCCP.ITEM_MASTER_SID = IM.ITEM_MASTER_SID @ITEM_FILTER JOIN CONTRACT_MASTER CON ON SCCP.CONTRACT_MASTER_SID = CON.CONTRACT_MASTER_SID @CONTRACT_FILTER ORDER BY CCP.CCP_DETAILS_SID OFFSET @?START_INDEX ROWS FETCH NEXT @?END_INDEX ROWS ONLY) TEMP_CCP_TABLE;
IF OBJECT_ID('TEMPDB.DBO.#TEMP', 'U') IS NOT NULL DROP TABLE #TEMP;
CREATE TABLE #TEMP ( CCP_DETAILS_SID INT , CONTRACT_MASTER_SID INT , COMPANY_MASTER_SID INT , ITEM_MASTER_SID INT , PERIOD_SID INT , PROJECTION_SALES NUMERIC(22, 6) , PROJECTION_UNITS NUMERIC(22, 6) , PROJECTION_DISCOUNT NUMERIC(22, 6) , RS_CONTRACT_SID INT )
IF @SCREEN_NAME = 'Sales Projection' BEGIN INSERT INTO #TEMP ( CCP_DETAILS_SID , CONTRACT_MASTER_SID , COMPANY_MASTER_SID , ITEM_MASTER_SID , PERIOD_SID , PROJECTION_SALES , PROJECTION_UNITS ) SELECT CCD.CCP_DETAILS_SID , cd.CONTRACT_MASTER_SID , cd.COMPANY_MASTER_SID , cd.ITEM_MASTER_SID , PERIOD_SID , COALESCE(PROJECTION_SALES, 0) AS PROJECTION_SALES , COALESCE(PROJECTION_UNITS, 0) AS PROJECTION_UNITS FROM @ACTUALS_CCP cd INNER JOIN CCP_DETAILS CCD ON CCD.CONTRACT_MASTER_SID = CD.CONTRACT_MASTER_SID AND CCD.COMPANY_MASTER_SID = CD.COMPANY_MASTER_SID AND CCD.ITEM_MASTER_SID = CD.ITEM_MASTER_SID CROSS APPLY ( SELECT OP.PROJECTION_DETAILS_SID , OP.CCP_DETAILS_SID , SUM(SP.PROJECTION_SALES) PROJECTION_SALES , SUM(SP.PROJECTION_UNITS) PROJECTION_UNITS , P.PERIOD_SID FROM ( SELECT TOP 1 PD.PROJECTION_DETAILS_SID , PD.CCP_DETAILS_SID FROM PROJECTION_MASTER PM INNER JOIN WORKFLOW_MASTER WM ON PM.PROJECTION_MASTER_SID = WM.PROJECTION_MASTER_SID AND WM.WORKFLOW_STATUS_ID = ( SELECT HELPER_TABLE_SID FROM HELPER_TABLE WHERE LIST_NAME = 'WORKFLOWSTATUS' AND DESCRIPTION = 'APPROVED' ) INNER JOIN PROJECTION_DETAILS PD ON WM.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID WHERE PD.CCP_DETAILS_SID = CCD.CCP_DETAILS_SID AND PM.FORECASTING_TYPE ='Non Mandated' ORDER BY WM.MODIFIED_DATE DESC ) OP INNER JOIN NM_SALES_PROJECTION SP ON OP.PROJECTION_DETAILS_SID = SP.PROJECTION_DETAILS_SID INNER JOIN PERIOD P ON SP.PERIOD_SID = P.PERIOD_SID WHERE P.PERIOD_DATE BETWEEN @START_DATE AND @END_DATE GROUP BY OP.CCP_DETAILS_SID , P.PERIOD_SID , OP.PROJECTION_DETAILS_SID ) CS END ELSE BEGIN INSERT INTO #TEMP ( CCP_DETAILS_SID , CONTRACT_MASTER_SID , COMPANY_MASTER_SID , ITEM_MASTER_SID , PERIOD_SID , PROJECTION_DISCOUNT , RS_CONTRACT_SID ) SELECT CCD.CCP_DETAILS_SID , cd.CONTRACT_MASTER_SID , cd.COMPANY_MASTER_SID , cd.ITEM_MASTER_SID , PERIOD_SID , COALESCE(PROJECTION_DISCOUNT, 0) AS PROJECTION_DISCOUNT , RS_CONTRACT_SID FROM @ACTUALS_CCP cd INNER JOIN CCP_DETAILS CCD ON CCD.CONTRACT_MASTER_SID = CD.CONTRACT_MASTER_SID AND CCD.COMPANY_MASTER_SID = CD.COMPANY_MASTER_SID AND CCD.ITEM_MASTER_SID = CD.ITEM_MASTER_SID CROSS APPLY ( SELECT OP.PROJECTION_DETAILS_SID , OP.CCP_DETAILS_SID , SUM(SP.PROJECTION_SALES) PROJECTION_DISCOUNT , SP.RS_CONTRACT_SID , P.PERIOD_SID FROM ( SELECT TOP 1 PD.PROJECTION_DETAILS_SID , PD.CCP_DETAILS_SID FROM PROJECTION_MASTER PM INNER JOIN WORKFLOW_MASTER WM ON PM.PROJECTION_MASTER_SID = WM.PROJECTION_MASTER_SID AND WM.WORKFLOW_STATUS_ID = ( SELECT HELPER_TABLE_SID FROM HELPER_TABLE WHERE LIST_NAME = 'WORKFLOWSTATUS' AND DESCRIPTION = 'APPROVED' ) INNER JOIN PROJECTION_DETAILS PD ON WM.PROJECTION_MASTER_SID = PD.PROJECTION_MASTER_SID WHERE PD.CCP_DETAILS_SID = CCD.CCP_DETAILS_SID AND PM.FORECASTING_TYPE ='Non Mandated' ORDER BY WM.MODIFIED_DATE DESC ) OP INNER JOIN NM_DISCOUNT_PROJECTION SP ON OP.PROJECTION_DETAILS_SID = SP.PROJECTION_DETAILS_SID INNER JOIN PERIOD P ON SP.PERIOD_SID = P.PERIOD_SID WHERE P.PERIOD_DATE BETWEEN @START_DATE AND @END_DATE GROUP BY OP.CCP_DETAILS_SID , P.PERIOD_SID , OP.PROJECTION_DETAILS_SID , SP.RS_CONTRACT_SID ) CS END
IF @SCREEN_NAME = 'Sales Projection' BEGIN SELECT SUM(SALES) AS ACTUAL_SALES , ISNULL(SUM(PROJECTION_SALES), 0) AS PROJECTION_SALES @?FILTER-FREQUENCY , YEAR , COMPANY_NO , COMPANY_NAME , CONTRACT_NO , CONTRACT_NAME , CCP_DETAILS_SID , ITEM_NO , ITEM_NAME , SUM(QUANTITY) AS ACTUAL_UNITS , ISNULL(SUM(PROJECTION_units), 0) AS PROJECTION_UNITS FROM ( SELECT DISTINCT SALES , T.PROJECTION_SALES , @?INNER-FREQUENCY , U.YEAR , CM.COMPANY_NO , CM.COMPANY_NAME , CON_M.CONTRACT_NO , CON_M.CONTRACT_NAME , CCP.CCP_DETAILS_SID , IM.ITEM_NO , IM.ITEM_NAME , u.QUANTITY , PROJECTION_units FROM UDF_ACTUALS_DETAILS(@ACTUALS_CCP, @END_DATE, @START_DATE) U INNER JOIN CONTRACT_MASTER CON_M ON CON_M.CONTRACT_MASTER_SID = U.CONTRACT_MASTER_SID INNER JOIN COMPANY_MASTER CM ON CM.COMPANY_MASTER_SID = U.COMPANY_MASTER_SID INNER JOIN ITEM_MASTER IM ON IM.ITEM_MASTER_SID = U.ITEM_MASTER_SID INNER JOIN CCP_DETAILS CCP ON CCP.COMPANY_MASTER_SID = U.COMPANY_MASTER_SID AND CCP.CONTRACT_MASTER_SID = U.CONTRACT_MASTER_SID AND CCP.ITEM_MASTER_SID = U.ITEM_MASTER_SID AND U.QUANTITY_INCLUSION = 'Y' LEFT JOIN #TEMP T ON T.COMPANY_MASTER_SID = U.COMPANY_MASTER_SID AND T.CONTRACT_MASTER_SID = U.CONTRACT_MASTER_SID AND T.ITEM_MASTER_SID = U.ITEM_MASTER_SID AND T.PERIOD_SID = U.PERIOD_SID ) a GROUP BY CCP_DETAILS_SID @?FILTER-FREQUENCY , YEAR , COMPANY_NO , COMPANY_NAME , CONTRACT_NO , CONTRACT_NAME , ITEM_NO , ITEM_NAME ORDER BY CCP_DETAILS_SID , YEAR @?FILTER-FREQUENCY END ELSE BEGIN SELECT SUM(DISCOUNT) AS ACTUAL_AMOUNT , ISNULL(SUM(PROJECTION_SALES), 0) AS PROJECTION_DISCOUNT @?FILTER-FREQUENCY , YEAR , COMPANY_NO , COMPANY_NAME , CONTRACT_NO , CONTRACT_NAME , CCP_DETAILS_SID , ITEM_NO , ITEM_NAME , RS_CONTRACT_SID FROM ( SELECT DISTINCT DISCOUNT , T.PROJECTION_SALES , @?INNER-FREQUENCY , U.YEAR , CM.COMPANY_NO , CM.COMPANY_NAME , CON_M.CONTRACT_NO , CON_M.CONTRACT_NAME , CCP.CCP_DETAILS_SID , IM.ITEM_NO , IM.ITEM_NAME , T.RS_CONTRACT_SID FROM UDF_ACTUALS_DETAILS(@ACTUALS_CCP, @END_DATE, @START_DATE) U LEFT JOIN #TEMP T ON T.COMPANY_MASTER_SID = U.COMPANY_MASTER_SID AND T.CONTRACT_MASTER_SID = U.CONTRACT_MASTER_SID AND T.ITEM_MASTER_SID = U.ITEM_MASTER_SID AND T.PERIOD_SID = U.PERIOD_SID INNER JOIN CONTRACT_MASTER CON_M ON CON_M.CONTRACT_MASTER_SID = U.CONTRACT_MASTER_SID INNER JOIN COMPANY_MASTER CM ON CM.COMPANY_MASTER_SID = U.COMPANY_MASTER_SID INNER JOIN ITEM_MASTER IM ON IM.ITEM_MASTER_SID = U.ITEM_MASTER_SID INNER JOIN CCP_DETAILS CCP ON CCP.COMPANY_MASTER_SID = U.COMPANY_MASTER_SID AND CCP.CONTRACT_MASTER_SID = U.CONTRACT_MASTER_SID AND CCP.ITEM_MASTER_SID = U.ITEM_MASTER_SID ) a GROUP BY YEAR @?FILTER-FREQUENCY , COMPANY_NO , COMPANY_NAME , CONTRACT_NO , CONTRACT_NAME , CCP_DETAILS_SID , ITEM_NO , ITEM_NAME , RS_CONTRACT_SID ORDER BY CCP_DETAILS_SID , RS_CONTRACT_SID , YEAR @?FILTER-FREQUENCY END

 
              
            DELETE SAHIS
            FROM
                    ST_AL_HISTORY_ITEM_SELECTION SAHIS;

