DECLARE @FREQUENCY CHAR(1) = '[$FREQUENCY]', @START_PERIOD INT = [$START_PERIOD], @START_YEAR INT = [$START_YEAR], @END_PERIOD INT = [$END_PERIOD], @END_YEAR INT = [$END_YEAR], @START_PERIOD_SID INT, @END_PERIOD_SID INT
SELECT @START_PERIOD_SID = MIN (PERIOD_SID), @END_PERIOD_SID = MAX(PERIOD_SID) FROM PERIOD WHERE ( CASE @FREQUENCY WHEN 'M' THEN MONTH WHEN 'S' THEN SEMI_ANNUAL WHEN 'Q' THEN QUARTER ELSE YEAR END = @START_PERIOD AND YEAR = @START_YEAR ) OR ( CASE @FREQUENCY WHEN 'M' THEN MONTH WHEN 'S' THEN SEMI_ANNUAL WHEN 'Q' THEN QUARTER ELSE YEAR END = @END_PERIOD AND YEAR = @END_YEAR );
WITH CTE AS (SELECT PERIOD_SID, PERIOD_DATE, MONTH, QUARTER, SEMI_ANNUAL, YEAR, PERIOD = CONCAT(CASE WHEN LEFT(@FREQUENCY, 1) = 'M' THEN MONTH WHEN LEFT(@FREQUENCY, 1) = 'Q' THEN QUARTER WHEN LEFT(@FREQUENCY, 1) = 'S' THEN SEMI_ANNUAL ELSE YEAR END,' ', YEAR) FROM PERIOD) SELECT DISTINCT CCP_DETAILS_SID FROM DBO.[$TABLE_NAME] ST JOIN CTE C ON C.PERIOD_SID = ST.PERIOD_SID WHERE ST.PERIOD_SID BETWEEN @START_PERIOD_SID AND @END_PERIOD_SID AND PERIOD IN ([$ALLOCATED_PERIODS]) AND ST.AVAILABLE_CHECKBOX = 1 GROUP BY CCP_DETAILS_SID, PERIOD, YEAR HAVING SUM(ISNULL(ST.ACTUAL_ALLOCATION_PERCENT, 0)) = 0 AND SUM(ISNULL(ST.PROJECTION_ALLOCATION_PERCENT, 0)) = 0;
