entity.query:
DECLARE @NA_PROJ_MASTER_SID INT=?PID, @BRAND_MASTER_SID INT=?BID, @TH_CLASS varchar(50)=?TID, @NDC9 varchar(50)=?NDC9
select A.TEMP_INDEX from (
SELECT IM.NDC9, IM.ITEM_DESC, ROW_NUMBER() OVER (ORDER BY IM.NDC9 ASC) AS TEMP_INDEX FROM NA_PROJ_MASTER PM, NA_PROJ_DETAILS PD, ITEM_MASTER IM WHERE PM.NA_PROJ_MASTER_SID = PD.NA_PROJ_MASTER_SID AND PD.ITEM_MASTER_SID = IM.ITEM_MASTER_SID AND PM.NA_PROJ_MASTER_SID = @NA_PROJ_MASTER_SID AND ( IM.BRAND_MASTER_SID = @BRAND_MASTER_SID OR @BRAND_MASTER_SID IS NULL ) AND ( IM.THERAPEUTIC_CLASS = @TH_CLASS OR @TH_CLASS IS NULL ) group by IM.NDC9,IM.ITEM_DESC ) A where A.NDC9=@NDC9;
