entity.query:
DECLARE @NA_PROJ_MASTER_SID INT=?PID, @BRAND_MASTER_SID INT=?BID, @TH_CLASS varchar(50)=?TID, @NA_ITEM_MASTER_SID INT=?IMD
select A.TEMP_INDEX from (
SELECT IM.ITEM_NO, IM.ITEM_MASTER_SID, IM.ITEM_DESC, ROW_NUMBER() OVER (ORDER BY IM.ITEM_MASTER_SID ASC) AS TEMP_INDEX FROM NA_PROJ_MASTER PM, NA_PROJ_DETAILS PD, ITEM_MASTER IM WHERE PM.NA_PROJ_MASTER_SID = PD.NA_PROJ_MASTER_SID AND PD.ITEM_MASTER_SID = IM.ITEM_MASTER_SID AND PM.NA_PROJ_MASTER_SID = @NA_PROJ_MASTER_SID AND ( IM.BRAND_MASTER_SID = @BRAND_MASTER_SID OR @BRAND_MASTER_SID IS NULL ) AND ( IM.THERAPEUTIC_CLASS = @TH_CLASS OR @TH_CLASS IS NULL )
) A where A.ITEM_MASTER_SID=@NA_ITEM_MASTER_SID;
