INSERT into #SELECTED_RElATION_HIERARCHY_NO( contract_name, contract_master_sid, HIERARCHY_NO ) SELECT 'a', RELATIONSHIP_LEVEL_DEFINITION.RELATIONSHIP_LEVEL_VALUES, RELATIONSHIP_LEVEL_DEFINITION.HIERARCHY_NO from dbo.RELATIONSHIP_LEVEL_DEFINITION as RELATIONSHIP_LEVEL_DEFINITION where RELATIONSHIP_BUILDER_SID = ? and LEVEL_NO = ? and VERSION_NO = ?;
INSERT into #SELECTED_SYSTEM_HIERARCHY_NO( contract_name, contract_master_sid, HIERARCHY_NO ) ?;
SET @VAR1=( SELECT COUNT(1) FROM #SELECTED_RELATION_HIERARCHY_NO A JOIN #SELECTED_SYSTEM_HIERARCHY_NO B ON A.HIERARCHY_NO LIKE B.HIERARCHY_NO+'%' )
IF ( (@VAR1=(SELECT COUNT(1) FROM #SELECTED_RELATION_HIERARCHY_NO) ) AND (@VAR1=(SELECT COUNT(1) FROM #SELECTED_SYSTEM_HIERARCHY_NO))) INSERT into #RELATIONISELIGIBLE SELECT 0 ELSE INSERT into #RELATIONISELIGIBLE SELECT 1 TRUNCATE TABLE #SELECTED_SYSTEM_HIERARCHY_NO TRUNCATE TABLE #SELECTED_RELATION_HIERARCHY_NO SET @VAR1 =0
