entity.query:
IF Object_id('TEMPDB..#SELECTED_CUST_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_CUST_HIERARCHY_NO CREATE TABLE #SELECTED_CUST_HIERARCHY_NO( contract_name VARCHAR(50), contract_master_sid VARCHAR(50), HIERARCHY_NO VARCHAR(50) ) INSERT into #SELECTED_CUST_HIERARCHY_NO(contract_name,contract_master_sid,HIERARCHY_NO) SELECT 'a',RELATIONSHIP_LEVEL_DEFINITION.RELATIONSHIP_LEVEL_VALUES,RELATIONSHIP_LEVEL_DEFINITION.HIERARCHY_NO from dbo.RELATIONSHIP_LEVEL_DEFINITION as RELATIONSHIP_LEVEL_DEFINITION where RELATIONSHIP_BUILDER_SID = ? and LEVEL_NO = ? and VERSION_NO =?;
IF Object_id('TEMPDB..#SELECTED_CUST_HIERARCHY_NO1') IS NOT NULL DROP TABLE #SELECTED_CUST_HIERARCHY_NO1 CREATE TABLE #SELECTED_CUST_HIERARCHY_NO1( contract_name VARCHAR(50), contract_master_sid VARCHAR(50), HIERARCHY_NO VARCHAR(50) ) INSERT into #SELECTED_CUST_HIERARCHY_NO1(contract_name,contract_master_sid,HIERARCHY_NO) ?;
IF EXISTS( select HIERARCHY_NO from #SELECTED_CUST_HIERARCHY_NO EXCEPT select HIERARCHY_NO from #SELECTED_CUST_HIERARCHY_NO1 UNION ALL (select HIERARCHY_NO from #SELECTED_CUST_HIERARCHY_NO1 EXCEPT select HIERARCHY_NO from #SELECTED_CUST_HIERARCHY_NO) ) BEGIN SELECT 1 END ELSE BEGIN SELECT 0 END
