entity.query:
DECLARE @USERS_SID VARCHAR(10)=?, @SESSION_ID VARCHAR(10)=?, @COMPANY_MASTER_SID INT=?
IF EXISTS( SELECT 1 FROM IMTD_CFP_DETAILS CFP WHERE CFP.USERS_SID = @USERS_SID AND CFP.SESSION_ID = @SESSION_ID AND COMPANY_MASTER_SID = @COMPANY_MASTER_SID ) UPDATE IMTD_CFP_DETAILS SET CFP_DETAILS_START_DATE = NULL, CFP_DETAILS_END_DATE = NULL, CFP_DETAILS_ATTACHED_DATE = GETDATE(), CFP_DETAILS_ATTACHED_STATUS = NULL, CFP_DETAILS_CREATED_DATE = GETDATE(), CFP_DETAILS_CREATED_BY = @USERS_SID, CFP_DETAILS_MODIFIED_DATE = NULL, CFP_DETAILS_MODIFIED_BY = NULL, OPERATION = 'A', TRADING_PARTNER_CONTRACT_NO=''
WHERE USERS_SID = @USERS_SID AND SESSION_ID = @SESSION_ID AND COMPANY_MASTER_SID = @COMPANY_MASTER_SID ELSE INSERT INTO IMTD_CFP_DETAILS( COMPANY_MASTER_SID, CFP_MODEL_SID, COMPANY_ID, COMPANY_NO, COMPANY_NAME, COMPANY_TYPE, COMPANY_STATUS, COMPANY_START_DATE, COMPANY_END_DATE, CFP_DETAILS_ATTACHED_DATE, USERS_SID, SESSION_ID, CFP_DETAILS_CREATED_DATE, CFP_DETAILS_CREATED_BY, CFP_DETAILS_MODIFIED_DATE, CFP_DETAILS_MODIFIED_BY, CFP_DETAILS_TRADE_CLASS, "OPERATION", CHECK_RECORD, TRADING_PARTNER_CONTRACT_NO ) SELECT CM.COMPANY_MASTER_SID, 0, CM.COMPANY_ID, CM.COMPANY_NO, CM.COMPANY_NAME, CM.COMPANY_TYPE, CM.COMPANY_STATUS, CM.COMPANY_START_DATE, CM.COMPANY_END_DATE, GETDATE(), @USERS_SID, @SESSION_ID, GETDATE(), @USERS_SID, NULL, NULL, CTC.COMPANY_TRADE_CLASS, 'A', 0, '' FROM DBO.COMPANY_MASTER CM JOIN COMPANY_TRADE_CLASS CTC ON CTC.COMPANY_MASTER_SID = CM.COMPANY_MASTER_SID LEFT JOIN HELPER_TABLE HTRADE ON HTRADE.HELPER_TABLE_SID = CTC.COMPANY_TRADE_CLASS WHERE CTC.COMPANY_TRADE_CLASS =( SELECT TOP 1(CT.COMPANY_TRADE_CLASS) FROM COMPANY_TRADE_CLASS CT WHERE CT.COMPANY_MASTER_SID = CM.COMPANY_MASTER_SID AND CT.INBOUND_STATUS <> 'D' ORDER BY CT.TRADE_CLASS_START_DATE DESC ) AND CM.INBOUND_STATUS <> 'D' AND CTC.INBOUND_STATUS <> 'D' AND CM.COMPANY_MASTER_SID = @COMPANY_MASTER_SID;

DECLARE @USERS_SID VARCHAR(10)=?, @SESSION_ID VARCHAR(10)=?
MERGE IMTD_CFP_DETAILS AS T USING( SELECT DISTINCT CM.COMPANY_MASTER_SID, CM.COMPANY_ID, CM.COMPANY_NO, CM.COMPANY_NAME, CM.COMPANY_TYPE, CM.COMPANY_STATUS, CM.COMPANY_CATEGORY, CM.COMPANY_GROUP, CM.COMPANY_START_DATE, CM.COMPANY_END_DATE, CM.ORGANIZATION_KEY, CM.LIVES, CM.FINANCIAL_SYSTEM, CM.ADDRESS1, CM.ADDRESS2, CM.CITY, CM.STATE, CM.ZIP_CODE, CM.COUNTRY REGION_CODE, CM.LAST_UPDATED_DATE, CM.INTERNAL_NOTES, CM.INBOUND_STATUS, CM.RECORD_LOCK_STATUS, CM.BATCH_ID, CM.SOURCE, CM.CREATED_BY, CM.CREATED_DATE, CM.MODIFIED_BY, CM.MODIFIED_DATE, CTC.COMPANY_TRADE_CLASS AS CFP_DETAILS_TRADE_CLASS FROM COMPANY_MASTER CM JOIN COMPANY_TRADE_CLASS CTC ON CM.COMPANY_MASTER_SID = CTC.COMPANY_MASTER_SID JOIN HELPER_TABLE htrade ON htrade.HELPER_TABLE_SID = CTC.COMPANY_TRADE_CLASS JOIN HELPER_TABLE HSTATUS ON HSTATUS.HELPER_TABLE_SID = CM.COMPANY_STATUS LEFT JOIN HELPER_TABLE HTYPE ON HTYPE.HELPER_TABLE_SID = COMPANY_TYPE WHERE CTC.COMPANY_TRADE_CLASS =( SELECT TOP 1(CT.COMPANY_TRADE_CLASS) FROM COMPANY_TRADE_CLASS CT WHERE CT.COMPANY_MASTER_SID = CM.COMPANY_MASTER_SID AND CT.INBOUND_STATUS <> 'D' ORDER BY CT.TRADE_CLASS_START_DATE DESC ) [$WHERE_CLAUSE] ) AS S ON ( S.COMPANY_MASTER_SID = T.COMPANY_MASTER_SID --AND T.CFP_MODEL_SID = AND T.USERS_SID = @USERS_SID AND T.SESSION_ID = @SESSION_ID ) WHEN MATCHED AND T.OPERATION IN( 'F', 'D' ) THEN UPDATE SET T.OPERATION = 'A', CFP_DETAILS_SID = 0, CFP_DETAILS_START_DATE = NULL, CFP_DETAILS_END_DATE = NULL, CFP_DETAILS_ATTACHED_DATE = GETDATE(), CFP_DETAILS_ATTACHED_STATUS = NULL, CFP_DETAILS_CREATED_DATE = GETDATE(), CFP_DETAILS_CREATED_BY = @USERS_SID, CFP_DETAILS_MODIFIED_DATE = GETDATE(), CFP_DETAILS_MODIFIED_BY = @USERS_SID, TRADING_PARTNER_CONTRACT_NO='' WHEN NOT MATCHED BY TARGET THEN INSERT ( COMPANY_MASTER_SID, COMPANY_ID, COMPANY_NO, COMPANY_NAME, COMPANY_TYPE, COMPANY_STATUS, COMPANY_START_DATE, COMPANY_END_DATE, CFP_DETAILS_TRADE_CLASS, CFP_DETAILS_ATTACHED_DATE, USERS_SID, SESSION_ID, IMTD_CREATED_DATE, CHECK_RECORD, "OPERATION", CFP_DETAILS_CREATED_BY, CFP_DETAILS_CREATED_DATE, CFP_DETAILS_SID ) VALUES( S.COMPANY_MASTER_SID, S.COMPANY_ID, S.COMPANY_NO, S.COMPANY_NAME, S.COMPANY_TYPE, S.COMPANY_STATUS, S.COMPANY_START_DATE, S.COMPANY_END_DATE, S.CFP_DETAILS_TRADE_CLASS, GETDATE(), @USERS_SID, @SESSION_ID, GETDATE(), 0, 'A', @USERS_SID, GETDATE(), 0 );
;

DECLARE @CFP_MODEL_SID INT =?, @USERS_SID VARCHAR(100) =?, @SESSION_ID VARCHAR(100) =?
UPDATE IMTD_CFP_DETAILS SET CFP_MODEL_SID=@CFP_MODEL_SID WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND CHECK_RECORD=1;
DELETE CFP FROM DBO.CFP_DETAILS CFP JOIN IMTD_CFP_DETAILS IMTD ON CFP.CFP_MODEL_SID=IMTD.CFP_MODEL_SID AND -- IMTD.CHECK_RECORD=1 AND IMTD.USERS_SID=@USERS_SID AND IMTD.SESSION_ID=@SESSION_ID;
DELETE FROM IMTD_CFP_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND CHECK_RECORD = 0;
INSERT INTO CFP_DETAILS( CFP_MODEL_SID, COMPANY_MASTER_SID, COMPANY_START_DATE, COMPANY_END_DATE, COMPANY_CFP_ATTACHED_STATUS, COMPANY_CFP_ATTACHED_DATE, TRADE_CLASS, TRADING_PARTNER_CONTRACT_NO, TRADE_CLASS_START_DATE, TRADE_CLASS_END_DATE, INBOUND_STATUS, RECORD_LOCK_STATUS, "SOURCE", CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE )
SELECT CFP_MODEL_SID, COMPANY_MASTER_SID, CFP_DETAILS_START_DATE, CFP_DETAILS_END_DATE, CFP_DETAILS_ATTACHED_STATUS, CFP_DETAILS_ATTACHED_DATE, CFP_DETAILS_TRADE_CLASS, TRADING_PARTNER_CONTRACT_NO, CFP_DETAILS_TC_START_DATE, CFP_DETAILS_TC_END_DATE, 'A', 0, 'GTN', CFP_DETAILS_CREATED_BY, CFP_DETAILS_CREATED_DATE, @USERS_SID, GETDATE() FROM IMTD_CFP_DETAILS WHERE CHECK_RECORD = 1 AND USERS_SID = @USERS_SID AND SESSION_ID = @SESSION_ID AND CFP_MODEL_SID = @CFP_MODEL_SID;
