entity.query:
DECLARE @USERS_SID VARCHAR(100) =?, @SESSION_ID VARCHAR(100) = ?, @COMPANY_GROUP_SID INT =?
DELETE FROM IMTD_COMPANY_GROUP_DETAILS WHERE USERS_ID=@USERS_SID AND SESSION_ID= @SESSION_ID
INSERT INTO IMTD_COMPANY_GROUP_DETAILS( COMPANY_GROUP_SID, COMPANY_MASTER_SID, COMPANY_TRADECLASS_SID, COMPANY_PARENT_DETAILS_SID, CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE, VERSION_NO, IMTD_CREATED_DATE, USERS_ID, SESSION_ID ) SELECT COMPANY_GROUP_SID, COMPANY_MASTER_SID, COMPANY_TRADECLASS_SID, COMPANY_PARENT_DETAILS_SID, CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE, VERSION_NO, GETDATE(), @USERS_SID, @SESSION_ID FROM COMPANY_GROUP_DETAILS WHERE COMPANY_GROUP_SID = @COMPANY_GROUP_SID;

DECLARE @USERS_SID VARCHAR(10) = ?, @SESSION_ID VARCHAR(10) = ?
MERGE IMTD_COMPANY_GROUP_DETAILS AS T USING( SELECT DISTINCT CM.COMPANY_MASTER_SID, CTC.COMPANY_TRADE_CLASS_SID, CPD.COMPANY_PARENT_DETAILS_SID [$MAIN_QUERY_CLAUSE] [$WHERE_CLAUSE] ) AS S ON ( S.COMPANY_MASTER_SID = T.COMPANY_MASTER_SID AND T.USERS_ID = @USERS_SID AND T.SESSION_ID = @SESSION_ID ) WHEN MATCHED THEN UPDATE SET MODIFIED_BY = @USERS_SID, MODIFIED_DATE = GETDATE(), VERSION_NO = T.VERSION_NO + 1, IMTD_CREATED_DATE = GETDATE() WHEN NOT MATCHED BY TARGET THEN INSERT
( COMPANY_GROUP_SID, COMPANY_MASTER_SID, COMPANY_TRADECLASS_SID, COMPANY_PARENT_DETAILS_SID, CREATED_BY, CREATED_DATE, VERSION_NO, IMTD_CREATED_DATE, USERS_ID, SESSION_ID ) VALUES( 0, S.COMPANY_MASTER_SID, S.COMPANY_TRADE_CLASS_SID, S.COMPANY_PARENT_DETAILS_SID, @USERS_SID, GETDATE(), 1, GETDATE(), @USERS_SID, @SESSION_ID ); ;
