entity.query:
MERGE COMPANY_FINANCIAL_CLOSE AS Target USING( select
? AS COMPANY_MASTER_SID, ? AS MODE , ? AS CALENDAR, PERIOD_SID AS PERIOD_SID, '' AS BUSINESS_DAY, '' AS HOUR, '' AS MINUTE, ? AS STATUS, ? AS STATUS_PERIOD_DATE, ? AS CREATED_BY, ? AS CREATED_DATE from PERIOD WHERE "YEAR"=? AND "MONTH"=? ) SOURCE ON Target.COMPANY_MASTER_SID =SOURCE.COMPANY_MASTER_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID
WHEN MATCHED THEN UPDATE SET COMPANY_MASTER_SID = SOURCE.COMPANY_MASTER_SID, MODE = SOURCE.MODE, CALENDAR = SOURCE.CALENDAR, PERIOD_SID = SOURCE.PERIOD_SID, BUSINESS_DAY =SOURCE.BUSINESS_DAY, HOUR =SOURCE.HOUR, MINUTE =SOURCE.MINUTE, STATUS = SOURCE.STATUS, STATUS_PERIOD_DATE =SOURCE.STATUS_PERIOD_DATE, CREATED_BY = SOURCE.CREATED_BY, CREATED_DATE =SOURCE.CREATED_DATE WHEN NOT MATCHED THEN INSERT ( COMPANY_MASTER_SID, MODE, CALENDAR, PERIOD_SID, BUSINESS_DAY, HOUR, MINUTE, STATUS, STATUS_PERIOD_DATE, CREATED_BY, CREATED_DATE ) VALUES(SOURCE.COMPANY_MASTER_SID, SOURCE. MODE, SOURCE. CALENDAR, SOURCE. PERIOD_SID, SOURCE. BUSINESS_DAY, SOURCE. HOUR, SOURCE. MINUTE, SOURCE. STATUS, SOURCE. STATUS_PERIOD_DATE, SOURCE. CREATED_BY, SOURCE. CREATED_DATE);
