entity.query:
DECLARE @USERS_SID VARCHAR(10)=?, @SESSION_ID VARCHAR(10)=?, @COMPANY_MASTER_SID INT=?
IF EXISTS( SELECT 1 FROM IMTD_CFP_DETAILS CFP WHERE CFP.USERS_SID = @USERS_SID AND CFP.SESSION_ID = @SESSION_ID AND COMPANY_MASTER_SID = @COMPANY_MASTER_SID ) UPDATE IMTD_CFP_DETAILS SET CFP_DETAILS_START_DATE = NULL, CFP_DETAILS_END_DATE = NULL, CFP_DETAILS_ATTACHED_DATE = GETDATE(), CFP_DETAILS_ATTACHED_STATUS = NULL, CFP_DETAILS_CREATED_DATE = GETDATE(), CFP_DETAILS_CREATED_BY = @USERS_SID, CFP_DETAILS_MODIFIED_DATE = NULL, CFP_DETAILS_MODIFIED_BY = NULL, OPERATION = 'A', TRADING_PARTNER_CONTRACT_NO=''
WHERE USERS_SID = @USERS_SID AND SESSION_ID = @SESSION_ID AND COMPANY_MASTER_SID = @COMPANY_MASTER_SID ELSE INSERT INTO IMTD_CFP_DETAILS( COMPANY_MASTER_SID, CFP_MODEL_SID, COMPANY_ID, COMPANY_NO, COMPANY_NAME, COMPANY_TYPE, COMPANY_STATUS, COMPANY_START_DATE, COMPANY_END_DATE, CFP_DETAILS_ATTACHED_DATE, USERS_SID, SESSION_ID, CFP_DETAILS_CREATED_DATE, CFP_DETAILS_CREATED_BY, CFP_DETAILS_MODIFIED_DATE, CFP_DETAILS_MODIFIED_BY, CFP_DETAILS_TRADE_CLASS, "OPERATION", CHECK_RECORD, TRADING_PARTNER_CONTRACT_NO ) SELECT CM.COMPANY_MASTER_SID, 0, CM.COMPANY_ID, CM.COMPANY_NO, CM.COMPANY_NAME, CM.COMPANY_TYPE, CM.COMPANY_STATUS, CM.COMPANY_START_DATE, CM.COMPANY_END_DATE, GETDATE(), @USERS_SID, @SESSION_ID, GETDATE(), @USERS_SID, GETDATE(), @USERS_SID, CTC.COMPANY_TRADE_CLASS, 'A', 0, '' FROM DBO.COMPANY_MASTER CM JOIN COMPANY_TRADE_CLASS CTC ON CTC.COMPANY_MASTER_SID = CM.COMPANY_MASTER_SID LEFT JOIN HELPER_TABLE HTRADE ON HTRADE.HELPER_TABLE_SID = CTC.COMPANY_TRADE_CLASS WHERE CTC.COMPANY_TRADE_CLASS =( SELECT TOP 1(CT.COMPANY_TRADE_CLASS) FROM COMPANY_TRADE_CLASS CT WHERE CT.COMPANY_MASTER_SID = CM.COMPANY_MASTER_SID AND CT.INBOUND_STATUS <> 'D' ORDER BY CT.TRADE_CLASS_START_DATE DESC ) AND CM.INBOUND_STATUS <> 'D' AND CTC.INBOUND_STATUS <> 'D' AND CM.COMPANY_MASTER_SID = @COMPANY_MASTER_SID;

SELECT IMTD.CHECK_RECORD, CASE WHEN CAST(PRICE_PROTECTION_START_DATE AS DATE) > convert(nvarchar, getdate(), 121) THEN 'Future' WHEN CAST(PRICE_PROTECTION_END_DATE AS DATE) < convert(nvarchar, getdate(), 121) THEN 'History' WHEN CAST(convert(nvarchar, getdate(), 121) AS DATE) BETWEEN CAST(PRICE_PROTECTION_START_DATE AS DATE) AND CAST(ISNULL(PRICE_PROTECTION_END_DATE, CAST(convert(nvarchar, getdate(), 121) AS DATE) ) AS DATE) THEN 'Current' END AS recordType, IMTD.ITEM_ID, IMTD.ITEM_NO, IMTD.ITEM_NAME, BM.BRAND_NAME, IMTD.PRICE_PROTECTION_STATUS, IMTD.PRICE_PROTECTION_START_DATE, IMTD.PRICE_PROTECTION_END_DATE, IMTD.PRICE_PROTECTION_PRICE_TYPE, NEP=CASE WHEN IMTD.NEP IS NULL THEN '' ELSE str(IMTD.NEP,16,6) END, NEP_FORMULA_VAL=CASE WHEN IMTD.NEP_FORMULA IS NULL THEN '' ELSE NEPF.NET_SALES_FORMULA_NAME END, IMTD.BASE_PRICE_TYPE, basePriceVal=CASE WHEN BPH.DESCRIPTION LIKE 'M%' THEN str(ISNULL(CONVERT(VARCHAR(20),IMTD.BASE_PRICE_ENTRY),''),16,2) WHEN BPH.DESCRIPTION LIKE 'P%' THEN ISNULL(CONVERT(VARCHAR(20),IMTD.BASE_PRICE_DDLB),'') WHEN BPH.DESCRIPTION LIKE 'D%' THEN ISNULL(CONVERT(VARCHAR(10),IMTD.BASE_PRICE_DATE,101),'') ELSE '' END, IMTD.NET_BASE_PRICE, NET_BASE_PRICE_FORMULA_VAL=CASE WHEN IMTD.NET_BASE_PRICE_FORMULA_ID IS NULL THEN '' ELSE NBPF.NET_SALES_FORMULA_NAME END, IMTD.SUBSEQUENT_PERIOD_PRICE_TYPE, IMTD.NET_SUBSEQUENT_PERIOD_PRICE, NET_SUBSEQUENT_PRICE_FORMULA_VAL=CASE WHEN IMTD.NET_SUBSEQUENT_PRICE_FORMULA_ID IS NULL THEN '' ELSE NSPF.NET_SALES_FORMULA_NAME END, IMTD.PRICE_TOLERANCE_INTERVAL, IMTD.PRICE_TOLERANCE_FREQUENCY, IMTD.PRICE_TOLERANCE_TYPE,
PRICE_TOLERANCE=CASE WHEN IMTD.PRICE_TOLERANCE IS NULL THEN '' ELSE str(IMTD.PRICE_TOLERANCE,16,2) END, MAX_INCREMENTAL_CHANGE=CASE WHEN IMTD.MAX_INCREMENTAL_CHANGE IS NULL THEN '' ELSE str(IMTD.MAX_INCREMENTAL_CHANGE,16,2) END, IMTD.RESET_ELIGIBLE, IMTD.RESET_TYPE, IMTD.RESET_DATE, IMTD.RESET_INTERVAL, IMTD.RESET_FREQUENCY, IMTD.RESET_PRICE_TYPE, IMTD.NET_RESET_PRICE_TYPE, NET_RESET_PRICE_FORMULA_VAL=CASE WHEN IMTD.NET_RESET_PRICE_FORMULA_ID IS NULL THEN '' ELSE NRPF.NET_SALES_FORMULA_NAME END, IMTD.NET_PRICE_TYPE, NET_PRICE_TYPE_FORMULA_VAL=CASE WHEN IMTD.NET_PRICE_TYPE_FORMULA IS NULL THEN '' ELSE NPTF.NET_SALES_FORMULA_NAME END, IMTD.ATTACHED_DATE, IMTD.IMTD_ITEM_PRICE_REBATE_SID, IMTD.ITEM_MASTER_SID, IMTD.NEP_FORMULA, IMTD.NET_BASE_PRICE_FORMULA_ID, IMTD.NET_SUBSEQUENT_PRICE_FORMULA_ID, IMTD.NET_RESET_PRICE_FORMULA_ID, IMTD.NET_PRICE_TYPE_FORMULA, IMTD.BASE_PRICE_DDLB, BASE_PRICE_ENTRY=CASE WHEN IMTD.BASE_PRICE_ENTRY IS NULL THEN '' ELSE str(IMTD.BASE_PRICE_ENTRY,16,2) END, PTH.DESCRIPTION as PRICE_TOLERANCE_TYPE_DES3, PTH.DESCRIPTION as PRICE_TOLERANCE_TYPE_DES4, PTH.DESCRIPTION as PRICE_TOLERANCE_TYPE_DES5, BPH.DESCRIPTION, PTH.DESCRIPTION as PRICE_TOLERANCE_TYPE_DES, PTH.DESCRIPTION as PRICE_TOLERANCE_TYPE_DES1, PTH.DESCRIPTION as PRICE_TOLERANCE_TYPE_DES2, BPH.DESCRIPTION AS BDES, IMTD.BASE_PRICE_DDLB AS IBPDDLB, IMTD.BASE_PRICE_DATE AS IBPD, BASE_PRICE_ENTRYYY=CASE WHEN IMTD.BASE_PRICE_ENTRY IS NULL THEN '' ELSE str(IMTD.BASE_PRICE_ENTRY,16,2) END
FROM IMTD_ITEM_PRICE_REBATE_DETAILS IMTD LEFT JOIN BRAND_MASTER BM ON IMTD.BRAND_MASTER_SID = BM.BRAND_MASTER_SID LEFT JOIN NET_SALES_FORMULA_MASTER NEPF ON NEPF.NET_SALES_FORMULA_MASTER_SID=IMTD.NEP_FORMULA LEFT JOIN NET_SALES_FORMULA_MASTER NBPF ON NBPF.NET_SALES_FORMULA_MASTER_SID=IMTD.NET_BASE_PRICE_FORMULA_ID LEFT JOIN NET_SALES_FORMULA_MASTER NSPF ON NSPF.NET_SALES_FORMULA_MASTER_SID=IMTD.NET_SUBSEQUENT_PRICE_FORMULA_ID LEFT JOIN NET_SALES_FORMULA_MASTER NRPF ON NRPF.NET_SALES_FORMULA_MASTER_SID=IMTD.NET_RESET_PRICE_FORMULA_ID LEFT JOIN NET_SALES_FORMULA_MASTER NPTF ON NPTF.NET_SALES_FORMULA_MASTER_SID=IMTD.NET_PRICE_TYPE_FORMULA LEFT JOIN HELPER_TABLE BPH ON BPH.HELPER_TABLE_SID=IMTD.BASE_PRICE_TYPE LEFT JOIN HELPER_TABLE PTH ON PTH.HELPER_TABLE_SID=IMTD.PRICE_TOLERANCE_TYPE WHERE OPERATION NOT IN ('D','F') and IMTD.USERS_SID = '?' and IMTD.SESSION_ID = '?' ? ORDER BY ? ,IMTD.IMTD_ITEM_PRICE_REBATE_SID ASC OFFSET ? ROWS FETCH NEXT ? ROWS ONLY;

INSERT INTO dbo.IMTD_ITEM_PRICE_REBATE_DETAILS (ITEM_MASTER_SID, ITEM_ID, ITEM_NO, ITEM_NAME, PS_STATUS,PRIMARY_UOM,PACKAGE_SIZE, CONTRACT_MASTER_SID, CFP_MODEL_SID, IFP_MODEL_SID, PS_MODEL_SID, RS_MODEL_SID, FORMULA_ID, REBATE_AMOUNT, RS_DETAILS_SID, ITEM_REBATE_START_DATE, ITEM_REBATE_END_DATE, REBATE_PLAN_SYSTEM_ID, RSCD.BUNDLE_NO, USERS_SID, SESSION_ID, CHECK_RECORD, IMTD_CREATED_DATE, "OPERATION", CREATED_BY, CREATED_DATE, ATTACHED_DATE, MODIFIED_DATE) SELECT DISTINCT IM.ITEM_MASTER_SID, IM.ITEM_ID, IM.ITEM_NO, IM.ITEM_NAME, IM.ITEM_STATUS,IM.PRIMARY_UOM,IM.PACKAGE_SIZE, RSC.CONTRACT_MASTER_SID, RSC.CFP_CONTRACT_SID, RSC.IFP_CONTRACT_SID, RSC.PS_CONTRACT_SID, RSC.RS_MODEL_SID,RSCD.FORMULA_ID, RSCD.REBATE_AMOUNT, RSCD.RS_CONTRACT_DETAILS_SID, RSCD.ITEM_REBATE_START_DATE, RSCD.ITEM_REBATE_END_DATE, RSCD.REBATE_PLAN_MASTER_SID, RSCD.BUNDLE_NO, '?' USERS_SID, '?' SESSION_ID, 0 CHECK_RECORD, '?' IMTD_CREATED_DATE, 'U' "Operation", RSCD.CREATED_BY, RSCD.CREATED_DATE , RSCD.RS_CONTRACT_ATTACHED_DATE, RSCD.MODIFIED_DATE FROM RS_CONTRACT RSC, RS_CONTRACT_DETAILS RSCD, ITEM_MASTER IM WHERE RSC.CONTRACT_MASTER_SID ? AND RSC.CFP_CONTRACT_SID ? AND RSC.IFP_CONTRACT_SID is null AND RSC.PS_CONTRACT_SID is null AND RSC.RS_CONTRACT_SID ?
AND RSC.RS_CONTRACT_SID=RSCD.RS_CONTRACT_SID AND RSCD.ITEM_MASTER_SID = IM.ITEM_MASTER_SID
AND IM.INBOUND_STATUS <> 'D' AND RSCD.INBOUND_STATUS <> 'D';

INSERT INTO dbo.IMTD_ITEM_PRICE_REBATE_DETAILS (ITEM_MASTER_SID, ITEM_ID, ITEM_NO, ITEM_NAME,PRIMARY_UOM,PACKAGE_SIZE, CONTRACT_MASTER_SID, PS_MODEL_SID, CONTRACT_PRICE, CONTRACT_PRICE_START_DATE, CONTRACT_PRICE_END_DATE, PRICE_PROTECTION_START_DATE, PRICE_PROTECTION_END_DATE, PRICE_TOLERANCE_TYPE, PRICE_TOLERANCE, BASE_PRICE, PRICE_TOLERANCE_FREQUENCY, PRICE_TOLERANCE_INTERVAL, PRICE_TYPE, Price, PS_STATUS, PS_DETAILS_SID, PRICE_PROTECTION_STATUS, BRAND_MASTER_SID, PRICE_PROTECTION_PRICE_TYPE, NEP, NEP_FORMULA, MAX_INCREMENTAL_CHANGE, RESET_ELIGIBLE, RESET_TYPE, RESET_DATE, RESET_INTERVAL, RESET_FREQUENCY, NET_PRICE_TYPE, NET_PRICE_TYPE_FORMULA, USERS_SID, SESSION_ID, CHECK_RECORD, IMTD_CREATED_DATE, "Operation", CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE,"SOURCE",SUGGESTED_PRICE, ATTACHED_DATE) SELECT Distinct IM.ITEM_MASTER_SID, IM.ITEM_ID, IM.ITEM_NO, IM.ITEM_NAME,IM.PRIMARY_UOM,IM.PACKAGE_SIZE, PSC.CONTRACT_MASTER_SID, PSC.PS_MODEL_SID, PS.CONTRACT_PRICE, PS.CONTRACT_PRICE_START_DATE, PS.CONTRACT_PRICE_END_DATE, PS.PRICE_PROTECTION_START_DATE, PS.PRICE_PROTECTION_END_DATE, PS.PRICE_TOLERANCE_TYPE, PS.PRICE_TOLERANCE, PS.BASE_PRICE, PS.PRICE_TOLERANCE_FREQUENCY, PS.PRICE_TOLERANCE_INTERVAL, PS.ITEM_PRICING_QUALIFIER_SID, PS.PRICE, PS.PS_CONTRACT_ATTACHED_STATUS, PS.PS_CONTRACT_DETAILS_SID, PS.PRICE_PROTECTION_STATUS,PS.BRAND_MASTER_SID,PS.PRICE_PROTECTION_PRICE_TYPE,PS.NEP,PS.NEP_FORMULA, PS.MAX_INCREMENTAL_CHANGE,PS.RESET_ELIGIBLE,PS.RESET_TYPE,PS.RESET_DATE,PS.RESET_INTERVAL,PS.RESET_FREQUENCY,PS.NET_PRICE_TYPE,PS.NET_PRICE_TYPE_FORMULA, ? USERS_SID, '?' SESSION_ID, 0 CHECK_RECORD, '?' IMTD_CREATED_DATE, 'U' "Operation", PS.CREATED_BY, PS.CREATED_DATE, PS.MODIFIED_BY, PS.MODIFIED_DATE,PS."SOURCE",PS.SUGGESTED_PRICE , PS.PS_CONTRACT_ATTACHED_DATE FROM PS_CONTRACT_DETAILS PS, PS_CONTRACT PSC, ITEM_MASTER IM WHERE PSC.CONTRACT_MASTER_SID ? AND PSC.CFP_CONTRACT_SID ? AND PSC.IFP_CONTRACT_SID is null AND PSC.PS_CONTRACT_SID ?
AND PS.ITEM_MASTER_SID = IM.ITEM_MASTER_SID AND PSC.PS_CONTRACT_SID = PS.PS_CONTRACT_SID
AND IM.INBOUND_STATUS <> 'D' AND PSC.INBOUND_STATUS <> 'D' AND PS.INBOUND_STATUS <> 'D';

