AND RSC.RS_CONTRACT_SID=RSCD.RS_CONTRACT_SID AND RSCD.ITEM_MASTER_SID = IM.ITEM_MASTER_SID AND PSCD.ITEM_MASTER_SID = IM.ITEM_MASTER_SID
AND IM.INBOUND_STATUS <> 'D' AND PSCD.INBOUND_STATUS <> 'D' AND RSCD.INBOUND_STATUS <> 'D';


DECLARE @USERS_SID VARCHAR(15)=?,
    @SESSION_ID VARCHAR(15)=?
UPDATE CFP_CONTRACT_DETAILS SET INBOUND_STATUS='D' WHERE CFP_CONTRACT_DETAILS_SID in
(SELECT CFP_DETAILS_SID FROM dbo.IMTD_CFP_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND (CHECK_RECORD=0 OR "Operation" = 'D'));


DECLARE @USERS_SID VARCHAR(15)=?,
    @SESSION_ID VARCHAR(15)=?
DELETE FROM dbo.IMTD_CFP_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND (CHECK_RECORD=0 OR "Operation" = 'D');


DECLARE @USERS_SID VARCHAR(15)=?,
    @SESSION_ID VARCHAR(15)=?
UPDATE IFP_CONTRACT_DETAILS SET INBOUND_STATUS='D' WHERE IFP_CONTRACT_DETAILS_SID in
(SELECT IFP_DETAILS_SID FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND (CHECK_RECORD=0 OR "Operation" = 'D'));


DECLARE @USERS_SID VARCHAR(15)=?,
    @SESSION_ID VARCHAR(15)=?
DELETE FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND (CHECK_RECORD=0 OR "Operation" = 'D');


DECLARE @USERS_SID VARCHAR(15)=?,
    @SESSION_ID VARCHAR(15)=?
UPDATE PS_CONTRACT_DETAILS SET INBOUND_STATUS='D' WHERE PS_CONTRACT_DETAILS_SID in
(SELECT PS_DETAILS_SID FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND (CHECK_RECORD=0 OR "Operation" = 'D'));


DECLARE @USERS_SID VARCHAR(15)=?,
    @SESSION_ID VARCHAR(15)=?
DELETE FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND (CHECK_RECORD=0 OR "Operation" = 'D');


DECLARE @USERS_SID VARCHAR(15)=?,
    @SESSION_ID VARCHAR(15)=?
UPDATE RS_CONTRACT_DETAILS SET INBOUND_STATUS='D' WHERE RS_CONTRACT_DETAILS_SID in
(SELECT RS_DETAILS_SID FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND (CHECK_RECORD=0 OR "Operation" = 'D'));


DECLARE @USERS_SID VARCHAR(15)=?,
    @SESSION_ID VARCHAR(15)=?
DELETE FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND (CHECK_RECORD=0 OR "Operation" = 'D');

DECLARE @USERS_SID VARCHAR(15)=?, @SESSION_ID VARCHAR(15)=?, @RS_CONTRACT_ID INT=? MERGE dbo.RS_CONTRACT_DETAILS AS Target USING (SELECT REBATE_AMOUNT, ITEM_REBATE_START_DATE, ITEM_REBATE_END_DATE, RS_DETAILS_SID, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY, BUNDLE_NO, REBATE_PLAN_SYSTEM_ID,FORMULA_ID, RS_ATTACHED_STATUS,RS_ATTACHED_DATE, DEDUCTION_CALENDAR_MASTER_SID, NET_SALES_FORMULA_MASTER_SID, FORMULA_TYPE, NET_SALES_RULE, EVALUATION_RULE, EVALUATION_RULE_BUNDLE, CALCULATION_RULE, CALCULATION_RULE_BUNDLE, ITEM_MASTER_SID FROM dbo.IMTD_ITEM_PRICE_REBATE_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND CHECK_RECORD=1 AND ( "Operation" = 'U' OR "Operation" = 'A' )) AS Source ON (Target.RS_CONTRACT_SID = @RS_CONTRACT_ID AND TARGET.ITEM_MASTER_SID = SOURCE.ITEM_MASTER_SID ) WHEN MATCHED THEN UPDATE SET
Target.ITEM_REBATE_START_DATE = Source.ITEM_REBATE_START_DATE, Target.ITEM_REBATE_END_DATE = Source.ITEM_REBATE_END_DATE, Target.REBATE_AMOUNT = Source.REBATE_AMOUNT,
Target.CREATED_DATE = Source.CREATED_DATE, Target.CREATED_BY = Source.CREATED_BY, Target.MODIFIED_DATE = Source.MODIFIED_DATE, Target.MODIFIED_BY = Source.MODIFIED_BY, Target.BUNDLE_NO = Source.BUNDLE_NO, Target.REBATE_PLAN_MASTER_SID = Source.REBATE_PLAN_SYSTEM_ID, Target.FORMULA_ID=Source.FORMULA_ID, Target.RS_CONTRACT_ATTACHED_STATUS = Source.RS_ATTACHED_STATUS, Target.RS_CONTRACT_ATTACHED_DATE = Source.RS_ATTACHED_DATE, Target.DEDUCTION_CALENDAR_MASTER_SID = Source.DEDUCTION_CALENDAR_MASTER_SID, Target.NET_SALES_FORMULA_MASTER_SID = Source.NET_SALES_FORMULA_MASTER_SID, Target.FORMULA_TYPE = Source.FORMULA_TYPE, Target.NET_SALES_RULE = Source.NET_SALES_RULE, Target.EVALUATION_RULE = Source.EVALUATION_RULE, Target.EVALUATION_RULE_BUNDLE = Source.EVALUATION_RULE_BUNDLE, Target.CALCULATION_RULE = Source.CALCULATION_RULE, Target.CALCULATION_RULE_BUNDLE = Source.CALCULATION_RULE_BUNDLE, Target.INBOUND_STATUS = 'C' WHEN NOT MATCHED THEN INSERT (RS_CONTRACT_SID, ITEM_MASTER_SID, ITEM_REBATE_START_DATE, REBATE_AMOUNT, INBOUND_STATUS, RECORD_LOCK_STATUS, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY, BUNDLE_NO, REBATE_PLAN_MASTER_SID,FORMULA_ID, RS_CONTRACT_ATTACHED_STATUS, DEDUCTION_CALENDAR_MASTER_SID, NET_SALES_FORMULA_MASTER_SID, FORMULA_TYPE, NET_SALES_RULE, EVALUATION_RULE, EVALUATION_RULE_BUNDLE, CALCULATION_RULE, CALCULATION_RULE_BUNDLE) Values( @RS_CONTRACT_ID , SOURCE.ITEM_MASTER_SID, SOURCE.ITEM_REBATE_START_DATE, SOURCE.REBATE_AMOUNT, 'A' ,'false' , SOURCE.CREATED_DATE, SOURCE.CREATED_BY, SOURCE.MODIFIED_DATE, SOURCE.MODIFIED_BY, SOURCE.BUNDLE_NO, SOURCE.REBATE_PLAN_SYSTEM_ID,SOURCE.FORMULA_ID, SOURCE.RS_ATTACHED_STATUS, SOURCE.DEDUCTION_CALENDAR_MASTER_SID, SOURCE.NET_SALES_FORMULA_MASTER_SID, SOURCE.FORMULA_TYPE, SOURCE.NET_SALES_RULE, SOURCE.EVALUATION_RULE, SOURCE.EVALUATION_RULE_BUNDLE, SOURCE.CALCULATION_RULE, SOURCE.CALCULATION_RULE_BUNDLE );;


DECLARE @CONTRACT_MASTER_ID INT = ?
UPDATE CFP_CONTRACT set INBOUND_STATUS='D' WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID;
UPDATE IFP_CONTRACT set INBOUND_STATUS='D' WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID;
UPDATE PS_CONTRACT set INBOUND_STATUS='D' WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID;
UPDATE RS_CONTRACT set INBOUND_STATUS='D' WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID;
UPDATE CFP_CONTRACT_DETAILS set INBOUND_STATUS='D' WHERE CFP_CONTRACT_SID in (SELECT CFP_CONTRACT_SID FROM CFP_CONTRACT WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID);
UPDATE IFP_CONTRACT_DETAILS set INBOUND_STATUS='D' WHERE IFP_CONTRACT_SID in (SELECT IFP_CONTRACT_SID FROM IFP_CONTRACT WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID);
UPDATE PS_CONTRACT_DETAILS set INBOUND_STATUS='D' WHERE PS_CONTRACT_SID in (SELECT PS_CONTRACT_SID FROM PS_CONTRACT WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID);
UPDATE RS_CONTRACT_DETAILS set INBOUND_STATUS='D' WHERE RS_CONTRACT_SID in (SELECT RS_CONTRACT_SID FROM RS_CONTRACT WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID);
UPDATE dbo.RS_CONTRACT_DETAILS_FR set INBOUND_STATUS='D' WHERE RS_CONTRACT_DETAILS_SID in (SELECT RS_CONTRACT_DETAILS_SID FROM RS_CONTRACT_DETAILS WHERE RS_CONTRACT_SID in (SELECT RS_CONTRACT_SID FROM RS_CONTRACT WHERE CONTRACT_MASTER_SID=@CONTRACT_MASTER_ID));

DECLARE @IFP_CONTRACT_ID INT=? MERGE IFP_CONTRACT_DETAILS AS Target USING (SELECT ITEM_STATUS, ITEM_START_DATE, ITEM_END_DATE, IFP_DETAILS_ATTACHED_DATE, IFP_DETAILS_SID, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY,ITEM_MASTER_SID FROM dbo.IMTD_IFP_DETAILS_PENDING
WHERE IFP_CONTRACT_SID = @IFP_CONTRACT_ID
) AS SOURCE ON (Target.IFP_CONTRACT_SID = @IFP_CONTRACT_ID AND TARGET.ITEM_MASTER_SID = SOURCE.ITEM_MASTER_SID
) WHEN MATCHED THEN UPDATE SET Target.ITEM_STATUS = Source.ITEM_STATUS, Target.ITEM_START_DATE = Source.ITEM_START_DATE, Target.ITEM_END_DATE = Source.ITEM_END_DATE, Target.CREATED_DATE = Source.CREATED_DATE, Target.IFP_CONTRACT_ATTACHED_STATUS = Source.ITEM_STATUS, Target.IFP_CONTRACT_ATTACHED_DATE = Source.IFP_DETAILS_ATTACHED_DATE, Target.CREATED_BY = Source.CREATED_BY, Target.MODIFIED_DATE = Source.MODIFIED_DATE, Target.MODIFIED_BY = Source.MODIFIED_BY, Target.INBOUND_STATUS = 'U' WHEN NOT MATCHED THEN INSERT (IFP_CONTRACT_SID, ITEM_MASTER_SID, ITEM_STATUS, ITEM_START_DATE, ITEM_END_DATE,INBOUND_STATUS, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY, IFP_CONTRACT_ATTACHED_STATUS,IFP_CONTRACT_ATTACHED_DATE) Values( @IFP_CONTRACT_ID , SOURCE.ITEM_MASTER_SID, SOURCE.ITEM_STATUS, SOURCE.ITEM_START_DATE, SOURCE.ITEM_END_DATE, 'A' , SOURCE.CREATED_DATE, SOURCE.CREATED_BY, SOURCE.MODIFIED_DATE, SOURCE.MODIFIED_BY, SOURCE.ITEM_STATUS,GETDATE());;

DECLARE @RS_CONTRACT_ID INT=? MERGE dbo.RS_CONTRACT_DETAILS AS Target USING
(select * from dbo.RS_CONTRACT_DETAILS_PENDING where RS_CONTRACT_SID = @RS_CONTRACT_ID)
AS Source ON (TARGET.RS_CONTRACT_SID = SOURCE.RS_CONTRACT_SID AND TARGET.ITEM_MASTER_SID = SOURCE.ITEM_MASTER_SID
) WHEN MATCHED AND TARGET.RS_CONTRACT_SID = @RS_CONTRACT_ID THEN UPDATE SET
Target.ITEM_REBATE_START_DATE = Source.ITEM_REBATE_START_DATE, Target.ITEM_REBATE_END_DATE = Source.ITEM_REBATE_END_DATE, Target.REBATE_AMOUNT = Source.REBATE_AMOUNT,
Target.CREATED_DATE = Source.CREATED_DATE, Target.CREATED_BY = Source.CREATED_BY, Target.MODIFIED_DATE = Source.MODIFIED_DATE, Target.MODIFIED_BY = Source.MODIFIED_BY, Target.BUNDLE_NO = Source.BUNDLE_NO, Target.REBATE_PLAN_MASTER_SID = Source.REBATE_PLAN_MASTER_SID, Target.FORMULA_ID=Source.FORMULA_ID, Target.RS_CONTRACT_ATTACHED_STATUS = Source.RS_CONTRACT_ATTACHED_STATUS, Target.RS_CONTRACT_ATTACHED_DATE = Source.RS_CONTRACT_ATTACHED_DATE, Target.DEDUCTION_CALENDAR_MASTER_SID = Source.DEDUCTION_CALENDAR_MASTER_SID, Target.NET_SALES_FORMULA_MASTER_SID = Source.NET_SALES_FORMULA_MASTER_SID, Target.FORMULA_TYPE = Source.FORMULA_TYPE, Target.NET_SALES_RULE = Source.NET_SALES_RULE, Target.EVALUATION_RULE = Source.EVALUATION_RULE, Target.EVALUATION_RULE_BUNDLE = Source.EVALUATION_RULE_BUNDLE, Target.CALCULATION_RULE = Source.CALCULATION_RULE, Target.CALCULATION_RULE_BUNDLE = Source.CALCULATION_RULE_BUNDLE, Target.INBOUND_STATUS = 'C' WHEN NOT MATCHED BY TARGET THEN INSERT (RS_CONTRACT_SID, ITEM_MASTER_SID, ITEM_REBATE_START_DATE,ITEM_REBATE_END_DATE, REBATE_AMOUNT, INBOUND_STATUS, RECORD_LOCK_STATUS, CREATED_DATE, CREATED_BY, MODIFIED_DATE, MODIFIED_BY, BUNDLE_NO, REBATE_PLAN_MASTER_SID,FORMULA_ID, RS_CONTRACT_ATTACHED_STATUS, DEDUCTION_CALENDAR_MASTER_SID, NET_SALES_FORMULA_MASTER_SID, FORMULA_TYPE, NET_SALES_RULE, EVALUATION_RULE, EVALUATION_RULE_BUNDLE, CALCULATION_RULE, CALCULATION_RULE_BUNDLE) Values( source.RS_CONTRACT_SID , SOURCE.ITEM_MASTER_SID, SOURCE.ITEM_REBATE_START_DATE,source.ITEM_REBATE_END_DATE, SOURCE.REBATE_AMOUNT, 'A' ,0 , SOURCE.CREATED_DATE, SOURCE.CREATED_BY, SOURCE.MODIFIED_DATE, SOURCE.MODIFIED_BY, SOURCE.BUNDLE_NO, SOURCE.REBATE_PLAN_MASTER_SID,SOURCE.FORMULA_ID, SOURCE.RS_CONTRACT_ATTACHED_STATUS, SOURCE.DEDUCTION_CALENDAR_MASTER_SID, SOURCE.NET_SALES_FORMULA_MASTER_SID, SOURCE.FORMULA_TYPE, SOURCE.NET_SALES_RULE, SOURCE.EVALUATION_RULE, SOURCE.EVALUATION_RULE_BUNDLE, SOURCE.CALCULATION_RULE, SOURCE.CALCULATION_RULE_BUNDLE );;

DECLARE @RS_CONTRACT_ID int = ? SELECT IMTD.CHECK_RECORD, recordType = '?', IM.ITEM_NO, IM.ITEM_NAME, itemType = CASE WHEN hitp.DESCRIPTION LIKE '%Select One%' THEN '' ELSE hitp.DESCRIPTION END, IM.ITEM_ID, RS_CONTRACT_ATTACHED_STATUS, IMTD.ITEM_REBATE_START_DATE, IMTD.ITEM_REBATE_END_DATE, BUNDLE_NO_VAL = CASE WHEN IMTD.BUNDLE_NO IS NULL THEN '' ELSE IMTD.BUNDLE_NO END, REBATE_PLAN_NO_VAL = CASE WHEN IMTD.REBATE_PLAN_MASTER_SID IS NULL THEN '' ELSE RPM.REBATE_PLAN_NO END, REBATE_PLAN_NAME_VAL = CASE WHEN IMTD.REBATE_PLAN_MASTER_SID IS NULL THEN '' ELSE RPM.REBATE_PLAN_NAME END,
DEDUCTION_CALENDAR_NO_VAL = CASE WHEN IMTD.DEDUCTION_CALENDAR_MASTER_SID IS NULL THEN '' ELSE DC.DEDUCTION_CALENDAR_NO END, DEDUCTION_CALENDAR_NAME_VAL = CASE WHEN IMTD.DEDUCTION_CALENDAR_MASTER_SID IS NULL THEN '' ELSE DC.DEDUCTION_CALENDAR_NAME END, FF.FORMULA_NAME, NET_SALE_FORMULA_NO_VAL = CASE WHEN IMTD.NET_SALES_FORMULA_MASTER_SID IS NULL THEN '' ELSE NSFM.NET_SALES_FORMULA_NO END, NET_SALES_RULE_VAL = CASE WHEN IMTD.NET_SALES_RULE IS NULL THEN '' ELSE NET.RULE_NAME END, EVA_RULE_VAL = CASE WHEN IMTD.EVALUATION_RULE IS NULL THEN '' ELSE EVA.RULE_NAME END, EVALUATION_RULE_BUNDLE = CASE WHEN IMTD.EVALUATION_RULE_BUNDLE IS NULL THEN '' ELSE IMTD.EVALUATION_RULE_BUNDLE END, CAL_RULE_VAL = CASE WHEN IMTD.CALCULATION_RULE IS NULL THEN '' ELSE CAL.RULE_NAME END, CALCULATION_RULE_BUNDLE = CASE WHEN IMTD.CALCULATION_RULE_BUNDLE IS NULL THEN '' ELSE IMTD.CALCULATION_RULE_BUNDLE END, IMTD.RS_CONTRACT_ATTACHED_DATE, IMTD.RS_CONTRACT_DETAILS_PENDING_SID, IMTD.ITEM_MASTER_SID, IMTD.FORMULA_ID, IMTD.NET_SALES_FORMULA_MASTER_SID, IMTD.NET_SALES_RULE, IMTD.EVALUATION_RULE, IMTD.CALCULATION_RULE, IMTD.REBATE_PLAN_MASTER_SID, IMTD.DEDUCTION_CALENDAR_MASTER_SID ,hitp.DESCRIPTION as RS_PENDINGSTATUS FROM dbo.RS_CONTRACT_DETAILS_PENDING IMTD JOIN ITEM_MASTER IM ON IM.ITEM_MASTER_SID = IMTD.ITEM_MASTER_SID LEFT JOIN DBO.HELPER_TABLE hitp ON hitp.HELPER_TABLE_SID = IMTD.RS_CONTRACT_ATTACHED_STATUS LEFT JOIN DBO.REBATE_PLAN_MASTER RPM ON RPM.REBATE_PLAN_MASTER_SID = IMTD.REBATE_PLAN_MASTER_SID LEFT JOIN DBO.DEDUCTION_CALENDAR_MASTER DC ON DC.DEDUCTION_CALENDAR_MASTER_SID = IMTD.DEDUCTION_CALENDAR_MASTER_SID LEFT JOIN DBO.FORECASTING_FORMULA FF ON FF.FORECASTING_FORMULA_SID = IMTD.FORMULA_ID LEFT JOIN DBO.NET_SALES_FORMULA_MASTER NSFM ON NSFM.NET_SALES_FORMULA_MASTER_SID = IMTD.NET_SALES_FORMULA_MASTER_SID LEFT JOIN DBO.CDR_MODEL NET ON NET.CDR_MODEL_SID = IMTD.NET_SALES_RULE LEFT JOIN DBO.CDR_MODEL EVA ON EVA.CDR_MODEL_SID = IMTD.EVALUATION_RULE LEFT JOIN DBO.CDR_MODEL CAL ON CAL.CDR_MODEL_SID = IMTD.CALCULATION_RULE WHERE IMTD.RS_CONTRACT_SID = @RS_CONTRACT_ID ?
ORDER BY ? OFFSET ? ROWS FETCH NEXT ? ROWS ONLY;

