DECLARE @PS_CONTRACT_ID int = ?
SELECT IMTD.CHECK_RECORD, recordType = '?', im.ITEM_ID, im.ITEM_NO, im.ITEM_NAME, BM.BRAND_NAME, IMTD.PS_CONTRACT_ATTACHED_STATUS, IMTD.CONTRACT_PRICE_START_DATE, IMTD.CONTRACT_PRICE_END_DATE, IMTD.ITEM_PRICING_QUALIFIER_SID, PRICE=CASE WHEN IMTD.PRICE<>0 THEN str(IMTD.PRICE,16,2) ELSE '' END, SUGGESTED_PRICE=CASE WHEN IMTD.SUGGESTED_PRICE<>0 THEN str(IMTD.SUGGESTED_PRICE,16,2) ELSE '' END, IMTD."SOURCE", usr1.firstName + ' ' + usr1.middleName + ' ' + usr1.lastName as CREATED_BY, IMTD.CREATED_DATE, IMTD.PS_CONTRACT_ATTACHED_DATE, IMTD.PS_CONT_DET_PENDING_SID, IMTD.ITEM_MASTER_SID, ptype=CASE WHEN (IMTD.ITEM_PRICING_QUALIFIER_SID<>0 AND IPQ.ITEM_PRICING_QUALIFIER_NAME NOT LIKE 'Contract Price') THEN 1 ELSE 0 END, PRICEVAL=CASE WHEN IMTD.PRICE<>0 THEN 1 ELSE 0 END FROM dbo.PS_CONTRACT_DETAILS_PENDING IMTD LEFT JOIN BRAND_MASTER BM ON IMTD.BRAND_MASTER_SID = BM.BRAND_MASTER_SID LEFT JOIN ITEM_PRICING_QUALIFIER IPQ ON IPQ.ITEM_PRICING_QUALIFIER_SID=IMTD.ITEM_PRICING_QUALIFIER_SID LEFT JOIN ?.dbo.User_ usr1 ON usr1.UserId = IMTD.CREATED_BY LEFT JOIN ITEM_MASTER im on im.ITEM_MASTER_SID = IMTD.ITEM_MASTER_SID AND im.INBOUND_STATUS <> 'D'
WHERE IMTD.PS_CONTRACT_SID = @PS_CONTRACT_ID ? ORDER BY ? OFFSET ? ROWS FETCH NEXT ? ROWS ONLY;



