entity.query:
DECLARE @IFP_MODEL_SID INT =?, @USERS_SID VARCHAR(100) =?, @SESSION_ID VARCHAR(100) =?
UPDATE IMTD_IFP_DETAILS SET IFP_MODEL_SID=@IFP_MODEL_SID WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND CHECK_BOX=1;
DELETE IFP FROM DBO.IFP_DETAILS IFP JOIN IMTD_IFP_DETAILS IMTD ON IFP.IFP_MODEL_SID=IMTD.IFP_MODEL_SID AND -- IMTD.CHECK_BOX=1 AND IMTD.USERS_SID=@USERS_SID AND IMTD.SESSION_ID=@SESSION_ID;
DELETE FROM IMTD_IFP_DETAILS WHERE USERS_SID=@USERS_SID AND SESSION_ID=@SESSION_ID AND CHECK_BOX=0;
INSERT INTO IFP_DETAILS( IFP_MODEL_SID, ITEM_MASTER_SID, START_DATE, END_DATE, ITEM_IFP_ATTACHED_STATUS, ITEM_IFP_ATTACHED_DATE, INBOUND_STATUS, RECORD_LOCK_STATUS, "SOURCE", CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE ) SELECT IFP_MODEL_SID, ITEM_MASTER_SID, IFP_DETAILS_START_DATE, IFP_DETAILS_END_DATE, IFP_DETAILS_ATTACHED_STATUS, IFP_DETAILS_ATTACHED_DATE, 'A', 0, 'GTN', IFP_DETAILS_CREATED_BY, IFP_DETAILS_CREATED_DATE, IFP_DETAILS_MODIFIED_BY, IFP_DETAILS_MODIFIED_DATE FROM IMTD_IFP_DETAILS WHERE CHECK_BOX = 1 AND USERS_SID = @USERS_SID AND SESSION_ID = @SESSION_ID AND IFP_MODEL_SID = @IFP_MODEL_SID;
