entity.query:
DECLARE @SQL NVARCHAR(MAX) SELECT @SQL =( SELECT ' IF OBJECT_ID(''TEMPDB..' + obj + ''') IS NOT NULL BEGIN DROP TABLE ' + obj + ' END ' FROM ( SELECT obj = name FROM tempdb..sysobjects where crdate < DATEADD( day, - 1, CONVERT( date, SYSDATETIME() ) ) and name like '##ITEM_PRICING%' ) t FOR XML PATH(''), TYPE ).value( '.', 'NVARCHAR(MAX)' ) EXEC(@SQL);
IF Object_id('TEMPDB..##ITEM_PRICING_TEMP_TABLE') IS NOT NULL DROP TABLE ##ITEM_PRICING_TEMP_TABLE
Select ? ITEM_PRICING_QUALIFIER_SID, ITEM_UOM, ITEM_PRICE, PRICING_CODE_STATUS, ENTITY_CODE, COALESCE(START_DATE, NULL) AS START_DATE, END_DATE, INBOUND_STATUS, RECORD_LOCK_STATUS, BATCH_ID, "SOURCE", CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE, ITEM_PRICE_PRECISION, ITEM_PRICING_SID = IDENTITY (int, 1,1) into ##ITEM_PRICING_TEMP_TABLE from dbo.ITEM_PRICING ?
