entity.query:



DECLARE @USERS_ID VARCHAR(10)= ?,
@SESSION_ID  VARCHAR(10)= ?

delete FROM dbo.IMTD_DEDUCTION_DETAILS WHERE USERS_SID= @USERS_ID AND SESSION_ID=@SESSION_ID;
delete FROM dbo.IMTD_SALES_BASIS_DETAILS WHERE USERS_SID= @USERS_ID AND SESSION_ID=@SESSION_ID;

DECLARE @NET_SALES_FORMULA_MASTER_SID INT=?, @USERS_ID VARCHAR(10)= ?, @SESSION_ID VARCHAR(10)= ?
delete FROM dbo.IMTD_SALES_BASIS_DETAILS WHERE USERS_SID= @USERS_ID AND SESSION_ID=@SESSION_ID;
INSERT into IMTD_SALES_BASIS_DETAILS( SALES_BASIS_DETAILS_SID, NET_SALES_FORMULA_MASTER_SID, CONTRACT_MASTER_SID, CONTRACT_NO, CONTRACT_NAME, CFP_CONTRACT_DETAILS_SID, CFP_NO, CFP_NAME, CUSTOMER_NO, CUSTOMER_NAME, CDR_MODEL_SID, CHECK_RECORD, INBOUND_STATUS, SESSION_ID, IMTD_CREATED_DATE, USERS_SID, "OPERATION", CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE ) select 0, 0, SBD.CONTRACT_MASTER_SID, CM.CONTRACT_NO, CM.CONTRACT_NAME, SBD.CFP_CONTRACT_DETAILS_SID, CFP.CFP_NO, CFP.CFP_NAME, COMP.COMPANY_NO, COMP.COMPANY_NAME, SBD.CDR_MODEL_SID, 0 as CHECK_RECORD, SBD.INBOUND_STATUS, @SESSION_ID as SESSION_ID, CURRENT_TIMESTAMP as IMTD_CREATEDDATE, @USERS_ID as USERS_SID, 'A', SBD.CREATED_BY, SBD.CREATED_DATE, SBD.MODIFIED_BY, SBD.MODIFIED_DATE from SALES_BASIS_DETAILS SBD LEFT JOIN CONTRACT_MASTER CM on CM.CONTRACT_MASTER_SID = SBD.CONTRACT_MASTER_SID LEFT JOIN CFP_CONTRACT_DETAILS CFPCD on CFPCD.CFP_CONTRACT_DETAILS_SID = SBD.CFP_CONTRACT_DETAILS_SID LEFT JOIN CFP_CONTRACT CFPC on CFPC.CFP_CONTRACT_SID = CFPCD.CFP_CONTRACT_SID LEFT JOIN CFP_MODEL CFP on CFP.CFP_MODEL_SID = CFPC.CFP_MODEL_SID LEFT JOIN COMPANY_MASTER COMP on COMP.COMPANY_MASTER_SID = CFPCD.COMPANY_MASTER_SID where SBD.NET_SALES_FORMULA_MASTER_SID = @NET_SALES_FORMULA_MASTER_SID AND SBD.INBOUND_STATUS <> 'D';

DECLARE @NET_SALES_FORMULA_MASTER_SID INT=?, @USERS_ID VARCHAR(10)= ?, @SESSION_ID VARCHAR(10)= ?
delete FROM dbo.IMTD_DEDUCTION_DETAILS WHERE USERS_SID= @USERS_ID AND SESSION_ID=@SESSION_ID;
INSERT into IMTD_DEDUCTION_DETAILS( DEDUCTION_DETAILS_SID, NET_SALES_FORMULA_MASTER_SID, CONTRACT_MASTER_SID, CONTRACT_NO, CONTRACT_NAME, DEDUCTION_NO, DEDUCTION_NAME, DEDUCTION_TYPE, DEDUCTION_SUB_TYPE, DEDUCTION_CATEGORY, CDR_MODEL_SID, INDICATOR, CHECK_RECORD, INBOUND_STATUS, USERS_SID, SESSION_ID, IMTD_CREATED_DATE, OPERATION, CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE, RS_CONTRACT_SID ) select 0, 0, DD.CONTRACT_MASTER_SID, CM.CONTRACT_NO, CM.CONTRACT_NAME, RSC.RS_NO, RSC.RS_NAME, DD.DEDUCTION_TYPE, DD.DEDUCTION_SUB_TYPE, DD.DEDUCTION_CATEGORY, DD.CDR_MODEL_SID, DD."INDICATOR", DD.CHECK_RECORD, DD.INBOUND_STATUS, @USERS_ID as USERS_SID, @SESSION_ID as SESSION_ID, CURRENT_TIMESTAMP as IMTD_CREATED_DATE, 'A', DD.CREATED_BY, DD.CREATED_DATE, DD.MODIFIED_BY, DD.MODIFIED_DATE, DD.RS_CONTRACT_SID from DEDUCTION_DETAILS DD LEFT JOIN CONTRACT_MASTER CM on CM.CONTRACT_MASTER_SID = DD.CONTRACT_MASTER_SID LEFT JOIN RS_CONTRACT RSC ON RSC.RS_CONTRACT_SID = DD.RS_CONTRACT_SID where DD.NET_SALES_FORMULA_MASTER_SID = @NET_SALES_FORMULA_MASTER_SID AND DD.INBOUND_STATUS <> 'D';




DECLARE @NET_SALES_FORMULA_MASTER_SID INT= ?

 UPDATE dbo.SALES_BASIS_DETAILS SET INBOUND_STATUS ='D' WHERE NET_SALES_FORMULA_MASTER_SID =@NET_SALES_FORMULA_MASTER_SID;
 UPDATE dbo.DEDUCTION_DETAILS SET INBOUND_STATUS ='D' WHERE NET_SALES_FORMULA_MASTER_SID =@NET_SALES_FORMULA_MASTER_SID;
 UPDATE dbo.NET_SALES_FORMULA_MASTER SET INBOUND_STATUS ='D' WHERE NET_SALES_FORMULA_MASTER_SID =@NET_SALES_FORMULA_MASTER_SID;




DECLARE  @USERS_ID VARCHAR(10)= ?,
		 @SESSION_ID  VARCHAR(10)= ?
 select count(*) from IMTD_DEDUCTION_DETAILS  
 WHERE INDICATOR not in ('+','-') and OPERATION <> 'D' and INBOUND_STATUS <> 'D' and session_ID =@SESSION_ID AND USERS_SID = @USERS_ID;




DECLARE @USERS_ID VARCHAR(10)= ?,
@SESSION_ID  VARCHAR(10)= ?
delete FROM dbo.IMTD_SALES_BASIS_DETAILS WHERE USERS_SID= @USERS_ID AND SESSION_ID=@SESSION_ID;




DECLARE @USERS_ID VARCHAR(10)= ?,
@SESSION_ID  VARCHAR(10)= ?
delete FROM dbo.IMTD_DEDUCTION_DETAILS WHERE USERS_SID= @USERS_ID AND SESSION_ID=@SESSION_ID;
