             DECLARE @COLUMN_NAME VARCHAR(50) = ?,
				@VALUE VARCHAR(50) = ?,
				@IMTD_PS_DETAILS_SID INT = ?,
				@USERS_SID VARCHAR(10) = ?,
				@SESSION_ID VARCHAR(50) = ? 

			DECLARE @SQL NVARCHAR(MAX)
			SET
			@SQL = ' UPDATE
				IMTD_PS_DETAILS
			SET ' + @COLUMN_NAME + ' = @VALUE
			WHERE
				IMTD_PS_DETAILS_SID = @IMTD_PS_DETAILS_SID
				AND USERS_SID = @USERS_SID
				AND SESSION_ID = @SESSION_ID' 
			exec sp_executesql @sql,
			N'@COLUMN_NAME VARCHAR(50) ,
			@VALUE VARCHAR(50),
			@IMTD_PS_DETAILS_SID INT,
			@USERS_SID VARCHAR(10) ,
			@SESSION_ID VARCHAR(50)',
			@COLUMN_NAME = @COLUMN_NAME,
			@VALUE = @VALUE,
			@IMTD_PS_DETAILS_SID = @IMTD_PS_DETAILS_SID,
			@USERS_SID = @USERS_SID,
			@SESSION_ID = @SESSION_ID

DECLARE @PS_MODEL_SID INT=?
Select distinct IFP.IFP_ID, IFP.IFP_NO, IFP.IFP_NAME, case WHEN IFP_STATUS.DESCRIPTION = '-Select One-' THEN '' END as IFP_STATUS, IFP.IFP_END_DATE, case WHEN IFP_TYPE.DESCRIPTION = '-Select One-' THEN '' END as IFP_TYPE,
case WHEN IFP_CATEGORY.DESCRIPTION='-Select One-' THEN '' END as IFP_CATEGORY, IFP.IFP_MODEL_SID FROM IFP_MODEL IFP LEFT JOIN HELPER_TABLE IFP_STATUS ON IFP_STATUS.HELPER_TABLE_SID=IFP.IFP_STATUS LEFT JOIN HELPER_TABLE IFP_TYPE ON IFP_TYPE.HELPER_TABLE_SID=IFP.IFP_TYPE LEFT JOIN HELPER_TABLE IFP_CATEGORY ON IFP_CATEGORY.HELPER_TABLE_SID=IFP.IFP_CATEGORY JOIN dbo.PS_DETAILS PSD ON PSD.IFP_MODEL_SID=IFP.IFP_MODEL_SID AND PSD.PS_MODEL_SID= @PS_MODEL_SID;



DECLARE 
@USERS_SID VARCHAR(10) = ?,
@SESSION_ID VARCHAR(10)=?

DELETE  FROM dbo.Imtd_Ps_Details where CHECK_RECORD = 1 and ADD_COPY_INDICATOR = 'A' AND USERS_SID=@USERS_SID	AND SESSION_ID= @SESSION_ID;

DECLARE @USERS_SID VARCHAR(10) = ?, @SESSION_ID VARCHAR(10)=?
INSERT INTO dbo.Imtd_Ps_Details( ps_Model_Sid, ps_Details_Sid, item_Master_Sid, item_Id, item_No, item_Name, ifp_Model_Sid, ifp_No, BRAND_MASTER_SID, ps_Details_Price, ps_Details_Pricetype, ps_Details_Base_Price, ps_Details_Contract_Price, ps_Dtls_Cont_Price_Startdate, ps_Dtls_Cont_Price_Enddate, ps_Details_Revision_Date, ps_Details_Attached_Date, ps_Details_Attached_Status, ps_Details_Created_Date, ps_Details_Created_By, status, users_Sid, session_Id, created_Date, check_Record, operation, ADD_COPY_INDICATOR ) select ps_Model_Sid, ps_Details_Sid, item_Master_Sid, item_Id, item_No, item_Name, ifp_Model_Sid, ifp_No, BRAND_MASTER_SID, ps_Details_Price, ps_Details_Pricetype, ps_Details_Base_Price, ps_Details_Contract_Price, ps_Dtls_Cont_Price_Startdate, ps_Dtls_Cont_Price_Enddate, ps_Details_Revision_Date, ps_Details_Attached_Date, ps_Details_Attached_Status, ps_Details_Created_Date, ps_Details_Created_By, status, users_Sid, session_Id, created_Date, 0 as check_Record, operation, 'A' as ADD_COPY_INDICATOR
from IMTD_PS_DETAILS where CHECK_RECORD = 1 AND USERS_SID=@USERS_SID AND SESSION_ID= @SESSION_ID;



DECLARE 
@USERS_SID VARCHAR(10) = ?,
@SESSION_ID VARCHAR(10)=?

