entity.query:
SELECT DISTINCT CM.CONT_HOLD_COMPANY_MASTER_SID AS contractHolder, COMP.COMPANY_NAME AS companyName, CM.CONTRACT_NAME AS contractName, CM.CONTRACT_NO AS contractNo, H1.DESCRIPTION AS marketTypeValue, CM.START_DATE AS startDate, CM.END_DATE AS endDate, CCT.CFP_NAME AS companyFamilyPlan, IFP_CONT.IFP_NAME AS itemFamilyPlan, PSC.PS_NAME AS priceSchedule, RSC.RS_NAME AS rebateSchedule, CM.CONTRACT_MASTER_SID, CCT.CFP_CONTRACT_SID, IFP_CONT.IFP_CONTRACT_SID, PSC.PS_CONTRACT_SID, RSC.RS_CONTRACT_SID, TEMP.CHECK_RECORD FROM CONTRACT_MASTER CM LEFT JOIN dbo.CONTRACT_ALIAS_MASTER CAM ON CM.CONTRACT_MASTER_SID = CAM.CONTRACT_MASTER_SID AND CAM.CONTRACT_ALIAS_NO LIKE '?' AND CAM.CONTRACT_ALIAS_TYPE LIKE '?' LEFT JOIN CFP_CONTRACT CCT ON CM.CONTRACT_MASTER_SID = CCT.CONTRACT_MASTER_SID AND CM.INBOUND_STATUS <> 'D' AND CCT.INBOUND_STATUS <> 'D' AND CCT.CFP_NAME LIKE '?' LEFT JOIN CFP_CONTRACT_DETAILS CCD ON CCT.CFP_CONTRACT_SID = CCD.CFP_CONTRACT_SID LEFT JOIN COMPANY_MASTER CMP_MAS ON CMP_MAS.COMPANY_MASTER_SID = CCD.COMPANY_MASTER_SID AND CMP_MAS.INBOUND_STATUS <> 'D' LEFT JOIN IFP_CONTRACT IFP_CONT ON IFP_CONT.CFP_CONTRACT_SID = CCT.CFP_CONTRACT_SID AND IFP_CONT.INBOUND_STATUS <> 'D' AND CM.CONTRACT_MASTER_SID = IFP_CONT.CONTRACT_MASTER_SID AND IFP_CONT.IFP_NAME LIKE '?'
LEFT JOIN PS_CONTRACT PSC ON PSC.CONTRACT_MASTER_SID = CM.CONTRACT_MASTER_SID AND PSC.INBOUND_STATUS <> 'D' AND PSC.PS_NAME LIKE '?' AND PSC.CFP_CONTRACT_SID=CCT.CFP_CONTRACT_SID AND PSC.IFP_CONTRACT_SID=IFP_CONT.IFP_CONTRACT_SID
LEFT JOIN RS_CONTRACT RSC ON RSC.CONTRACT_MASTER_SID = CM.CONTRACT_MASTER_SID AND RSC.INBOUND_STATUS <> 'D' AND PSC.CFP_CONTRACT_SID=CCT.CFP_CONTRACT_SID AND PSC.IFP_CONTRACT_SID=IFP_CONT.IFP_CONTRACT_SID AND RSC.PS_CONTRACT_SID=PSC.PS_CONTRACT_SID LEFT JOIN HELPER_TABLE H1 ON CM.CONTRACT_TYPE = H1.HELPER_TABLE_SID LEFT JOIN IMTD_ITEM_PRICE_REBATE_DETAILS TEMP ON TEMP.CONTRACT_master_sid = CM.CONTRACT_MASTER_SID AND TEMP.CFP_MODEL_SID = CCT.CFP_CONTRACT_SID AND IFP_CONT.CFP_CONTRACT_SID = TEMP.IFP_MODEL_SID AND PSC.PS_CONTRACT_SID = TEMP.PS_MODEL_SID AND Temp.RS_MODEL_SID = RSC.RS_CONTRACT_SID AND TEMP.SESSION_ID = '?' AND TEMP.USERS_SID = '?' LEFT JOIN COMPANY_MASTER COMP ON CM.CONT_HOLD_COMPANY_MASTER_SID = COMP.COMPANY_MASTER_SID WHERE Isnull(CM.CONT_HOLD_COMPANY_MASTER_SID, '0') LIKE '?' AND CM.CONTRACT_NO LIKE '?' AND CM.CONTRACT_NAME LIKE '?' AND CM.CONTRACT_TYPE LIKE '?' ? ? ? ? ?;
