SELECT DISTINCT Temp.PROJECTION_MASTER_SID AS Projection_id, HT.DESCRIPTION AS Status, COMP.COMPANY_NAME AS CONTRACT_HOLDER, CM.CONTRACT_NO, CM.CONTRACT_NAME, Market_Type.DESCRIPTION AS MARKET_TYPE, CM.START_DATE AS CM_START, CM.END_DATE AS CM_END, Temp.ITEM_STATUS, Temp.START_DATE AS TEM_START, Temp.END_DATE AS TEM_END, Temp.CONTRACT_PRICE_START_DATE, Temp.CONTRACT_PRICE_END_DATE, Temp.contract_price, Temp.price, Temp.PRice_protection_START_date, Temp.PRice_protection_end_date, Temp.price_tolerance_type, Temp.price_tolerance, Temp.price_tolerance_frequency, Temp.price_tolerance_interval, Temp.base_price, Temp.ITEM_REBATE_START_DATE, Temp.ITEM_REBATE_End_DATE, Temp.formula_id, Temp.formula_method_id, Temp.REBATE_AMOUNT, CCT.CFP_NO, CCT.CFP_NAME, IFP_CONT.IFP_NO, IFP_CONT.IFP_NAME, PSC.PS_NO, PSC.PS_NAME, RSC.RS_NO, RSC.RS_NAME, 'RAR CAT' AS RARCategory, Temp.check_record, TEMP.CONTRACT_MASTER_SID, TEMP.CFP_CONTRACT_SID, TEMP.IFP_CONTRACT_SID, TEMP.PS_CONTRACT_SID, TEMP.RS_CONTRACT_SID, Temp.item_master_sid, ST.DESCRIPTION AS ST1, PTT.DESCRIPTION AS ST2, PTF.DESCRIPTION AS ST3, PT.DESCRIPTION AS ST4 FROM GCM_GLOBAL_DETAILS Temp JOIN CONTRACT_MASTER CM ON CM.CONTRACT_MASTER_SID = Temp.CONTRACT_MASTER_SID AND Temp.session_id = '?' AND Temp.OPERATION = '?' AND Temp.MODULE_NAME = 'ITEM_MANAGEMENT' AND ( cm.contract_no LIKE '?' ) AND ( cm.CONTRACT_NAME LIKE '?' OR cm.CONTRACT_NAME IS NULL ) JOIN WORKFLOW_MASTER WM ON wm.PROJECTION_MASTER_SID = TEMP.PROJECTION_MASTER_SID
LEFT JOIN helper_table HT ON ht.HELPER_TABLE_SID = WM.WORKFLOW_STATUS_ID LEFT JOIN COMPANY_MASTER COMP ON CM.CONT_HOLD_COMPANY_MASTER_SID = COMP.COMPANY_MASTER_SID AND ( COMP.COMPANY_NAME LIKE '?' OR cm.CONT_HOLD_COMPANY_MASTER_SID IS NULL ) LEFT JOIN HELPER_TABLE Market_Type ON CM.CONTRACT_TYPE = Market_Type.HELPER_TABLE_SID AND ( CM.CONTRACT_TYPE LIKE '?' OR CM.CONTRACT_TYPE IS NULL ) LEFT JOIN HELPER_TABLE ST ON ST.HELPER_TABLE_SID = Temp.ITEM_STATUS LEFT JOIN HELPER_TABLE PTT ON PTT.HELPER_TABLE_SID = Temp.price_tolerance_type LEFT JOIN HELPER_TABLE PTF ON PTF.HELPER_TABLE_SID = Temp.price_tolerance_frequency LEFT JOIN HELPER_TABLE PT ON PT.HELPER_TABLE_SID = Temp.price_tolerance_interval JOIN CFP_CONTRACT CCT ON Temp.cfP_contract_sid = CCT.cfP_contract_sid INNER JOIN CFP_MODEL CFP_M ON CCT.CFP_MODEL_SID = CFP_M.CFP_MODEL_SID AND ( CFP_M.CFP_NO LIKE '?' ) INNER JOIN IFP_CONTRACT IFP_CONT ON IFP_CONT.IFP_CONTRACT_SID = Temp.IFP_CONTRACT_SID INNER JOIN IFP_MODEL IFP_M ON IFP_CONT.IFP_MODEL_SID = IFP_M.IFP_MODEL_SID AND ( IFP_M.IFP_NO LIKE '?' ) INNER JOIN PS_CONTRACT PSC ON PSC.PS_CONTRACT_SID = Temp.PS_CONTRACT_SID INNER JOIN PS_MODEL PS_M ON PSC.PS_MODEL_SID = PS_M.PS_MODEL_SID AND ( PS_M.PS_NO LIKE '?' OR PS_M.PS_NO IS NULL ) JOIN RS_CONTRACT RSC ON RSC.RS_CONTRACT_SID = Temp.RS_CONTRACT_SID AND ( rsc.RS_ID LIKE '?' ) AND ( rsc.RS_NAME LIKE '?' OR rsc.RS_NAME IS NULL ) AND ( rsc.RS_TYPE LIKE '?' ) AND ( rsc.RS_CATEGORY LIKE '?' OR rsc.RS_CATEGORY IS NULL ) AND ( rsc.REBATE_PROGRAM_TYPE LIKE '?' OR rsc.REBATE_PROGRAM_TYPE IS NULL ) AND ( rsc.RS_ALIAS LIKE '?' OR rsc.RS_ALIAS IS NULL ) LEFT JOIN RS_MODEL RS_M ON RSC.RS_MODEL_SID = RS_M.RS_MODEL_SID AND ( RS_M.RS_NO LIKE '?' ) ? ORDER BY TEMP.PROJECTION_MASTER_SID
OFFSET ? ROWS FETCH NEXT ? ROWS ONLY;

