PSCD.CONTRACT_PRICE_START_DATE, PSCD.CONTRACT_PRICE_END_DATE, PSCD.CONTRACT_PRICE, PSCD.PRICE, PSCD.PRICE_PROTECTION_START_DATE, PSCD.PRICE_PROTECTION_END_DATE, PSCD.PRICE_TOLERANCE_TYPE, PSCD.PRICE_TOLERANCE, PSCD.PRICE_TOLERANCE_FREQUENCY, PSCD.PRICE_TOLERANCE_INTERVAL, PSCD.BASE_PRICE, RSCD.ITEM_REBATE_START_DATE, RSCD.ITEM_REBATE_END_DATE, RSCD.FORMULA_ID, RSCD.FORMULA_METHOD_ID, RSCD.REBATE_AMOUNT, CM.CONTRACT_MASTER_SID, CCT.CFP_CONTRACT_SID, IFP_CONT.IFP_CONTRACT_SID, PSC.PS_CONTRACT_SID, RSC.RS_CONTRACT_SID, ICD.ITEM_MASTER_SID, PM.PROJECTION_MASTER_SID, '?', '?', '?', 'ITEM_MANAGEMENT' FROM WORKFLOW_MASTER WM JOIN PROJECTION_MASTER PM ON WM.PROJECTION_MASTER_SID = PM.PROJECTION_MASTER_SID JOIN PROJECTION_DETAILS pd ON PD.PROJECTION_MASTER_SID = PM.PROJECTION_MASTER_SID JOIN CCP_DETAILS ccp ON pd.CCP_DETAILS_SID = ccp.CCP_DETAILS_SID INNER JOIN CONTRACT_MASTER CM ON cm.CONTRACT_MASTER_SID = ccp.CONTRACT_MASTER_SID INNER JOIN CFP_CONTRACT CCT ON CM.CONTRACT_MASTER_SID = CCT.CONTRACT_MASTER_SID AND CM.INBOUND_STATUS <> 'D' AND CCT.INBOUND_STATUS <> 'D' INNER JOIN IFP_CONTRACT IFP_CONT ON IFP_CONT.CFP_CONTRACT_SID = CCT.CFP_CONTRACT_SID AND IFP_CONT.INBOUND_STATUS <> 'D' AND CM.CONTRACT_MASTER_SID = IFP_CONT.CONTRACT_MASTER_SID INNER JOIN IFP_CONTRACT_DETAILS ICD ON IFP_CONT.IFP_CONTRACT_SID = ICD.IFP_CONTRACT_SID AND icd.ITEM_MASTER_SID IN( ? ) AND ICD.ITEM_MASTER_SID=ccp.ITEM_MASTER_SID INNER JOIN PS_CONTRACT PSC ON PSC.CONTRACT_MASTER_SID = CM.CONTRACT_MASTER_SID AND PSC.IFP_CONTRACT_SID = IFP_CONT.IFP_CONTRACT_SID AND PSC.INBOUND_STATUS <> 'D' INNER JOIN PS_CONTRACT_DETAILS PSCD ON PSCD.PS_CONTRACT_SID = PSC.PS_CONTRACT_SID AND PSCD.ITEM_MASTER_SID = icd.ITEM_MASTER_SID JOIN RS_CONTRACT RSC ON RSC.CONTRACT_MASTER_SID = CM.CONTRACT_MASTER_SID AND RSC.CFP_CONTRACT_SID = CCT.CFP_CONTRACT_SID AND RSC.IFP_CONTRACT_SID = IFP_CONT.IFP_CONTRACT_SID AND RSC.PS_CONTRACT_SID = PSC.PS_CONTRACT_SID AND RSC.INBOUND_STATUS <> 'D' JOIN RS_CONTRACT_DETAILS RSCD ON RSCD.RS_CONTRACT_SID = RSC.RS_CONTRACT_SID AND RSCD.ITEM_MASTER_SID = icd.ITEM_MASTER_SID ORDER BY PROJECTION_MASTER_SID ;

