custom-sql.sql:
SELECT DISTINCT IT.ITEM_MASTER_SID, CM.COMPANY_NAME AS COMPANY, IT.ITEM_ID, IT.ITEM_NO, IT.ITEM_NAME, IT.ITEM_DESC, TherapeuticClass.DESCRIPTION AS TherapeuticClass, BM.BRAND_NAME AS BRAND, form.DESCRIPTION AS FORM, Strength.DESCRIPTION AS STRENGTH, IT.RECORD_LOCK_STATUS AS PLACEHOLDER, IT.NDC9, item_category.DESCRIPTION AS item_category, item_type.DESCRIPTION AS item_type
FROM ITEM_MASTER IT JOIN ITEM_IDENTIFIER ID ON ID.ITEM_MASTER_SID = IT.ITEM_MASTER_SID JOIN ITEM_QUALIFIER IQ ON IQ.ITEM_QUALIFIER_SID = ID.ITEM_QUALIFIER_SID AND (IQ.ITEM_QUALIFIER_SID LIKE '?') AND (ID.ITEM_IDENTIFIER_VALUE LIKE '?') JOIN HELPER_TABLE Form ON Form.HELPER_TABLE_SID = IT.FORM AND ( IT.ITEM_ID LIKE '?' OR IT.ITEM_ID IS NULL ) AND ( IT.ITEM_NAME LIKE '?' OR IT.ITEM_NAME IS NULL ) AND ( IT.THERAPEUTIC_CLASS LIKE '?' OR IT.THERAPEUTIC_CLASS IS NULL ) AND ( IT.FORM LIKE '?' OR IT.FORM IS NULL ) AND ( IT.ITEM_NO LIKE '?' OR IT.ITEM_NO IS NULL ) AND ( IT.ITEM_DESC LIKE '?' OR IT.ITEM_DESC IS NULL ) AND ( IT.BRAND_MASTER_SID LIKE '?' OR IT.BRAND_MASTER_SID IS NULL ) AND ( IT.STRENGTH LIKE '?' OR IT.STRENGTH IS NULL ) AND ( IT.NDC9 LIKE '?' OR IT.NDC9 IS NULL ) AND ( IT.ITEM_CATEGORY LIKE '?' OR IT.ITEM_CATEGORY IS NULL ) AND ( IT.ITEM_TYPE LIKE '?' OR IT.ITEM_TYPE IS NULL ) AND (IT.RECORD_LOCK_STATUS LIKE '?')
LEFT JOIN COMPANY_MASTER CM ON CM.COMPANY_MASTER_SID=IT.MANUFACTURER_ID
JOIN HELPER_TABLE Strength ON Strength.HELPER_TABLE_SID = IT.STRENGTH JOIN BRAND_MASTER BM ON IT.BRAND_MASTER_SID = BM.brand_master_sid JOIN HELPER_TABLE TherapeuticClass ON TherapeuticClass.HELPER_TABLE_SID = IT.THERAPEUTIC_CLASS JOIN HELPER_TABLE item_category ON item_category.HELPER_TABLE_SID = IT.ITEM_CATEGORY JOIN HELPER_TABLE item_type ON item_type.HELPER_TABLE_SID = IT.ITEM_TYPE AND IsNull(CM.COMPANY_MASTER_SID,'0') LIKE '?' JOIN IFP_CONTRACT_DETAILS IFP_CD ON IFP_CD.ITEM_MASTER_SID = IT.ITEM_MASTER_SID JOIN PS_CONTRACT_DETAILS PS ON PS.ITEM_MASTER_SID = IT.ITEM_MASTER_SID
WHERE ( IT.ITEM_MASTER_SID LIKE '?' OR IT.ITEM_MASTER_SID IS NULL ) AND Isnull(CM.COMPANY_MASTER_SID, '0') LIKE '?' AND ( IT.ITEM_ID LIKE '?' OR IT.ITEM_ID IS NULL ) AND ( IT.ITEM_NO LIKE '?' OR IT.ITEM_NO IS NULL ) AND ( IT.ITEM_NAME LIKE '?' OR IT.ITEM_NAME IS NULL ) AND ( IT.ITEM_DESC LIKE '?' OR IT.ITEM_DESC IS NULL ) AND ( IT.THERAPEUTIC_CLASS LIKE '?' OR IT.THERAPEUTIC_CLASS IS NULL ) AND ( IT.BRAND_MASTER_SID LIKE '?' OR IT.BRAND_MASTER_SID IS NULL ) AND ( IT.FORM LIKE '?' OR IT.FORM IS NULL ) AND ( IT.STRENGTH LIKE '?' OR IT.STRENGTH IS NULL ) AND (IT.RECORD_LOCK_STATUS LIKE '?') AND ( IT.NDC9 LIKE '?' OR IT.NDC9 IS NULL ) AND ( IT.ITEM_CATEGORY LIKE '?' OR IT.ITEM_CATEGORY IS NULL ) AND ( IT.ITEM_TYPE LIKE '?' OR IT.ITEM_TYPE IS NULL ) ORDER BY it.item_master_sid OFFSET ? ROWS FETCH NEXT ? ROWS ONLY;
DELETE FROM GCM_GLOBAL_DETAILS WHERE CREATED_DATE <= GETDATE() - 2 AND MODULE_NAME = 'ITEM_MANAGEMENT';
