entity.query:
DECLARE @CCP TABLE ( CCP_DETAILS_SID INT, CUST_HIERARCHY_NO VARCHAR(100), CUST_RELATIONSHIP_LEVEL_VALUES INT, PROD_HIERARCHY_NO VARCHAR(100), PROD_RELATIONSHIP_LEVEL_VALUES INT ) DECLARE @PROJECTION_MASTER_SID INT=[$PROJECTION_MASTER_SID], @CUSTOM_VIEW_MASTER_SID INT = [$CUSTOM_VIEW_MASTER_SID]
INSERT @CCP (CCP_DETAILS_SID, CUST_HIERARCHY_NO, CUST_RELATIONSHIP_LEVEL_VALUES, PROD_HIERARCHY_NO, PROD_RELATIONSHIP_LEVEL_VALUES) SELECT CCPMAPC.CCP_DETAILS_SID, CUST_HIERARCHY_NO, CUST_RELATIONSHIP_LEVEL_VALUES, PROD_HIERARCHY_NO, PROD_RELATIONSHIP_LEVEL_VALUES FROM (SELECT RLD.RELATIONSHIP_LEVEL_VALUES AS CUST_RELATIONSHIP_LEVEL_VALUES, RLD.HIERARCHY_NO AS CUST_HIERARCHY_NO, CCP.CCP_DETAILS_SID FROM RELATIONSHIP_LEVEL_DEFINITION RLD JOIN CCP_MAP CCP ON RLD.RELATIONSHIP_LEVEL_SID = CCP.RELATIONSHIP_LEVEL_SID JOIN CFF_DETAILS PD ON PD.CCP_DETAILS_SID = CCP.CCP_DETAILS_SID JOIN CFF_MASTER PM ON PM.CFF_MASTER_SID = PD.CFF_MASTER_SID AND RLD.RELATIONSHIP_BUILDER_SID = PM.CUST_RELATIONSHIP_BUILDER_SID AND PD.CFF_MASTER_SID = @PROJECTION_MASTER_SID) CCPMAPC JOIN (SELECT RLD.RELATIONSHIP_LEVEL_VALUES AS PROD_RELATIONSHIP_LEVEL_VALUES, RLD.HIERARCHY_NO AS PROD_HIERARCHY_NO, CCP.CCP_DETAILS_SID FROM RELATIONSHIP_LEVEL_DEFINITION RLD JOIN CCP_MAP CCP ON RLD.RELATIONSHIP_LEVEL_SID = CCP.RELATIONSHIP_LEVEL_SID JOIN CFF_DETAILS PD ON PD.CCP_DETAILS_SID = CCP.CCP_DETAILS_SID JOIN CFF_MASTER PM ON PM.CFF_MASTER_SID = PD.CFF_MASTER_SID AND RLD.RELATIONSHIP_BUILDER_SID = PM.PROD_RELATIONSHIP_BUILDER_SID AND PD.CFF_MASTER_SID = @PROJECTION_MASTER_SID) CCPMAPP ON CCPMAPC.CCP_DETAILS_SID = CCPMAPP.CCP_DETAILS_SID;
WITH PIVOT_DATE AS (SELECT DISTINCT LEVEL_NO, CCP_DETAILS_SID, CUST_RELATIONSHIP_LEVEL_VALUES, CUST_HIERARCHY_NO, C_LEVEL_NAME, RELATIONSHIP_LEVEL_VALUES, CFF_CUSTOM_VIEW_DETAILS_SID FROM @CCP JOIN (SELECT RLD2.HIERARCHY_NO, CFF_CUSTOM_VIEW_DETAILS_SID, RLD2.RELATIONSHIP_LEVEL_SID, CVD.LEVEL_NO AS LEVEL_NO, HLD.LEVEL_NAME AS C_LEVEL_NAME, RELATIONSHIP_LEVEL_VALUES FROM DBO.CFF_CUSTOM_VIEW_DETAILS CVD JOIN DBO.HIERARCHY_LEVEL_DEFINITION HLD ON CVD.HIERARCHY_ID = HLD.HIERARCHY_LEVEL_DEFINITION_SID AND CVD.CFF_CUSTOM_VIEW_MASTER_SID = @CUSTOM_VIEW_MASTER_SID JOIN RELATIONSHIP_LEVEL_DEFINITION RLD2 ON HLD.HIERARCHY_LEVEL_DEFINITION_SID = RLD2.HIERARCHY_LEVEL_DEFINITION_SID JOIN CFF_CUST_HIERARCHY PCH2 ON PCH2.RELATIONSHIP_LEVEL_SID = RLD2.RELATIONSHIP_LEVEL_SID AND PCH2.CFF_MASTER_SID = @PROJECTION_MASTER_SID) HLDC ON CUST_HIERARCHY_NO LIKE HLDC.HIERARCHY_NO + '%' UNION ALL SELECT DISTINCT P_LEVEL_NO, CCP_DETAILS_SID, PROD_RELATIONSHIP_LEVEL_VALUES, PROD_HIERARCHY_NO, P_LEVEL_NAME, RELATIONSHIP_LEVEL_VALUES, CFF_CUSTOM_VIEW_DETAILS_SID FROM @CCP JOIN (SELECT RLD2.HIERARCHY_NO, CFF_CUSTOM_VIEW_DETAILS_SID, RLD2.RELATIONSHIP_LEVEL_SID, CVD.LEVEL_NO AS P_LEVEL_NO, HLD.LEVEL_NAME AS P_LEVEL_NAME, RELATIONSHIP_LEVEL_VALUES FROM DBO.CFF_CUSTOM_VIEW_DETAILS CVD JOIN DBO.HIERARCHY_LEVEL_DEFINITION HLD ON CVD.HIERARCHY_ID = HLD.HIERARCHY_LEVEL_DEFINITION_SID AND CVD.CFF_CUSTOM_VIEW_MASTER_SID = @CUSTOM_VIEW_MASTER_SID JOIN RELATIONSHIP_LEVEL_DEFINITION RLD2 ON HLD.HIERARCHY_LEVEL_DEFINITION_SID = RLD2.HIERARCHY_LEVEL_DEFINITION_SID JOIN CFF_PROD_HIERARCHY PCH2 ON PCH2.RELATIONSHIP_LEVEL_SID = RLD2.RELATIONSHIP_LEVEL_SID AND PCH2.CFF_MASTER_SID = @PROJECTION_MASTER_SID) HLDP ON PROD_HIERARCHY_NO LIKE HLDP.HIERARCHY_NO + '%') INSERT INTO CFF_CUSTOM_VIEW (CFF_CUSTOM_VIEW_DETAILS_SID, CFF_RELATIONSHIP_LEVEL_VALUES_SID, CCP_DETAILS_SID) SELECT CFF_CUSTOM_VIEW_DETAILS_SID, RELATIONSHIP_LEVEL_VALUES, CCP_DETAILS_SID FROM PIVOT_DATE PD WHERE NOT EXISTS(SELECT 1 FROM CFF_CUSTOM_VIEW CCM WHERE CCM.CFF_CUSTOM_VIEW_DETAILS_SID = PD.CFF_CUSTOM_VIEW_DETAILS_SID AND CCM.CFF_RELATIONSHIP_LEVEL_VALUES_SID = PD.RELATIONSHIP_LEVEL_VALUES AND CCM.CCP_DETAILS_SID = PD.CCP_DETAILS_SID)
