INSERT INTO ST_CH_SALES_PROJECTION_MASTER( PROJECTION_DETAILS_SID, METHODOLOGY, CALCULATION_PERIODS, CHECK_RECORD, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT A.PROJECTION_DETAILS_SID, A.METHODOLOGY, A.CALCULATION_PERIODS, A.CHECK_RECORD, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.CH_SALES_PROJECTION_MASTER A, dbo.PROJECTION_DETAILS B WHERE A.PROJECTION_DETAILS_SID=B.PROJECTION_DETAILS_SID AND B.PROJECTION_MASTER_SID=?PID;
INSERT INTO dbo.ST_CH_SALES_PROJECTION( PROJECTION_DETAILS_SID, GTS_SALES, CONTRACT_SALES, CONTRACT_UNITS, PERIOD_SID, PER_OF_BUSINESS, WAC_PRICE, USER_ID, SESSION_ID, LAST_MODIFIED_DATE ) SELECT A.PROJECTION_DETAILS_SID, A.GTS_SALES, A.CONTRACT_SALES, A.CONTRACT_UNITS, A.PERIOD_SID, A.PER_OF_BUSINESS, A.WAC_PRICE, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.CH_SALES_PROJECTION A, dbo.PROJECTION_DETAILS B WHERE A.PROJECTION_DETAILS_SID=B.PROJECTION_DETAILS_SID AND B.PROJECTION_MASTER_SID=?PID;
INSERT INTO dbo.ST_CH_ACTUAL_SALES( PROJECTION_DETAILS_SID, PERIOD_SID, CONTRACT_SALES, CONTRACT_UNITS, GTS_SALES, PER_OF_BUSINESS, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT A.PROJECTION_DETAILS_SID, A.PERIOD_SID, A.CONTRACT_SALES, A.CONTRACT_UNITS, A.GTS_SALES, A.PER_OF_BUSINESS, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.CH_ACTUAL_SALES A, dbo.PROJECTION_DETAILS B WHERE A.PROJECTION_DETAILS_SID=B.PROJECTION_DETAILS_SID AND B.PROJECTION_MASTER_SID=?PID;
INSERT INTO dbo.ST_CH_DISCOUNT_PROJ_MASTER( PROJECTION_DETAILS_SID, RS_MODEL_SID, DISCOUNT_TYPE, PROJECTED_TYPE, METHODOLOGY, PRICE_GROUP_TYPE, NET_FLAG, CHECK_RECORD, BASELINE_PERIODS, SELECTED_PERIODS, CALCULATION_PERIODS, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT A.PROJECTION_DETAILS_SID, A.RS_MODEL_SID, A.DISCOUNT_TYPE, A.PROJECTED_TYPE, A.METHODOLOGY, A.PRICE_GROUP_TYPE, A.NET_FLAG, A.CHECK_RECORD, A.BASELINE_PERIODS, A.SELECTED_PERIODS, A.CALCULATION_PERIODS, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.CH_DISCOUNT_PROJ_MASTER A, dbo.PROJECTION_DETAILS B WHERE A.PROJECTION_DETAILS_SID=B.PROJECTION_DETAILS_SID AND B.PROJECTION_MASTER_SID=?PID;
INSERT INTO dbo.ST_CH_PROJECTION_DISCOUNT( PROJECTION_DETAILS_SID, RS_MODEL_SID, PERIOD_SID, DISCOUNT_RATE, DISCOUNT_AMOUNT, PRODUCT_GROWTH, ACCOUNT_GROWTH, PROJECTION_SALES, PROJECTION_RATE, ADJUSTMENT_TYPE, ADJUSTMENT_BASIS, ADJUSTMENT_VALUE, ADJUSTMENT_METHODOLOGY, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT A.PROJECTION_DETAILS_SID, A.RS_MODEL_SID, A.PERIOD_SID, A.DISCOUNT_RATE, A.DISCOUNT_AMOUNT, A.PRODUCT_GROWTH, A.ACCOUNT_GROWTH, A.PROJECTION_SALES, A.PROJECTION_RATE, A.ADJUSTMENT_TYPE, A.ADJUSTMENT_BASIS, A.ADJUSTMENT_VALUE, A.ADJUSTMENT_METHODOLOGY, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.CH_PROJECTION_DISCOUNT A, dbo.PROJECTION_DETAILS B WHERE A.PROJECTION_DETAILS_SID=B.PROJECTION_DETAILS_SID AND B.PROJECTION_MASTER_SID=?PID;
INSERT INTO dbo.ST_CH_ACTUAL_DISCOUNT( PROJECTION_DETAILS_SID, RS_MODEL_SID, PERIOD_SID, ACTUAL_SALES, ACTUAL_RATE, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT A.PROJECTION_DETAILS_SID, A.RS_MODEL_SID, A.PERIOD_SID, A.ACTUAL_SALES, A.ACTUAL_RATE, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.CH_ACTUAL_DISCOUNT A, dbo.PROJECTION_DETAILS B WHERE A.PROJECTION_DETAILS_SID=B.PROJECTION_DETAILS_SID AND B.PROJECTION_MASTER_SID=?PID;
INSERT INTO dbo.ST_CH_ASSUMPTIONS( CH_ASSUMPTIONS_SID, PROJECTION_DETAILS_SID, QUARTER, YEAR, GROSS_TRADE_SALES, TOTAL_DISCOUNT_PERCENT_PRIOR, TOTAL_DISCOUNT_PERCENT_PROJECTED, TOTAL_DISCOUNT_PERCENT_CHANGE, TOTAL_DISCOUNT_PRIOR, TOTAL_DISCOUNT_PROJECTED, TOTAL_DISCOUNT_CHANGE, REASON_CODES, COMMENTARY, CAM_ID, PARENT, IS_CHECKED, USER_ID, SESSION_ID) SELECT A.CH_ASSUMPTIONS_SID, A.PROJECTION_DETAILS_SID, A.QUARTER, A.YEAR, A.GROSS_TRADE_SALES, A.TOTAL_DISCOUNT_PERCENT_PRIOR, A.TOTAL_DISCOUNT_PERCENT_PROJECTED, A.TOTAL_DISCOUNT_PERCENT_CHANGE, A.TOTAL_DISCOUNT_PRIOR, A.TOTAL_DISCOUNT_PROJECTED, A.TOTAL_DISCOUNT_CHANGE, A.REASON_CODES, A.COMMENTARY, A.CAM_ID, A.PARENT, 0 IS_CHECKED, ?UID USER_ID, ?SID SESSION_ID FROM dbo.CH_ASSUMPTIONS A, dbo.PROJECTION_DETAILS B WHERE A.PROJECTION_DETAILS_SID=B.PROJECTION_DETAILS_SID AND B.PROJECTION_MASTER_SID=?PID;


