UPDATE dbo.NA_PROJ_MASTER set MODIFIED_DATE = CURRENT_TIMESTAMP where NA_PROJ_MASTER_SID=?PID;
MERGE PHS_PROJ AS TARGET USING ( SELECT NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, PROJECTION_PRICE, ADJUSTMENT, NOTES FROM dbo.ST_PHS_PROJ WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE ON (TARGET.NA_PROJ_DETAILS_SID=SOURCE.NA_PROJ_DETAILS_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID AND TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE)
WHEN MATCHED THEN UPDATE SET TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE, TARGET.PROJECTION_PRICE=SOURCE.PROJECTION_PRICE, TARGET.ADJUSTMENT=SOURCE.ADJUSTMENT, TARGET.NOTES=SOURCE.NOTES
WHEN NOT MATCHED BY TARGET THEN INSERT ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, PROJECTION_PRICE, ADJUSTMENT, NOTES ) VALUES(SOURCE.NA_PROJ_DETAILS_SID, SOURCE.PERIOD_SID, SOURCE.PRICE_TYPE, SOURCE.PROJECTION_PRICE, SOURCE.ADJUSTMENT, SOURCE.NOTES );
MERGE PHS_ACTUALS AS TARGET USING ( SELECT NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE, NOTES FROM dbo.ST_PHS_ACTUALS WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE ON (TARGET.NA_PROJ_DETAILS_SID=SOURCE.NA_PROJ_DETAILS_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID AND TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE)
WHEN MATCHED THEN UPDATE SET TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE, TARGET.ACTUAL_PRICE=SOURCE.ACTUAL_PRICE, TARGET.NOTES=SOURCE.NOTES
WHEN NOT MATCHED BY TARGET THEN INSERT ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE, NOTES ) VALUES(SOURCE.NA_PROJ_DETAILS_SID, SOURCE.PERIOD_SID, SOURCE.PRICE_TYPE, SOURCE.ACTUAL_PRICE, SOURCE.NOTES );
MERGE FCP_ACTUALS AS TARGET USING ( SELECT NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE, NOTES FROM dbo.ST_FCP_ACTUALS WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE ON (TARGET.NA_PROJ_DETAILS_SID=SOURCE.NA_PROJ_DETAILS_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID AND TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE)
WHEN MATCHED THEN UPDATE SET TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE, TARGET.ACTUAL_PRICE=SOURCE.ACTUAL_PRICE, TARGET.NOTES=SOURCE.NOTES
WHEN NOT MATCHED BY TARGET THEN INSERT ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE, NOTES ) VALUES(SOURCE.NA_PROJ_DETAILS_SID, SOURCE.PERIOD_SID, SOURCE.PRICE_TYPE, SOURCE.ACTUAL_PRICE, SOURCE.NOTES );
MERGE FCP_PROJ AS TARGET USING ( SELECT NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, PROJECTION_PRICE, ADJUSTMENT, NOTES FROM dbo.ST_FCP_PROJ WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE ON (TARGET.NA_PROJ_DETAILS_SID=SOURCE.NA_PROJ_DETAILS_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID AND TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE)
WHEN MATCHED THEN UPDATE SET TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE, TARGET.PROJECTION_PRICE=SOURCE.PROJECTION_PRICE, TARGET.ADJUSTMENT=SOURCE.ADJUSTMENT, TARGET.NOTES=SOURCE.NOTES
WHEN NOT MATCHED BY TARGET THEN INSERT ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, PROJECTION_PRICE, ADJUSTMENT, NOTES ) VALUES(SOURCE.NA_PROJ_DETAILS_SID, SOURCE.PERIOD_SID, SOURCE.PRICE_TYPE, SOURCE.PROJECTION_PRICE, SOURCE.ADJUSTMENT, SOURCE.NOTES );
MERGE MEDICAID_URA_ACTUALS AS TARGET USING ( SELECT NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE, BASE_YEAR, NOTES, ALT_BASE_YEAR FROM dbo.ST_MEDICAID_URA_ACTUALS WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE ON (TARGET.NA_PROJ_DETAILS_SID=SOURCE.NA_PROJ_DETAILS_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID AND TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE)
WHEN MATCHED THEN UPDATE SET TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE, TARGET.ACTUAL_PRICE=SOURCE.ACTUAL_PRICE, TARGET.BASE_YEAR=SOURCE.BASE_YEAR, TARGET.NOTES=SOURCE.NOTES, TARGET.ALT_BASE_YEAR=SOURCE.ALT_BASE_YEAR
WHEN NOT MATCHED BY TARGET THEN INSERT ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE, BASE_YEAR, NOTES, ALT_BASE_YEAR ) VALUES(SOURCE.NA_PROJ_DETAILS_SID, SOURCE.PERIOD_SID, SOURCE.PRICE_TYPE, SOURCE.ACTUAL_PRICE, SOURCE.BASE_YEAR, SOURCE.NOTES, SOURCE.ALT_BASE_YEAR );
MERGE MEDICAID_URA_PROJ AS TARGET USING ( SELECT NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, PROJECTION_PRICE, ADJUSTMENT, NOTES FROM dbo.ST_MEDICAID_URA_PROJ WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE ON (TARGET.NA_PROJ_DETAILS_SID=SOURCE.NA_PROJ_DETAILS_SID AND TARGET.PERIOD_SID=SOURCE.PERIOD_SID AND TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE)
WHEN MATCHED THEN UPDATE SET TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE, TARGET.PROJECTION_PRICE=SOURCE.PROJECTION_PRICE, TARGET.ADJUSTMENT=SOURCE.ADJUSTMENT, TARGET.NOTES=SOURCE.NOTES
WHEN NOT MATCHED BY TARGET THEN
INSERT ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, PROJECTION_PRICE, ADJUSTMENT, NOTES ) VALUES(SOURCE.NA_PROJ_DETAILS_SID, SOURCE.PERIOD_SID, SOURCE.PRICE_TYPE, SOURCE.PROJECTION_PRICE, SOURCE.ADJUSTMENT, SOURCE.NOTES );
MERGE NATIONAL_ASSUMPTIONS_ACTUALS AS TARGET USING (SELECT ITEM_MASTER_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE FROM dbo.ST_NATIONAL_ASSUMPTIONS_ACTUALS WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE(ITEM_MASTER_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE) ON ( TARGET.ITEM_MASTER_SID = SOURCE.ITEM_MASTER_SID AND TARGET.PERIOD_SID = SOURCE.PERIOD_SID AND TARGET.PRICE_TYPE = SOURCE.PRICE_TYPE)
WHEN MATCHED THEN UPDATE SET TARGET.PERIOD_SID = SOURCE.PERIOD_SID, TARGET.PRICE_TYPE = SOURCE.PRICE_TYPE, TARGET.ACTUAL_PRICE = SOURCE.ACTUAL_PRICE
WHEN NOT MATCHED BY TARGET THEN INSERT (ITEM_MASTER_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE ) VALUES(SOURCE.ITEM_MASTER_SID, SOURCE.PERIOD_SID, SOURCE.PRICE_TYPE, SOURCE.ACTUAL_PRICE );
MERGE NATIONAL_ASSUMPTIONS_PROJ AS TARGET USING ( SELECT ITEM_MASTER_SID, PRICE_TYPE, PERIOD_SID, PROJECTION_PRICE FROM dbo.ST_NATIONAL_ASSUMPTIONS_PROJ WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE (ITEM_MASTER_SID, PRICE_TYPE, PERIOD_SID, PROJECTION_PRICE) ON (TARGET.ITEM_MASTER_SID=SOURCE.ITEM_MASTER_SID AND TARGET.PERIOD_SID = SOURCE.PERIOD_SID AND TARGET.PRICE_TYPE = SOURCE.PRICE_TYPE)
WHEN MATCHED THEN UPDATE SET TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE, TARGET.PERIOD_SID=SOURCE.PERIOD_SID, TARGET.PROJECTION_PRICE=SOURCE.PROJECTION_PRICE
WHEN NOT MATCHED BY TARGET THEN
INSERT ( ITEM_MASTER_SID, PRICE_TYPE, PERIOD_SID, PROJECTION_PRICE ) VALUES(SOURCE.ITEM_MASTER_SID, SOURCE.PRICE_TYPE, SOURCE.PERIOD_SID, SOURCE.PROJECTION_PRICE );
MERGE NATIONAL_ASSUMPTIONS AS TARGET USING ( SELECT NA_PROJ_MASTER_SID, PRICE_TYPE, BASELINE_METHODOLOGY, FORECAST_METHODOLOGY, BASELINE_PERIOD, ROLLING_PERIOD, START_PERIOD, END_PERIOD, GROWTH_RATE, FREQUENCY, PRICE_BASIS FROM dbo.ST_NATIONAL_ASSUMPTIONS WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE (NA_PROJ_MASTER_SID, PRICE_TYPE, BASELINE_METHODOLOGY, FORECAST_METHODOLOGY, BASELINE_PERIOD, ROLLING_PERIOD, START_PERIOD, END_PERIOD, GROWTH_RATE, FREQUENCY, PRICE_BASIS ) ON (TARGET.NA_PROJ_MASTER_SID=SOURCE.NA_PROJ_MASTER_SID AND TARGET.START_PERIOD=SOURCE.START_PERIOD AND TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE AND TARGET.END_PERIOD=SOURCE.END_PERIOD)
WHEN MATCHED THEN UPDATE SET TARGET.PRICE_TYPE=SOURCE.PRICE_TYPE, TARGET.BASELINE_METHODOLOGY=SOURCE.BASELINE_METHODOLOGY, TARGET.FORECAST_METHODOLOGY=SOURCE.FORECAST_METHODOLOGY, TARGET.BASELINE_PERIOD=SOURCE.BASELINE_PERIOD, TARGET.ROLLING_PERIOD=SOURCE.ROLLING_PERIOD, TARGET.START_PERIOD=SOURCE.START_PERIOD, TARGET.END_PERIOD=SOURCE.END_PERIOD, TARGET.GROWTH_RATE=SOURCE.GROWTH_RATE, TARGET.FREQUENCY=SOURCE.FREQUENCY, TARGET.PRICE_BASIS=SOURCE.PRICE_BASIS
WHEN NOT MATCHED BY TARGET THEN
INSERT ( NA_PROJ_MASTER_SID, PRICE_TYPE, BASELINE_METHODOLOGY, FORECAST_METHODOLOGY, BASELINE_PERIOD, ROLLING_PERIOD, START_PERIOD, END_PERIOD, GROWTH_RATE, FREQUENCY, PRICE_BASIS ) VALUES(SOURCE.NA_PROJ_MASTER_SID, SOURCE.PRICE_TYPE, SOURCE.BASELINE_METHODOLOGY, SOURCE.FORECAST_METHODOLOGY, SOURCE.BASELINE_PERIOD, SOURCE.ROLLING_PERIOD, SOURCE.START_PERIOD, SOURCE.END_PERIOD, SOURCE.GROWTH_RATE, SOURCE.FREQUENCY, SOURCE.PRICE_BASIS );
UPDATE dbo.NA_PROJ_MASTER set SAVE_FLAG = 1 where NA_PROJ_MASTER_SID=?PID;

MERGE MEDICAID_NEW_NDC AS TARGET USING ( SELECT NDC9, WAC_PRICE, BASE_YEAR_AMP, BASE_YEAR_CPI, FORECAST_AMP, FORECAST_BESTPRICE FROM dbo.ST_MEDICAID_NEW_NDC WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE (NDC9, WAC_PRICE, BASE_YEAR_AMP, BASE_YEAR_CPI, FORECAST_AMP, FORECAST_BESTPRICE ) ON (TARGET.NDC9=SOURCE.NDC9)
WHEN MATCHED THEN UPDATE SET TARGET.NDC9=SOURCE.NDC9, TARGET.WAC_PRICE=SOURCE.WAC_PRICE, TARGET.BASE_YEAR_AMP=SOURCE.BASE_YEAR_AMP, TARGET.BASE_YEAR_CPI=SOURCE.BASE_YEAR_CPI, TARGET.FORECAST_AMP=SOURCE.FORECAST_AMP, TARGET.FORECAST_BESTPRICE=SOURCE.FORECAST_BESTPRICE
WHEN NOT MATCHED BY TARGET THEN INSERT VALUES(SOURCE.NDC9, SOURCE.WAC_PRICE, SOURCE.BASE_YEAR_AMP, SOURCE.BASE_YEAR_CPI, SOURCE.FORECAST_AMP, SOURCE.FORECAST_BESTPRICE );
MERGE FEDERAL_NEW_NDC AS TARGET USING ( SELECT ITEM_MASTER_SID, WAC_PRICE, NON_FAMP, FSS FROM dbo.ST_FEDERAL_NEW_NDC WHERE USER_ID=?UID AND SESSION_ID=?SID ) AS SOURCE (ITEM_MASTER_SID, WAC_PRICE, NON_FAMP, FSS) ON (TARGET.ITEM_MASTER_SID=SOURCE.ITEM_MASTER_SID)
WHEN MATCHED THEN UPDATE SET TARGET.ITEM_MASTER_SID=SOURCE.ITEM_MASTER_SID, TARGET.WAC_PRICE=SOURCE.WAC_PRICE, TARGET.NON_FAMP=SOURCE.NON_FAMP, TARGET.FSS=SOURCE.FSS
WHEN NOT MATCHED BY TARGET THEN INSERT VALUES(SOURCE.ITEM_MASTER_SID, SOURCE.WAC_PRICE, SOURCE.NON_FAMP, SOURCE.FSS );


