INSERT INTO ST_NATIONAL_ASSUMPTIONS ( NA_PROJ_MASTER_SID, PRICE_TYPE, BASELINE_METHODOLOGY, FORECAST_METHODOLOGY, BASELINE_PERIOD, ROLLING_PERIOD, START_PERIOD, END_PERIOD, GROWTH_RATE, FREQUENCY, USER_ID, SESSION_ID, LAST_MODIFIED_DATE, PRICE_BASIS) SELECT NA_PROJ_MASTER_SID, PRICE_TYPE, BASELINE_METHODOLOGY, FORECAST_METHODOLOGY, BASELINE_PERIOD, ROLLING_PERIOD, START_PERIOD, END_PERIOD, GROWTH_RATE, FREQUENCY, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE, PRICE_BASIS FROM dbo.NATIONAL_ASSUMPTIONS WHERE NA_PROJ_MASTER_SID=?PID;
INSERT INTO ST_MEDICAID_NEW_NDC ( NDC9, WAC_PRICE, BASE_YEAR_AMP, BASE_YEAR_CPI, FORECAST_AMP, FORECAST_BESTPRICE, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT MN.NDC9, WAC_PRICE, BASE_YEAR_AMP, BASE_YEAR_CPI, FORECAST_AMP, FORECAST_BESTPRICE, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.MEDICAID_NEW_NDC MN WHERE EXISTS (SELECT 1 FROM NA_PROJ_DETAILS PD INNER JOIN ITEM_MASTER IM ON PD.ITEM_MASTER_SID = IM.ITEM_MASTER_SID WHERE MN.NDC9 = IM.NDC9 AND NA_PROJ_MASTER_SID = ?PID) AND NOT EXISTS (SELECT 1 FROM ITEM_MASTER IM INNER JOIN ITEM_PRICING IP ON IM.ITEM_MASTER_SID = IP.ITEM_MASTER_SID INNER JOIN ITEM_PRICING_QUALIFIER IPQ ON IP.ITEM_PRICING_QUALIFIER_SID = IPQ.ITEM_PRICING_QUALIFIER_SID INNER JOIN NA_PROJ_DETAILS NPD ON NPD.ITEM_MASTER_SID = IP.ITEM_MASTER_SID WHERE mn.NDC9 = IM.NDC9 AND NPD.NA_PROJ_MASTER_SID = ?PID AND IPQ.PRICING_QUALIFIER IN ( 'AMP', 'BP' ) GROUP BY IP.ITEM_MASTER_SID HAVING Count(DISTINCT IPQ.PRICING_QUALIFIER) <> 2);
INSERT INTO ST_FEDERAL_NEW_NDC ( ITEM_MASTER_SID, WAC_PRICE, NON_FAMP, FSS, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT FNN.ITEM_MASTER_SID, WAC_PRICE, NON_FAMP, FSS, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.FEDERAL_NEW_NDC FNN WHERE EXISTS (SELECT 1 FROM NA_PROJ_DETAILS PD INNER JOIN ITEM_MASTER IM ON PD.ITEM_MASTER_SID = IM.ITEM_MASTER_SID WHERE FNN.ITEM_MASTER_SID = IM.ITEM_MASTER_SID AND NA_PROJ_MASTER_SID = ?PID) AND NOT EXISTS (SELECT 1 FROM ITEM_MASTER IM INNER JOIN ITEM_PRICING IP ON IM.ITEM_MASTER_SID = IP.ITEM_MASTER_SID INNER JOIN ITEM_PRICING_QUALIFIER IPQ ON IP.ITEM_PRICING_QUALIFIER_SID = IPQ.ITEM_PRICING_QUALIFIER_SID INNER JOIN NA_PROJ_DETAILS NPD ON NPD.ITEM_MASTER_SID = IP.ITEM_MASTER_SID WHERE FNN.ITEM_MASTER_SID = IM.ITEM_MASTER_SID AND NPD.NA_PROJ_MASTER_SID = ?PID AND IPQ.PRICING_QUALIFIER IN ( 'QNON-FAMP', 'QFSS' ) GROUP BY IP.ITEM_MASTER_SID HAVING Count(DISTINCT IPQ.PRICING_QUALIFIER) <> 2);
INSERT INTO ST_MEDICAID_URA_ACTUALS ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE, BASE_YEAR, NOTES, USER_ID, SESSION_ID, LAST_MODIFIED_DATE, ALT_BASE_YEAR) SELECT A.NA_PROJ_DETAILS_SID, A.PERIOD_SID, A.PRICE_TYPE, A.ACTUAL_PRICE, A.BASE_YEAR, A.NOTES, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE, A.ALT_BASE_YEAR FROM dbo.MEDICAID_URA_ACTUALS A, dbo.NA_PROJ_DETAILS B WHERE A.NA_PROJ_DETAILS_SID=B.NA_PROJ_DETAILS_SID AND B.NA_PROJ_MASTER_SID=?PID;
